// @ts-check
const { test, expect } = require('@playwright/test');

test.describe('–ò–Ω—Ç–µ—Ä–∞–∫—Ç–∏–≤–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
  });

  test.describe('FAQ –ê–∫–∫–æ—Ä–¥–µ–æ–Ω', () => {
    test('–í–æ–ø—Ä–æ—Å—ã –æ—Ç–æ–±—Ä–∞–∂–∞—é—Ç—Å—è', async ({ page }) => {
      // –ü—Ä–æ–∫—Ä—É—á–∏–≤–∞–µ–º –∫ FAQ —Å–µ–∫—Ü–∏–∏
      const faqSection = page.locator('.faq').first();
      if (await faqSection.count() > 0) {
        await faqSection.scrollIntoViewIfNeeded();
        await page.waitForTimeout(500);

        const faqItems = page.locator('.faq__item');
        const itemCount = await faqItems.count();
        expect(itemCount).toBeGreaterThan(0);
      }
    });

    test('–û—Ç–∫—Ä—ã—Ç–∏–µ –≤–æ–ø—Ä–æ—Å–∞ –ø–æ –∫–ª–∏–∫—É', async ({ page }) => {
      const faqSection = page.locator('.faq').first();
      if (await faqSection.count() === 0) {
        test.skip();
      }

      await faqSection.scrollIntoViewIfNeeded();
      await page.waitForTimeout(500);

      const firstQuestion = page.locator('.faq__question').first();
      const firstAnswer = page.locator('.faq__answer').first();
      const firstItem = page.locator('.faq__item').first();

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞—á–∞–ª—å–Ω–æ–µ —Å–æ—Å—Ç–æ—è–Ω–∏–µ (–∑–∞–∫—Ä—ã—Ç–æ)
      await expect(firstQuestion).toHaveAttribute('aria-expanded', 'false');
      await expect(firstAnswer).toHaveAttribute('aria-hidden', 'true');

      // –û—Ç–∫—Ä—ã–≤–∞–µ–º –≤–æ–ø—Ä–æ—Å
      await firstQuestion.click();
      await page.waitForTimeout(300);

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –≤–æ–ø—Ä–æ—Å –æ—Ç–∫—Ä—ã—Ç
      await expect(firstQuestion).toHaveAttribute('aria-expanded', 'true');
      await expect(firstAnswer).toHaveAttribute('aria-hidden', 'false');
      await expect(firstItem).toHaveClass(/faq__item--open/);
    });

    test('–ó–∞–∫—Ä—ã—Ç–∏–µ –ø—Ä–∏ –ø–æ–≤—Ç–æ—Ä–Ω–æ–º –∫–ª–∏–∫–µ', async ({ page }) => {
      const faqSection = page.locator('.faq').first();
      if (await faqSection.count() === 0) {
        test.skip();
      }

      await faqSection.scrollIntoViewIfNeeded();
      await page.waitForTimeout(500);

      const firstQuestion = page.locator('.faq__question').first();
      const firstAnswer = page.locator('.faq__answer').first();
      const firstItem = page.locator('.faq__item').first();

      // –û—Ç–∫—Ä—ã–≤–∞–µ–º
      await firstQuestion.click();
      await page.waitForTimeout(300);
      await expect(firstItem).toHaveClass(/faq__item--open/);

      // –ó–∞–∫—Ä—ã–≤–∞–µ–º
      await firstQuestion.click();
      await page.waitForTimeout(300);
      await expect(firstQuestion).toHaveAttribute('aria-expanded', 'false');
      await expect(firstAnswer).toHaveAttribute('aria-hidden', 'true');
    });

    test('–ó–∞–∫—Ä—ã—Ç–∏–µ –¥—Ä—É–≥–∏—Ö –ø—Ä–∏ –æ—Ç–∫—Ä—ã—Ç–∏–∏ –Ω–æ–≤–æ–≥–æ', async ({ page }) => {
      const faqSection = page.locator('.faq').first();
      if (await faqSection.count() === 0) {
        test.skip();
      }

      await faqSection.scrollIntoViewIfNeeded();
      await page.waitForTimeout(500);

      const questions = page.locator('.faq__question');
      const questionsCount = await questions.count();

      if (questionsCount < 2) {
        test.skip();
      }

      const firstQuestion = questions.nth(0);
      const secondQuestion = questions.nth(1);
      const firstItem = page.locator('.faq__item').nth(0);
      const secondItem = page.locator('.faq__item').nth(1);

      // –û—Ç–∫—Ä—ã–≤–∞–µ–º –ø–µ—Ä–≤—ã–π –≤–æ–ø—Ä–æ—Å
      await firstQuestion.click();
      await page.waitForTimeout(300);
      await expect(firstItem).toHaveClass(/faq__item--open/);

      // –û—Ç–∫—Ä—ã–≤–∞–µ–º –≤—Ç–æ—Ä–æ–π –≤–æ–ø—Ä–æ—Å
      await secondQuestion.click();
      await page.waitForTimeout(300);

      // –ü–µ—Ä–≤—ã–π –¥–æ–ª–∂–µ–Ω –∑–∞–∫—Ä—ã—Ç—å—Å—è, –≤—Ç–æ—Ä–æ–π –æ—Ç–∫—Ä—ã—Ç—å—Å—è
      await expect(firstItem).not.toHaveClass(/faq__item--open/);
      await expect(secondItem).toHaveClass(/faq__item--open/);
    });

    test('Keyboard navigation (Enter, Space)', async ({ page }) => {
      const faqSection = page.locator('.faq').first();
      if (await faqSection.count() === 0) {
        test.skip();
      }

      await faqSection.scrollIntoViewIfNeeded();
      await page.waitForTimeout(500);

      const firstQuestion = page.locator('.faq__question').first();
      const firstItem = page.locator('.faq__item').first();

      // –§–æ–∫—É—Å–∏—Ä—É–µ–º—Å—è –Ω–∞ –≤–æ–ø—Ä–æ—Å–µ
      await firstQuestion.focus();

      // –û—Ç–∫—Ä—ã–≤–∞–µ–º –ø–æ Enter
      await page.keyboard.press('Enter');
      await page.waitForTimeout(300);
      await expect(firstItem).toHaveClass(/faq__item--open/);

      // –ó–∞–∫—Ä—ã–≤–∞–µ–º –ø–æ Space
      await page.keyboard.press('Space');
      await page.waitForTimeout(300);
      await expect(firstQuestion).toHaveAttribute('aria-expanded', 'false');
    });

    test('ARIA –∞—Ç—Ä–∏–±—É—Ç—ã –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã', async ({ page }) => {
      const faqSection = page.locator('.faq').first();
      if (await faqSection.count() === 0) {
        test.skip();
      }

      await faqSection.scrollIntoViewIfNeeded();
      await page.waitForTimeout(500);

      const firstQuestion = page.locator('.faq__question').first();
      const firstAnswer = page.locator('.faq__answer').first();

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º ARIA –∞—Ç—Ä–∏–±—É—Ç—ã
      const questionId = await firstQuestion.getAttribute('id');
      const answerId = await firstAnswer.getAttribute('id');
      const ariaControls = await firstQuestion.getAttribute('aria-controls');
      const ariaLabelledBy = await firstAnswer.getAttribute('aria-labelledby');

      expect(questionId).toBeTruthy();
      expect(answerId).toBeTruthy();
      expect(ariaControls).toBe(answerId);
      expect(ariaLabelledBy).toBe(questionId);

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º role
      const answerRole = await firstAnswer.getAttribute('role');
      expect(answerRole).toBe('region');
    });
  });

  test.describe('–ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ —Ç–µ–º—ã', () => {
    test('–ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –Ω–∞ —Ç–µ–º–Ω—É—é —Ç–µ–º—É', async ({ page }) => {
      // –ù–∞—Ö–æ–¥–∏–º –∫–Ω–æ–ø–∫—É –ø–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏—è –Ω–∞ —Ç–µ–º–Ω—É—é —Ç–µ–º—É
      const darkThemeButton = page.locator('[data-theme="dark"]').first();

      if (await darkThemeButton.count() === 0) {
        test.skip();
      }

      // –ü–µ—Ä–µ–∫–ª—é—á–∞–µ–º—Å—è –Ω–∞ —Ç–µ–º–Ω—É—é —Ç–µ–º—É
      await darkThemeButton.click();

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ URL —Å–æ–¥–µ—Ä–∂–∏—Ç #dark
      await expect(page).toHaveURL(/#dark/);

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ç–µ–º–∞ –ø—Ä–∏–º–µ–Ω–∏–ª–∞—Å—å (–ø—Ä–æ–≤–µ—Ä—è–µ–º –∫–ª–∞—Å—Å –Ω–∞ body –∏–ª–∏ html)
      const bodyClass = await page.evaluate(() => {
        return document.body.classList.toString() || document.documentElement.classList.toString();
      });

      // –ú–æ–∂–µ—Ç –±—ã—Ç—å –∫–ª–∞—Å—Å –∏–ª–∏ CSS –ø–µ—Ä–µ–º–µ–Ω–Ω—ã–µ –∏–∑–º–µ–Ω–∏–ª–∏—Å—å
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ —è–∫–æ—Ä—è –¥–ª—è —Ç–µ–º—ã
      const darkAnchor = page.locator('#dark');
      const anchorExists = await darkAnchor.count() > 0;
      expect(anchorExists).toBeTruthy();
    });

    test('–ü–µ—Ä–µ–∫–ª—é—á–µ–Ω–∏–µ –Ω–∞ —Å–≤–µ—Ç–ª—É—é —Ç–µ–º—É', async ({ page }) => {
      // –°–Ω–∞—á–∞–ª–∞ –ø–µ—Ä–µ–∫–ª—é—á–∞–µ–º—Å—è –Ω–∞ —Ç–µ–º–Ω—É—é
      const darkThemeButton = page.locator('[data-theme="dark"]').first();
      if (await darkThemeButton.count() > 0) {
        await darkThemeButton.click();
        await page.waitForTimeout(300);
      }

      // –ü–µ—Ä–µ–∫–ª—é—á–∞–µ–º—Å—è –Ω–∞ —Å–≤–µ—Ç–ª—É—é
      const lightThemeButton = page.locator('[data-theme="light"]').first();
      if (await lightThemeButton.count() === 0) {
        test.skip();
      }

      await lightThemeButton.click();

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º URL (–º–æ–∂–µ—Ç –±—ã—Ç—å #light –∏–ª–∏ –±–µ–∑ hash)
      const url = page.url();
      // URL –º–æ–∂–µ—Ç —Å–æ–¥–µ—Ä–∂–∞—Ç—å #light –∏–ª–∏ –Ω–µ —Å–æ–¥–µ—Ä–∂–∞—Ç—å hash –≤–æ–æ–±—â–µ
      expect(url).toMatch(/#light|^[^#]*$/);
    });

    test('–°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ —Ç–µ–º—ã –≤ URL hash', async ({ page }) => {
      const darkThemeButton = page.locator('[data-theme="dark"]').first();
      if (await darkThemeButton.count() === 0) {
        test.skip();
      }

      // –ü–µ—Ä–µ–∫–ª—é—á–∞–µ–º—Å—è –Ω–∞ —Ç–µ–º–Ω—É—é —Ç–µ–º—É
      await darkThemeButton.click();
      await expect(page).toHaveURL(/#dark/);

      // –ü–µ—Ä–µ–∑–∞–≥—Ä—É–∂–∞–µ–º —Å—Ç—Ä–∞–Ω–∏—Ü—É
      await page.reload();
      await page.waitForTimeout(500);

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ç–µ–º–∞ —Å–æ—Ö—Ä–∞–Ω–∏–ª–∞—Å—å
      const url = page.url();
      expect(url).toMatch(/#dark/);
    });
  });

  test.describe('–ü–ª–∞–≤–Ω–∞—è –ø—Ä–æ–∫—Ä—É—Ç–∫–∞', () => {
    test('–ü—Ä–æ–∫—Ä—É—Ç–∫–∞ –∫ —è–∫–æ—Ä–Ω—ã–º —Å—Å—ã–ª–∫–∞–º', async ({ page }) => {
      // –ò—â–µ–º —Å—Å—ã–ª–∫—É —Å —è–∫–æ—Ä–µ–º, –∏—Å–∫–ª—é—á–∞—è skip-link –∏ —Ç–µ–º—ã
      const anchorLinks = page.locator('a[href^="#"]').filter({ 
        hasNotText: /–ü–µ—Ä–µ–π—Ç–∏ –∫ –æ—Å–Ω–æ–≤–Ω–æ–º—É —Å–æ–¥–µ—Ä–∂–∏–º–æ–º—É|–°–≤–µ—Ç–ª–∞—è —Ç–µ–º–∞|–¢–µ–º–Ω–∞—è —Ç–µ–º–∞/
      });
      const linkCount = await anchorLinks.count();

      if (linkCount === 0) {
        test.skip();
      }

      // –ü–æ–ª—É—á–∞–µ–º –Ω–∞—á–∞–ª—å–Ω—É—é –ø–æ–∑–∏—Ü–∏—é —Å–∫—Ä–æ–ª–ª–∞
      const initialScroll = await page.evaluate(() => window.scrollY);

      // –ö–ª–∏–∫–∞–µ–º –Ω–∞ –ø–µ—Ä–≤—É—é —è–∫–æ—Ä–Ω—É—é —Å—Å—ã–ª–∫—É
      const firstAnchorLink = anchorLinks.first();
      const href = await firstAnchorLink.getAttribute('href');

      if (href && href !== '#' && href !== '#dark' && href !== '#light' && !href.includes('skip')) {
        // –ü—Ä–æ–∫—Ä—É—á–∏–≤–∞–µ–º –∫ —Å—Å—ã–ª–∫–µ, –µ—Å–ª–∏ –æ–Ω–∞ –≤–Ω–µ viewport
        await firstAnchorLink.scrollIntoViewIfNeeded();
        await page.waitForTimeout(300);

        await firstAnchorLink.click();
        await page.waitForTimeout(1000); // –ñ–¥–µ–º –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –ø–ª–∞–≤–Ω–æ–π –ø—Ä–æ–∫—Ä—É—Ç–∫–∏

        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Å–∫—Ä–æ–ª–ª –∏–∑–º–µ–Ω–∏–ª—Å—è
        const finalScroll = await page.evaluate(() => window.scrollY);

        // –ü—Ä–æ–∫—Ä—É—Ç–∫–∞ –¥–æ–ª–∂–Ω–∞ –±—ã–ª–∞ –ø—Ä–æ–∏–∑–æ–π—Ç–∏ (–º–æ–∂–µ—Ç –±—ã—Ç—å –≤–≤–µ—Ä—Ö –∏–ª–∏ –≤–Ω–∏–∑)
        // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –ø–æ–∑–∏—Ü–∏—è –∏–∑–º–µ–Ω–∏–ª–∞—Å—å –∏–ª–∏ —á—Ç–æ —Ü–µ–ª–µ–≤–æ–π —ç–ª–µ–º–µ–Ω—Ç –≤–∏–¥–µ–Ω
        if (href.startsWith('#')) {
          const targetId = href.substring(1);
          const targetElement = page.locator(`#${targetId}`);
          if (await targetElement.count() > 0) {
            const isVisible = await targetElement.isVisible();
            // –≠–ª–µ–º–µ–Ω—Ç –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –≤–∏–¥–µ–Ω –∏–ª–∏ –ø–æ–∑–∏—Ü–∏—è —Å–∫—Ä–æ–ª–ª–∞ –¥–æ–ª–∂–Ω–∞ –∏–∑–º–µ–Ω–∏—Ç—å—Å—è
            expect(isVisible || finalScroll !== initialScroll).toBeTruthy();
          }
        }
      } else {
        test.skip();
      }
    });
  });
});

