/* Testimonials Component - Premium Enhanced */

.testimonials {
  display: grid;
  grid-template-columns: 1fr;
  gap: var(--spacing-lg);
}

@media (min-width: 768px) {
  .testimonials {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 1024px) {
  .testimonials {
    grid-template-columns: repeat(3, 1fr);
  }
}

.testimonials__section {
  padding: var(--spacing-3xl) 0;
}

.testimonials__title {
  text-align: center;
  margin-bottom: var(--spacing-2xl);
}

.review-card {
  padding: var(--spacing-xl);
  background: var(--bg-primary);
  border: 1px solid var(--border-color);
  border-radius: var(--radius-lg);
  box-shadow: var(--shadow-sm);
  transition: all var(--transition-base);
  position: relative;
  height: 100%;
  display: flex;
  flex-direction: column;
}

.review-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: var(--gradient-primary);
  transform: scaleX(0);
  transform-origin: left;
  transition: transform var(--transition-base);
}

@media (hover: hover) and (pointer: fine) {
  .review-card:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-primary-lg);
    border-color: var(--color-primary);
  }

  .review-card:hover::before {
    transform: scaleX(1);
  }
}

.review-card__header {
  display: flex;
  align-items: flex-start;
  justify-content: space-between;
  margin-bottom: var(--spacing-md);
  gap: var(--spacing-md);
}

.review-card__author {
  font-weight: 600;
  color: var(--text-primary);
  font-size: 1rem;
}

.review-card__avatar {
  width: 48px;
  height: 48px;
  border-radius: 50%;
  background: var(--gradient-primary);
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-weight: 700;
  font-size: 1.25rem;
  flex-shrink: 0;
  box-shadow: var(--shadow-primary);
}

.review-card__rating {
  display: flex;
  gap: var(--spacing-xs);
  color: var(--color-warning);
  font-size: 1.125rem;
}

.review-card__date {
  font-size: 0.875rem;
  color: var(--text-muted);
}

.review-card__text {
  color: var(--text-secondary);
  line-height: 1.6;
  flex: 1;
  margin-bottom: var(--spacing-md);
}

.review-card__verified {
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-xs);
  font-size: 0.875rem;
  color: var(--color-success);
  margin-top: auto;
  font-weight: 500;
}

assets/js/phone-formatter.js.content:
/**
 * Phone Formatter - –ê–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–æ–µ —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Ç–µ–ª–µ—Ñ–æ–Ω–Ω—ã—Ö –Ω–æ–º–µ—Ä–æ–≤ –ø—Ä–∏ –≤–≤–æ–¥–µ
 */

(function() {
  'use strict';

  // –§—É–Ω–∫—Ü–∏—è —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏—è —Ç–µ–ª–µ—Ñ–æ–Ω–∞ –≤ —Ñ–æ—Ä–º–∞—Ç +7 (999) 123-45-67
  function formatPhone(value) {
    // –£–¥–∞–ª—è–µ–º –≤—Å–µ –Ω–µ—Ü–∏—Ñ—Ä–æ–≤—ã–µ —Å–∏–º–≤–æ–ª—ã –∫—Ä–æ–º–µ +
    let cleaned = value.replace(/[^\d+]/g, '');

    // –ï—Å–ª–∏ –Ω–∞—á–∏–Ω–∞–µ—Ç—Å—è —Å 8, –∑–∞–º–µ–Ω—è–µ–º –Ω–∞ +7
    if (cleaned.startsWith('8')) {
      cleaned = '+7' + cleaned.substring(1);
    }
    // –ï—Å–ª–∏ –Ω–∞—á–∏–Ω–∞–µ—Ç—Å—è —Å 7, –¥–æ–±–∞–≤–ª—è–µ–º +
    else if (cleaned.startsWith('7') && !cleaned.startsWith('+7')) {
      cleaned = '+7' + cleaned.substring(1);
    }
    // –ï—Å–ª–∏ –Ω–∞—á–∏–Ω–∞–µ—Ç—Å—è —Å —Ü–∏—Ñ—Ä—ã, –¥–æ–±–∞–≤–ª—è–µ–º +7
    else if (/^\d/.test(cleaned) && !cleaned.startsWith('+')) {
      cleaned = '+7' + cleaned;
    }
    // –ï—Å–ª–∏ –Ω–µ –Ω–∞—á–∏–Ω–∞–µ—Ç—Å—è —Å +7, –¥–æ–±–∞–≤–ª—è–µ–º
    else if (!cleaned.startsWith('+7')) {
      cleaned = '+7' + cleaned;
    }

    // –û–≥—Ä–∞–Ω–∏—á–∏–≤–∞–µ–º –¥–ª–∏–Ω—É (11 —Ü–∏—Ñ—Ä –ø–æ—Å–ª–µ +7)
    cleaned = cleaned.substring(0, 12); // +7 + 10 —Ü–∏—Ñ—Ä

    // –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º: +7 (999) 123-45-67
    if (cleaned.length > 2) {
      const digits = cleaned.substring(2); // –¶–∏—Ñ—Ä—ã –ø–æ—Å–ª–µ +7
      let formatted = '+7';

      if (digits.length > 0) {
        formatted += ' (' + digits.substring(0, 3);
      }
      if (digits.length > 3) {
        formatted += ') ' + digits.substring(3, 6);
      }
      if (digits.length > 6) {
        formatted += '-' + digits.substring(6, 8);
      }
      if (digits.length > 8) {
        formatted += '-' + digits.substring(8, 10);
      }

      return formatted;
    }

    return cleaned;
  }

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏—è —Ç–µ–ª–µ—Ñ–æ–Ω–æ–≤
  function init() {
    const phoneInputs = document.querySelectorAll('input[type="tel"], input[name="phone"]');

    phoneInputs.forEach(input => {
      // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –ø—Ä–∏ –≤–≤–æ–¥–µ
      input.addEventListener('input', function(e) {
        const cursorPosition = this.selectionStart;
        const oldValue = this.value;
        const newValue = formatPhone(this.value);

        this.value = newValue;

        // –í–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –ø–æ–∑–∏—Ü–∏—é –∫—É—Ä—Å–æ—Ä–∞
        const diff = newValue.length - oldValue.length;
        const newPosition = cursorPosition + diff;
        this.setSelectionRange(newPosition, newPosition);
      });

      // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –ø—Ä–∏ –≤—Å—Ç–∞–≤–∫–µ
      input.addEventListener('paste', function(e) {
        e.preventDefault();
        const pastedText = (e.clipboardData || window.clipboardData).getData('text');
        const formatted = formatPhone(pastedText);
        this.value = formatted;
      });

      // –§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ –ø—Ä–∏ –ø–æ—Ç–µ—Ä–µ —Ñ–æ–∫—É—Å–∞ (–µ—Å–ª–∏ –ø–æ–ª–µ –Ω–µ –ø—É—Å—Ç–æ–µ)
      input.addEventListener('blur', function() {
        if (this.value.trim() && !this.value.startsWith('+7')) {
          this.value = formatPhone(this.value);
        }
      });
    });
  }

  // –ó–∞–ø—É—Å–∫ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ DOM
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', init);
  } else {
    init();
  }
})();

