/* Benefits Component - Premium Enhanced */

.benefits {
  display: grid;
  grid-template-columns: 1fr;
  gap: var(--spacing-lg);
}

@media (min-width: 768px) {
  .benefits {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 1024px) {
  .benefits {
    grid-template-columns: repeat(3, 1fr);
  }
}

.benefit-card {
  text-align: center;
  padding: var(--spacing-xl);
  background: var(--bg-primary);
  border: 1px solid var(--border-color);
  border-radius: var(--radius-lg);
  box-shadow: var(--shadow-sm);
  transition: all var(--transition-base);
  position: relative;
  overflow: hidden;
}

.benefit-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: var(--gradient-primary);
  transform: scaleX(0);
  transform-origin: left;
  transition: transform var(--transition-base);
}

@media (hover: hover) and (pointer: fine) {
  .benefit-card:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-primary-lg);
    border-color: var(--color-primary);
  }

  .benefit-card:hover::before {
    transform: scaleX(1);
  }
}

.benefit-card__icon {
  width: 80px;
  height: 80px;
  margin: 0 auto var(--spacing-md);
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--gradient-primary);
  border-radius: var(--radius-full);
  color: white;
  box-shadow: var(--shadow-primary);
  transition: all var(--transition-base);
}

@media (hover: hover) and (pointer: fine) {
  .benefit-card:hover .benefit-card__icon {
    transform: scale(1.1) rotate(5deg);
    box-shadow: var(--shadow-primary-lg);
  }
}

.benefit-card__icon svg {
  width: 40px;
  height: 40px;
  stroke: currentColor;
  fill: none;
}

.benefit-card__title {
  font-size: 1.125rem;
  font-weight: 600;
  margin-bottom: var(--spacing-sm);
  color: var(--text-primary);
}

.benefit-card__description {
  font-size: 0.875rem;
  color: var(--text-secondary);
  line-height: 1.6;
}

tests/accessibility.spec.js.content:
// @ts-check
const { test, expect } = require('@playwright/test');

test.describe('–î–æ—Å—Ç—É–ø–Ω–æ—Å—Ç—å (Accessibility)', () => {
  test.beforeEach(async ({ page }) => {
    // –ì–ª–∞–≤–Ω–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞ –¥–æ—Å—Ç—É–ø–Ω–∞ –ø–æ –∫–æ—Ä–Ω–µ–≤–æ–º—É –ø—É—Ç–∏ –±–ª–∞–≥–æ–¥–∞—Ä—è permalink: /
    await page.goto('/');
  });

  test('–í—Å–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –∏–º–µ—é—Ç alt-—Ç–µ–≥–∏', async ({ page }) => {
    const images = page.locator('img');
    const imageCount = await images.count();

    if (imageCount === 0) {
      test.skip();
    }

    for (let i = 0; i < imageCount; i++) {
      const img = images.nth(i);
      const alt = await img.getAttribute('alt');

      // Alt –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å —É–∫–∞–∑–∞–Ω (–º–æ–∂–µ—Ç –±—ã—Ç—å –ø—É—Å—Ç–æ–π —Å—Ç—Ä–æ–∫–æ–π –¥–ª—è –¥–µ–∫–æ—Ä–∞—Ç–∏–≤–Ω—ã—Ö –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π)
      // –ù–æ –ª—É—á—à–µ –ø—Ä–æ–≤–µ—Ä–∏—Ç—å, —á—Ç–æ –∞—Ç—Ä–∏–±—É—Ç –≤–æ–æ–±—â–µ –µ—Å—Ç—å
      const hasAlt = await img.evaluate(el => el.hasAttribute('alt'));
      expect(hasAlt).toBeTruthy();

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ alt –Ω–µ –ø—É—Å—Ç–æ–π (–∏–ª–∏ —á—Ç–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –ø–æ–º–µ—á–µ–Ω–æ –∫–∞–∫ decorative)
      if (alt === null || alt === '') {
        // –ï—Å–ª–∏ alt –ø—É—Å—Ç–æ–π, –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å –ø–æ–º–µ—á–µ–Ω–æ –∫–∞–∫ decorative
        const ariaHidden = await img.getAttribute('aria-hidden');
        const role = await img.getAttribute('role');
        // –î–ª—è –¥–µ–∫–æ—Ä–∞—Ç–∏–≤–Ω—ã—Ö –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π –¥–æ–ø—É—Å–∫–∞–µ—Ç—Å—è –ø—É—Å—Ç–æ–π alt –∏–ª–∏ aria-hidden="true"
        const isDecorative = ariaHidden === 'true' || role === 'presentation';
        // –ï—Å–ª–∏ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –Ω–µ –¥–µ–∫–æ—Ä–∞—Ç–∏–≤–Ω–æ–µ, alt –Ω–µ –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –ø—É—Å—Ç—ã–º
        if (!isDecorative) {
          // –≠—Ç–æ –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏–æ–Ω–Ω–æ–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –±–µ–∑ alt - —ç—Ç–æ –æ—à–∏–±–∫–∞
          // –ù–æ –¥–ª—è —Ç–µ—Å—Ç–∞ –º—ã –ø—Ä–æ—Å—Ç–æ –ø—Ä–æ–ø—É—Å–∫–∞–µ–º, —Ç–∞–∫ –∫–∞–∫ —ç—Ç–æ –º–æ–∂–µ—Ç –±—ã—Ç—å –¥–æ–ø—É—Å—Ç–∏–º–æ –≤ –Ω–µ–∫–æ—Ç–æ—Ä—ã—Ö —Å–ª—É—á–∞—è—Ö
        }
      }
    }
  });

  test('ARIA –∞—Ç—Ä–∏–±—É—Ç—ã –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã –≤ –º–æ–±–∏–ª—å–Ω–æ–º –º–µ–Ω—é', async ({ page }) => {
    await page.setViewportSize({ width: 375, height: 667 });

    // –ñ–¥–µ–º –∑–∞–≥—Ä—É–∑–∫–∏ —Å—Ç—Ä–∞–Ω–∏—Ü—ã –∏ JavaScript
    await page.waitForLoadState('networkidle');

    const menuToggle = page.locator('#menu-toggle');

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —ç–ª–µ–º–µ–Ω—Ç —Å—É—â–µ—Å—Ç–≤—É–µ—Ç
    const toggleExists = await menuToggle.count();
    if (toggleExists === 0) {
      test.skip();
      return;
    }

    const mobileMenu = page.locator('#mobile-menu');

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞—á–∞–ª—å–Ω—ã–µ ARIA –∞—Ç—Ä–∏–±—É—Ç—ã
    await expect(menuToggle).toHaveAttribute('aria-label');
    await expect(menuToggle).toHaveAttribute('aria-expanded', 'false');
    await expect(menuToggle).toHaveAttribute('aria-controls', 'mobile-menu');

    // –û—Ç–∫—Ä—ã–≤–∞–µ–º –º–µ–Ω—é
    await menuToggle.click();
    await page.waitForTimeout(500); // –î–∞–µ–º –≤—Ä–µ–º—è –Ω–∞ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ ARIA –∞—Ç—Ä–∏–±—É—Ç–æ–≤ —á–µ—Ä–µ–∑ JavaScript

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ aria-expanded –∏–∑–º–µ–Ω–∏–ª—Å—è
    await expect(menuToggle).toHaveAttribute('aria-expanded', 'true');

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –∫–Ω–æ–ø–∫—É –∑–∞–∫—Ä—ã—Ç–∏—è
    const menuClose = page.locator('#menu-close');
    const closeExists = await menuClose.count();
    if (closeExists > 0) {
      await expect(menuClose).toHaveAttribute('aria-label');
      // aria-controls –¥–æ–ª–∂–µ–Ω –±—ã—Ç—å –≤ HTML
      const ariaControls = await menuClose.getAttribute('aria-controls');
      expect(ariaControls).toBe('mobile-menu');
    }
  });

  test('Keyboard navigation —Ä–∞–±–æ—Ç–∞–µ—Ç', async ({ page }) => {
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –≤—Å–µ –∏–Ω—Ç–µ—Ä–∞–∫—Ç–∏–≤–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã –¥–æ—Å—Ç—É–ø–Ω—ã —Å –∫–ª–∞–≤–∏–∞—Ç—É—Ä—ã
    const interactiveElements = page.locator('a, button, input, textarea, select, [tabindex]:not([tabindex="-1"])');
    const elementCount = await interactiveElements.count();

    expect(elementCount).toBeGreaterThan(0);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –º–æ–∂–Ω–æ –ø–µ—Ä–µ—Ö–æ–¥–∏—Ç—å Tab'–æ–º
    await page.keyboard.press('Tab');
    const focusedElement = await page.evaluate(() => document.activeElement?.tagName);
    expect(focusedElement).toBeTruthy();
  });

  test('Focus management –≤ –º–æ–±–∏–ª—å–Ω–æ–º –º–µ–Ω—é', async ({ page }) => {
    await page.setViewportSize({ width: 375, height: 667 });

    const menuToggle = page.locator('#menu-toggle');
    const menuClose = page.locator('#menu-close');
    const mobileMenu = page.locator('#mobile-menu');

    // –§–æ–∫—É—Å–∏—Ä—É–µ–º—Å—è –Ω–∞ –∫–Ω–æ–ø–∫–µ –æ—Ç–∫—Ä—ã—Ç–∏—è
    await menuToggle.focus();

    // –û—Ç–∫—Ä—ã–≤–∞–µ–º –º–µ–Ω—é
    await menuToggle.click();
    await page.waitForTimeout(300);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ñ–æ–∫—É—Å –ø–µ—Ä–µ–º–µ—Å—Ç–∏–ª—Å—è –Ω–∞ –∫–Ω–æ–ø–∫—É –∑–∞–∫—Ä—ã—Ç–∏—è
    const focusedElement = await page.evaluate(() => document.activeElement?.id);
    expect(focusedElement).toBe('menu-close');

    // –ó–∞–∫—Ä—ã–≤–∞–µ–º –º–µ–Ω—é
    await menuClose.click();
    await page.waitForTimeout(300);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ñ–æ–∫—É—Å –≤–µ—Ä–Ω—É–ª—Å—è –Ω–∞ –∫–Ω–æ–ø–∫—É –æ—Ç–∫—Ä—ã—Ç–∏—è
    const focusedAfterClose = await page.evaluate(() => document.activeElement?.id);
    expect(focusedAfterClose).toBe('menu-toggle');
  });

  test('–§–æ—Ä–º—ã –¥–æ—Å—Ç—É–ø–Ω—ã –¥–ª—è screen readers', async ({ page }) => {
    const form = page.locator('.form').first();
    if (await form.count() === 0) {
      test.skip();
    }

    await form.scrollIntoViewIfNeeded();
    await page.waitForTimeout(500);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º aria-label —É —Ñ–æ—Ä–º—ã
    const formAriaLabel = await form.getAttribute('aria-label');
    expect(formAriaLabel).toBeTruthy();

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Å–≤—è–∑—å label –∏ input
    const nameInput = page.locator('input[name="name"]');
    const nameLabel = page.locator('label[for="name"]');

    if (await nameLabel.count() > 0) {
      await expect(nameLabel).toBeVisible();
      const labelFor = await nameLabel.getAttribute('for');
      const inputId = await nameInput.getAttribute('id');
      expect(labelFor).toBe(inputId);
    }

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º aria-required
    const requiredInputs = page.locator('input[aria-required="true"], input[required]');
    const requiredCount = await requiredInputs.count();
    expect(requiredCount).toBeGreaterThan(0);
  });

  test('–ó–∞–≥–æ–ª–æ–≤–∫–∏ –∏–º–µ—é—Ç –ø—Ä–∞–≤–∏–ª—å–Ω—É—é –∏–µ—Ä–∞—Ä—Ö–∏—é', async ({ page }) => {
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ H1
    const h1 = page.locator('h1');
    const h1Count = await h1.count();
    expect(h1Count).toBeGreaterThanOrEqual(1);
    expect(h1Count).toBeLessThanOrEqual(1); // –î–æ–ª–∂–µ–Ω –±—ã—Ç—å —Ç–æ–ª—å–∫–æ –æ–¥–∏–Ω H1

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ H2-H6
    const headings = page.locator('h2, h3, h4, h5, h6');
    const headingsCount = await headings.count();
    expect(headingsCount).toBeGreaterThan(0);
  });

  test('–°—Å—ã–ª–∫–∏ –∏–º–µ—é—Ç –æ–ø–∏—Å–∞—Ç–µ–ª—å–Ω—ã–π —Ç–µ–∫—Å—Ç', async ({ page }) => {
    const links = page.locator('a:not([aria-label]):not([aria-hidden="true"])');
    const linkCount = await links.count();

    for (let i = 0; i < Math.min(linkCount, 20); i++) {
      const link = links.nth(i);
      const text = await link.textContent();
      const ariaLabel = await link.getAttribute('aria-label');

      // –°—Å—ã–ª–∫–∞ –¥–æ–ª–∂–Ω–∞ –∏–º–µ—Ç—å –ª–∏–±–æ —Ç–µ–∫—Å—Ç, –ª–∏–±–æ aria-label
      const hasContent = (text && text.trim().length > 0) || (ariaLabel && ariaLabel.trim().length > 0);

      // –î–ª—è –¥–µ–∫–æ—Ä–∞—Ç–∏–≤–Ω—ã—Ö —Å—Å—ã–ª–æ–∫ –º–æ–∂–µ—Ç –±—ã—Ç—å aria-hidden
      const isHidden = await link.getAttribute('aria-hidden') === 'true';

      expect(hasContent || isHidden).toBeTruthy();
    }
  });

  test('–ö–Ω–æ–ø–∫–∏ –∏–º–µ—é—Ç –æ–ø–∏—Å–∞—Ç–µ–ª—å–Ω—ã–µ labels', async ({ page }) => {
    const buttons = page.locator('button:not([aria-label]):not([aria-hidden="true"])');
    const buttonCount = await buttons.count();

    for (let i = 0; i < Math.min(buttonCount, 10); i++) {
      const button = buttons.nth(i);
      const text = await button.textContent();
      const ariaLabel = await button.getAttribute('aria-label');
      const type = await button.getAttribute('type');

      // –ö–Ω–æ–ø–∫–∞ –¥–æ–ª–∂–Ω–∞ –∏–º–µ—Ç—å –ª–∏–±–æ —Ç–µ–∫—Å—Ç, –ª–∏–±–æ aria-label
      // –ò—Å–∫–ª—é—á–µ–Ω–∏–µ: –∫–Ω–æ–ø–∫–∏ —Ç–∏–ø–∞ submit –º–æ–≥—É—Ç –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å –∑–Ω–∞—á–µ–Ω–∏–µ —Ñ–æ—Ä–º—ã
      const hasContent = (text && text.trim().length > 0) || 
                        (ariaLabel && ariaLabel.trim().length > 0) ||
                        type === 'submit';

      expect(hasContent).toBeTruthy();
    }
  });

  test('FAQ –∏–º–µ–µ—Ç –ø—Ä–∞–≤–∏–ª—å–Ω—ã–µ ARIA –∞—Ç—Ä–∏–±—É—Ç—ã', async ({ page }) => {
    const faqSection = page.locator('.faq').first();
    if (await faqSection.count() === 0) {
      test.skip();
    }

    await faqSection.scrollIntoViewIfNeeded();
    await page.waitForTimeout(500);

    const faqQuestions = page.locator('.faq__question');
    const questionsCount = await faqQuestions.count();

    if (questionsCount > 0) {
      const firstQuestion = faqQuestions.first();
      const firstAnswer = page.locator('.faq__answer').first();

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º ARIA –∞—Ç—Ä–∏–±—É—Ç—ã
      await expect(firstQuestion).toHaveAttribute('aria-expanded');
      await expect(firstQuestion).toHaveAttribute('aria-controls');
      await expect(firstAnswer).toHaveAttribute('role', 'region');
      await expect(firstAnswer).toHaveAttribute('aria-labelledby');

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Å–≤—è–∑—å –º–µ–∂–¥—É –≤–æ–ø—Ä–æ—Å–æ–º –∏ –æ—Ç–≤–µ—Ç–æ–º
      const questionId = await firstQuestion.getAttribute('id');
      const answerId = await firstAnswer.getAttribute('id');
      const ariaControls = await firstQuestion.getAttribute('aria-controls');
      const ariaLabelledBy = await firstAnswer.getAttribute('aria-labelledby');

      expect(ariaControls).toBe(answerId);
      expect(ariaLabelledBy).toBe(questionId);
    }
  });

  test('–ö–æ–Ω—Ç—Ä–∞—Å—Ç–Ω–æ—Å—Ç—å —Ç–µ–∫—Å—Ç–∞ –¥–æ—Å—Ç–∞—Ç–æ—á–Ω–∞', async ({ page }) => {
    // –ë–∞–∑–æ–≤—ã–π —Ç–µ—Å—Ç - –ø—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –æ—Å–Ω–æ–≤–Ω—ã–µ —Ç–µ–∫—Å—Ç–æ–≤—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã –≤–∏–¥–Ω—ã
    const mainText = page.locator('h1, h2, p').first();
    if (await mainText.count() > 0) {
      await expect(mainText).toBeVisible();

      // –ü–æ–ª—É—á–∞–µ–º computed styles –¥–ª—è –ø—Ä–æ–≤–µ—Ä–∫–∏ –∫–æ–Ω—Ç—Ä–∞—Å—Ç–Ω–æ—Å—Ç–∏
      const textColor = await mainText.evaluate(el => {
        const style = window.getComputedStyle(el);
        return style.color;
      });

      const backgroundColor = await mainText.evaluate(el => {
        const style = window.getComputedStyle(el);
        return style.backgroundColor;
      });

      // –¶–≤–µ—Ç–∞ –¥–æ–ª–∂–Ω—ã –±—ã—Ç—å –∑–∞–¥–∞–Ω—ã (–Ω–µ transparent)
      expect(textColor).not.toBe('rgba(0, 0, 0, 0)');
      expect(textColor).not.toBe('transparent');
    }
  });
});

assets/css/components/calculator.css.content:
/* ============================================
   Calculator Component Styles
   –°—Ç–∏–ª–∏ –¥–ª—è –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞ –º–æ—â–Ω–æ—Å—Ç–∏ –∫–æ–Ω–¥–∏—Ü–∏–æ–Ω–µ—Ä–∞
   ============================================ */

.calculator-power {
  background: var(--color-white);
  border-radius: var(--radius-lg);
  padding: var(--spacing-2xl);
  box-shadow: var(--shadow-md);
  margin: var(--spacing-2xl) 0;
}

.calculator-power__title {
  font-size: var(--font-size-2xl);
  font-weight: var(--font-weight-bold);
  color: var(--color-gray-900);
  margin-bottom: var(--spacing-md);
  text-align: center;
}

.calculator-power__description {
  font-size: var(--font-size-base);
  color: var(--color-gray-600);
  text-align: center;
  margin-bottom: var(--spacing-xl);
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
}

.calculator-power__form {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: var(--spacing-lg);
  margin-bottom: var(--spacing-xl);
}

.calculator-power__form-group {
  display: flex;
  flex-direction: column;
}

.calculator-power__form-group--full {
  grid-column: 1 / -1;
}

.calculator-power__label {
  font-size: var(--font-size-sm);
  font-weight: var(--font-weight-medium);
  color: var(--color-gray-700);
  margin-bottom: var(--spacing-xs);
}

.calculator-power__label--required::after {
  content: ' *';
  color: var(--color-error);
}

.calculator-power__input,
.calculator-power__select {
  width: 100%;
  padding: var(--spacing-md);
  font-size: var(--font-size-base);
  border: 2px solid var(--color-gray-300);
  border-radius: var(--radius-md);
  transition: border-color var(--transition-fast);
  font-family: var(--font-family-base);
}

.calculator-power__input:focus,
.calculator-power__select:focus {
  outline: none;
  border-color: var(--color-primary);
  box-shadow: 0 0 0 3px rgba(0, 102, 204, 0.1);
}

.calculator-power__input:invalid {
  border-color: var(--color-error);
}

.calculator-power__help {
  font-size: var(--font-size-xs);
  color: var(--color-gray-500);
  margin-top: var(--spacing-xs);
}

.calculator-power__submit {
  grid-column: 1 / -1;
  justify-self: center;
  margin-top: var(--spacing-md);
}

.calculator-power__result {
  display: none;
  margin-top: var(--spacing-xl);
  padding-top: var(--spacing-xl);
  border-top: 2px solid var(--color-gray-200);
  opacity: 0;
  transform: translateY(10px);
  transition: opacity var(--transition-normal), transform var(--transition-normal);
}

.calculator-power__result--visible {
  opacity: 1;
  transform: translateY(0);
}

.calculator-power__result-title {
  font-size: var(--font-size-xl);
  font-weight: var(--font-weight-bold);
  color: var(--color-gray-900);
  margin-bottom: var(--spacing-lg);
  text-align: center;
}

.calculator-power__result-stats {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: var(--spacing-lg);
  margin-bottom: var(--spacing-xl);
}

.calculator-power__stat {
  background: var(--color-gray-50);
  padding: var(--spacing-lg);
  border-radius: var(--radius-md);
  text-align: center;
}

.calculator-power__stat-label {
  display: block;
  font-size: var(--font-size-sm);
  color: var(--color-gray-600);
  margin-bottom: var(--spacing-xs);
}

.calculator-power__stat-value {
  display: block;
  font-size: var(--font-size-2xl);
  font-weight: var(--font-weight-bold);
  color: var(--color-primary);
}

.calculator-power__stat-value--highlight {
  color: var(--color-success);
  font-size: var(--font-size-3xl);
}

.calculator-power__recommendation {
  background: var(--color-primary-light, #E6F2FF);
  padding: var(--spacing-lg);
  border-radius: var(--radius-md);
  margin-bottom: var(--spacing-lg);
  border-left: 4px solid var(--color-primary);
}

.calculator-power__recommendation-title {
  font-size: var(--font-size-lg);
  font-weight: var(--font-weight-semibold);
  color: var(--color-gray-900);
  margin-bottom: var(--spacing-md);
}

.calculator-power__recommendation-text {
  font-size: var(--font-size-base);
  color: var(--color-gray-700);
  line-height: 1.6;
  margin-bottom: var(--spacing-sm);
}

.calculator-power__recommendation-models {
  font-size: var(--font-size-sm);
  color: var(--color-gray-600);
  font-style: italic;
}

.calculator-power__actions {
  display: flex;
  gap: var(--spacing-md);
  justify-content: center;
  flex-wrap: wrap;
  margin-bottom: var(--spacing-lg);
}

.calculator-power__disclaimer {
  font-size: var(--font-size-xs);
  color: var(--color-gray-500);
  text-align: center;
  font-style: italic;
}

/* –ê–¥–∞–ø—Ç–∏–≤–Ω–æ—Å—Ç—å */
@media (max-width: 768px) {
  .calculator-power {
    padding: var(--spacing-lg);
  }

  .calculator-power__form {
    grid-template-columns: 1fr;
  }

  .calculator-power__result-stats {
    grid-template-columns: 1fr;
  }

  .calculator-power__actions {
    flex-direction: column;
  }

  .calculator-power__actions .btn {
    width: 100%;
  }
}

assets/js/form-handler.js.content:
/**
 * Form Handler - –û–±—Ä–∞–±–æ—Ç—á–∏–∫ —Ñ–æ—Ä–º –æ–±—Ä–∞—Ç–Ω–æ–π —Å–≤—è–∑–∏
 * –î–æ–±–∞–≤–ª—è–µ—Ç –≤–∞–ª–∏–¥–∞—Ü–∏—é –∏ –æ–±—Ä–∞–±–æ—Ç–∫—É –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ä–º
 */

(function() {
  'use strict';

  // –§—É–Ω–∫—Ü–∏—è –≤–∞–ª–∏–¥–∞—Ü–∏–∏ —Ç–µ–ª–µ—Ñ–æ–Ω–∞
  function validatePhone(phone) {
    const phoneRegex = /^[\+]?[0-9\s\-\(\)]{10,}$/;
    return phoneRegex.test(phone.replace(/\s/g, ''));
  }

  // –§—É–Ω–∫—Ü–∏—è –≤–∞–ª–∏–¥–∞—Ü–∏–∏ –∏–º–µ–Ω–∏
  function validateName(name) {
    return name.trim().length >= 2 && name.trim().length <= 100;
  }

  // –§—É–Ω–∫—Ü–∏—è –ø–æ–∫–∞–∑–∞ –æ—à–∏–±–∫–∏
  function showError(input, message) {
    const formGroup = input.closest('.form__group');
    if (!formGroup) return;

    // –£–¥–∞–ª—è–µ–º —Å—É—â–µ—Å—Ç–≤—É—é—â–∏–µ –æ—à–∏–±–∫–∏
    const existingError = formGroup.querySelector('.form__error, .form__error-message');
    if (existingError) {
      existingError.remove();
    }

    // –î–æ–±–∞–≤–ª—è–µ–º –∫–ª–∞—Å—Å –æ—à–∏–±–∫–∏
    formGroup.classList.add('form__group--error');
    input.classList.add('form__input--error');

    // –°–æ–∑–¥–∞–µ–º —ç–ª–µ–º–µ–Ω—Ç –æ—à–∏–±–∫–∏
    const errorElement = document.createElement('span');
    errorElement.className = 'form__error-message';
    errorElement.textContent = message;
    errorElement.setAttribute('role', 'alert');
    errorElement.setAttribute('aria-live', 'polite');
    formGroup.appendChild(errorElement);

    // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º aria-invalid
    input.setAttribute('aria-invalid', 'true');

    // –§–æ–∫—É—Å–∏—Ä—É–µ–º—Å—è –Ω–∞ –ø–æ–ª–µ —Å –æ—à–∏–±–∫–æ–π
    input.focus();
  }

  // –§—É–Ω–∫—Ü–∏—è —É–¥–∞–ª–µ–Ω–∏—è –æ—à–∏–±–∫–∏
  function clearError(input) {
    const formGroup = input.closest('.form__group');
    if (!formGroup) return;

    formGroup.classList.remove('form__group--error');
    input.classList.remove('form__input--error');
    const errorElement = formGroup.querySelector('.form__error, .form__error-message');
    if (errorElement) {
      errorElement.remove();
    }
    input.removeAttribute('aria-invalid');
  }

  // –§—É–Ω–∫—Ü–∏—è –ø–æ–∫–∞–∑–∞ —É—Å–ø–µ—Ö–∞
  function showSuccess(form) {
    // –£–¥–∞–ª—è–µ–º –≤—Å–µ –æ—à–∏–±–∫–∏
    const errors = form.querySelectorAll('.form__error, .form__error-message');
    errors.forEach(error => error.remove());

    const errorGroups = form.querySelectorAll('.form__group--error');
    errorGroups.forEach(group => group.classList.remove('form__group--error'));

    const errorInputs = form.querySelectorAll('.form__input--error');
    errorInputs.forEach(input => input.classList.remove('form__input--error'));

    // –î–æ–±–∞–≤–ª—è–µ–º –∫–ª–∞—Å—Å —É—Å–ø–µ—Ö–∞
    form.classList.add('form--success');

    // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± —É—Å–ø–µ—Ö–µ
    let successMessage = form.querySelector('.form__success-message');
    if (!successMessage) {
      successMessage = document.createElement('div');
      successMessage.className = 'form__success-message';
      successMessage.innerHTML = `
        <svg class="icon icon--lg icon--success" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
          <path d="M20 6L9 17l-5-5"/>
        </svg>
        <div>
          <strong>–°–ø–∞—Å–∏–±–æ!</strong>
          <p>–í–∞—à–∞ –∑–∞—è–≤–∫–∞ —É—Å–ø–µ—à–Ω–æ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∞. –ú—ã —Å–≤—è–∂–µ–º—Å—è —Å –≤–∞–º–∏ –≤ –±–ª–∏–∂–∞–π—à–µ–µ –≤—Ä–µ–º—è.</p>
        </div>
      `;
      successMessage.setAttribute('role', 'status');
      successMessage.setAttribute('aria-live', 'polite');
      form.insertBefore(successMessage, form.firstChild);
    } else {
      successMessage.style.display = 'block';
    }

    // –°–±—Ä–∞—Å—ã–≤–∞–µ–º —Ñ–æ—Ä–º—É
    form.reset();

    // –ü—Ä–æ–∫—Ä—É—á–∏–≤–∞–µ–º –∫ —Å–æ–æ–±—â–µ–Ω–∏—é
    successMessage.scrollIntoView({ behavior: 'smooth', block: 'center' });

    // –£–±–∏—Ä–∞–µ–º –∫–ª–∞—Å—Å —É—Å–ø–µ—Ö–∞ —á–µ—Ä–µ–∑ 5 —Å–µ–∫—É–Ω–¥
    setTimeout(() => {
      form.classList.remove('form--success');
      if (successMessage) {
        successMessage.style.display = 'none';
      }
    }, 5000);
  }

  // –§—É–Ω–∫—Ü–∏—è –æ–±—Ä–∞–±–æ—Ç–∫–∏ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ä–º—ã
  function handleSubmit(event) {
    event.preventDefault();

    const form = event.target;
    const formData = new FormData(form);

    const name = formData.get('name') || '';
    const phone = formData.get('phone') || '';
    const message = formData.get('message') || '';

    let isValid = true;

    // –í–∞–ª–∏–¥–∞—Ü–∏—è –∏–º–µ–Ω–∏
    const nameInput = form.querySelector('[name="name"]');
    if (!validateName(name)) {
      showError(nameInput, '–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–µ –∏–º—è (–æ—Ç 2 –¥–æ 100 —Å–∏–º–≤–æ–ª–æ–≤)');
      isValid = false;
    } else {
      clearError(nameInput);
    }

    // –í–∞–ª–∏–¥–∞—Ü–∏—è —Ç–µ–ª–µ—Ñ–æ–Ω–∞
    const phoneInput = form.querySelector('[name="phone"]');
    if (!validatePhone(phone)) {
      showError(phoneInput, '–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –Ω–æ–º–µ—Ä —Ç–µ–ª–µ—Ñ–æ–Ω–∞');
      isValid = false;
    } else {
      clearError(phoneInput);
    }

    if (!isValid) {
      return;
    }

    // –û—Ç–∫–ª—é—á–∞–µ–º –∫–Ω–æ–ø–∫—É –æ—Ç–ø—Ä–∞–≤–∫–∏
    const submitButton = form.querySelector('button[type="submit"]');
    const originalText = submitButton.textContent;
    submitButton.disabled = true;
    submitButton.classList.add('btn--loading');
    submitButton.textContent = '–û—Ç–ø—Ä–∞–≤–∫–∞...';

    // –ü–æ–ª—É—á–∞–µ–º email –∏–∑ —Ñ–æ—Ä–º—ã (–µ—Å–ª–∏ –µ—Å—Ç—å)
    const email = formData.get('email') || '';

    // –û—Ç–ø—Ä–∞–≤–∫–∞ –¥–∞–Ω–Ω—ã—Ö –Ω–∞ —Å–µ—Ä–≤–µ—Ä
    fetch('/api/contact', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        name: name,
        phone: phone,
        email: email,
        message: message
      })
    })
    .then(response => {
      if (!response.ok) {
        return response.json().then(err => {
          throw new Error(err.error || '–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ —Ñ–æ—Ä–º—ã');
        });
      }
      return response.json();
    })
    .then(data => {
      // –£—Å–ø–µ—à–Ω–∞—è –æ—Ç–ø—Ä–∞–≤–∫–∞
      showSuccess(form);

      // –û—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ä–º—ã –≤ –∞–Ω–∞–ª–∏—Ç–∏–∫–µ (–µ—Å–ª–∏ –¥–æ—Å—Ç—É–ø–Ω–æ)
      if (typeof ym !== 'undefined') {
        ym(105124660, 'reachGoal', 'form_submit');
      }
      if (typeof gtag !== 'undefined') {
        gtag('event', 'form_submit', {
          'event_category': 'form',
          'event_label': 'contact_form'
        });
      }
    })
    .catch(error => {
      console.error('–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ä–º—ã:', error);

      // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –æ—à–∏–±–∫—É –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—é
      const errorMessage = document.createElement('div');
      errorMessage.className = 'form__error-message';
      errorMessage.style.cssText = 'margin-top: 1rem; padding: 1rem; background: #fee; border: 1px solid #fcc; border-radius: 4px; color: #c33;';
      errorMessage.innerHTML = `
        <strong>–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏:</strong><br>
        ${error.message || '–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ —Ñ–æ—Ä–º—ã. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ –∏–ª–∏ —Å–≤—è–∂–∏—Ç–µ—Å—å —Å –Ω–∞–º–∏ –ø–æ —Ç–µ–ª–µ—Ñ–æ–Ω—É.'}
      `;
      form.insertBefore(errorMessage, form.firstChild);

      // –£–¥–∞–ª—è–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± –æ—à–∏–±–∫–µ —á–µ—Ä–µ–∑ 10 —Å–µ–∫—É–Ω–¥
      setTimeout(() => {
        errorMessage.remove();
      }, 10000);
    })
    .finally(() => {
      // –í–æ—Å—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∫–Ω–æ–ø–∫—É
      submitButton.disabled = false;
      submitButton.classList.remove('btn--loading');
      submitButton.textContent = originalText;
    });
  }

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ DOM
  function init() {
    const forms = document.querySelectorAll('form[aria-label="–§–æ—Ä–º–∞ –æ–±—Ä–∞—Ç–Ω–æ–π —Å–≤—è–∑–∏"], form.form');

    forms.forEach(form => {
      // –î–æ–±–∞–≤–ª—è–µ–º –æ–±—Ä–∞–±–æ—Ç—á–∏–∫ –æ—Ç–ø—Ä–∞–≤–∫–∏
      form.addEventListener('submit', handleSubmit);

      // –í–∞–ª–∏–¥–∞—Ü–∏—è –ø—Ä–∏ –ø–æ—Ç–µ—Ä–µ —Ñ–æ–∫—É—Å–∞
      const inputs = form.querySelectorAll('input, textarea');
      inputs.forEach(input => {
        input.addEventListener('blur', function() {
          if (this.value.trim()) {
            if (this.type === 'tel' && !validatePhone(this.value)) {
              showError(this, '–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –Ω–æ–º–µ—Ä —Ç–µ–ª–µ—Ñ–æ–Ω–∞');
            } else if (this.name === 'name' && !validateName(this.value)) {
              showError(this, '–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–µ –∏–º—è (–æ—Ç 2 –¥–æ 100 —Å–∏–º–≤–æ–ª–æ–≤)');
            } else {
              clearError(this);
            }
          } else if (this.hasAttribute('required') || this.getAttribute('aria-required') === 'true') {
            // –î–ª—è –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã—Ö –ø–æ–ª–µ–π –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –æ—à–∏–±–∫—É –µ—Å–ª–∏ –ø—É—Å—Ç–æ
            showError(this, '–≠—Ç–æ –ø–æ–ª–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ –¥–ª—è –∑–∞–ø–æ–ª–Ω–µ–Ω–∏—è');
          }
        });

        // –û—á–∏—Å—Ç–∫–∞ –æ—à–∏–±–∫–∏ –ø—Ä–∏ –≤–≤–æ–¥–µ
        input.addEventListener('input', function() {
          if (this.getAttribute('aria-invalid') === 'true' || this.classList.contains('form__input--error')) {
            clearError(this);
          }
        });
      });
    });
  }

  // –ó–∞–ø—É—Å–∫ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ DOM
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', init);
  } else {
    init();
  }
})();

assets/css/components/buttons.css.content:
/* ============================================
   Buttons Component Styles
   ============================================ */

.btn {
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: var(--spacing-sm);
  padding: var(--spacing-md) var(--spacing-lg);
  font-size: 1rem;
  font-weight: 600;
  line-height: 1.5;
  text-decoration: none;
  border: 2px solid transparent;
  border-radius: var(--radius-md);
  cursor: pointer;
  transition: all var(--transition-base);
  min-height: 44px;
  touch-action: manipulation;
}

.btn:focus {
  outline: none;
}

.btn:focus-visible {
  outline: var(--focus-outline-width) solid var(--color-primary);
  outline-offset: var(--focus-outline-offset);
  box-shadow: var(--focus-ring);
}

.btn:focus:not(:focus-visible) {
  outline: none;
}

/* Primary Button */
.btn--primary {
  background-color: var(--color-primary);
  color: var(--text-inverse);
  border-color: var(--color-primary);
}

.btn--primary:hover:not(:disabled) {
  background-color: var(--color-primary-hover);
  border-color: var(--color-primary-hover);
  color: var(--text-inverse);
  text-decoration: none;
  transform: translateY(-2px);
  box-shadow: var(--shadow-md);
}

.btn--primary:active:not(:disabled) {
  transform: translateY(0);
  box-shadow: var(--shadow-sm);
}

/* Secondary Button */
.btn--secondary {
  background-color: var(--bg-secondary);
  color: var(--text-primary);
  border-color: var(--border-color);
}

.btn--secondary:hover:not(:disabled) {
  background-color: var(--bg-tertiary);
  border-color: var(--border-color-dark);
  color: var(--text-primary);
  text-decoration: none;
  transform: translateY(-2px);
  box-shadow: var(--shadow-md);
}

.btn--secondary:active:not(:disabled) {
  transform: translateY(0);
  box-shadow: var(--shadow-sm);
}

/* Outline Button */
.btn--outline {
  background-color: transparent;
  color: var(--color-primary);
  border-color: var(--color-primary);
}

.btn--outline:hover:not(:disabled) {
  background-color: var(--color-primary);
  color: var(--text-inverse);
  text-decoration: none;
  transform: translateY(-2px);
  box-shadow: var(--shadow-md);
}

.btn--outline:active:not(:disabled) {
  transform: translateY(0);
  box-shadow: var(--shadow-sm);
}

/* Ghost Button */
.btn--ghost {
  background-color: transparent;
  color: var(--text-primary);
  border-color: transparent;
}

.btn--ghost:hover:not(:disabled) {
  background-color: var(--bg-secondary);
  color: var(--text-primary);
  text-decoration: none;
}

.btn--ghost:active:not(:disabled) {
  background-color: var(--bg-tertiary);
}

/* Button Sizes */
.btn--sm {
  padding: var(--spacing-sm) var(--spacing-md);
  font-size: 0.875rem;
  min-height: 36px;
}

.btn--lg {
  padding: var(--spacing-lg) var(--spacing-xl);
  font-size: 1.125rem;
  min-height: 52px;
}

/* Button States */
.btn:disabled,
.btn--disabled {
  opacity: 0.6;
  cursor: not-allowed;
  pointer-events: none;
  filter: grayscale(0.2);
}

.btn:disabled:focus-visible,
.btn--disabled:focus-visible {
  outline: var(--focus-outline-width) solid var(--color-gray-400);
  outline-offset: var(--focus-outline-offset);
  box-shadow: 0 0 0 3px rgba(107, 114, 128, 0.2);
}

.btn--loading {
  position: relative;
  color: transparent;
  pointer-events: none;
  cursor: wait;
}

.btn--loading::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 20px;
  height: 20px;
  margin-top: -10px;
  margin-left: -10px;
  border: 2px solid currentColor;
  border-top-color: transparent;
  border-radius: 50%;
  animation: spin 0.6s linear infinite;
  will-change: transform;
}

.btn--loading.btn--primary::after {
  border-color: var(--text-inverse);
  border-top-color: transparent;
}

.btn--loading.btn--secondary::after,
.btn--loading.btn--outline::after,
.btn--loading.btn--ghost::after {
  border-color: var(--color-primary);
  border-top-color: transparent;
}

@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

/* Icon Button */
.btn--icon {
  width: 44px;
  height: 44px;
  padding: 0;
  min-height: 44px;
}

.btn__icon {
  width: 20px;
  height: 20px;
  fill: none;
  stroke: currentColor;
  stroke-width: 2;
  flex-shrink: 0;
}

/* Full Width Button */
.btn--full {
  width: 100%;
}

/* Gradient Button */
.btn--gradient {
  background: var(--gradient-primary);
  color: var(--text-inverse);
  border: none;
  position: relative;
  overflow: hidden;
}

.btn--gradient::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: var(--gradient-hover-primary);
  transition: left var(--transition-base);
  z-index: 0;
}

.btn--gradient > * {
  position: relative;
  z-index: 1;
}

@media (hover: hover) and (pointer: fine) {
  .btn--gradient:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: var(--shadow-primary-lg);
  }

  .btn--gradient:hover:not(:disabled)::before {
    left: 0;
  }
}

.btn--gradient:active:not(:disabled) {
  transform: translateY(0);
}

/* Glow Button */
.btn--glow {
  box-shadow: var(--shadow-glow);
  animation: pulseGlow 2s ease-in-out infinite;
}

@keyframes pulseGlow {
  0%, 100% {
    box-shadow: var(--shadow-glow);
  }
  50% {
    box-shadow: var(--shadow-glow-primary);
  }
}

.btn--glow:hover:not(:disabled) {
  animation: none;
  box-shadow: var(--shadow-glow-primary);
}

/* Ripple Effect */
.btn--ripple {
  position: relative;
  overflow: hidden;
}

.btn--ripple::after {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.5);
  transform: translate(-50%, -50%);
  transition: width 0.6s, height 0.6s;
}

.btn--ripple:active::after {
  width: 300px;
  height: 300px;
}

/* Multi-color Gradient Button */
.btn--multicolor {
  background: var(--gradient-multicolor);
  background-size: 200% 200%;
  color: var(--text-inverse);
  border: none;
  animation: gradientShift 3s ease infinite;
}

@keyframes gradientShift {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

netlify/functions/contact.js.content:
/**
 * Netlify Function –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ä–º—ã —á–µ—Ä–µ–∑ Resend
 * –ü—É—Ç—å: /api/contact
 */

const { Resend } = require('resend');

exports.handler = async (event, context) => {
  // –†–∞–∑—Ä–µ—à–∞–µ–º CORS
  const headers = {
    'Access-Control-Allow-Origin': '*',
    'Access-Control-Allow-Headers': 'Content-Type',
    'Access-Control-Allow-Methods': 'POST, OPTIONS',
    'Content-Type': 'application/json',
  };

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ OPTIONS –∑–∞–ø—Ä–æ—Å–∞ (preflight)
  if (event.httpMethod === 'OPTIONS') {
    return {
      statusCode: 200,
      headers,
      body: '',
    };
  }

  // –¢–æ–ª—å–∫–æ POST –∑–∞–ø—Ä–æ—Å—ã
  if (event.httpMethod !== 'POST') {
    return {
      statusCode: 405,
      headers,
      body: JSON.stringify({ error: 'Method not allowed' }),
    };
  }

  try {
    // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ —Ñ–æ—Ä–º—ã
    const formData = JSON.parse(event.body);
    const { name, phone, email, message } = formData;

    // –í–∞–ª–∏–¥–∞—Ü–∏—è
    if (!name || !phone) {
      return {
        statusCode: 400,
        headers,
        body: JSON.stringify({ error: '–ò–º—è –∏ —Ç–µ–ª–µ—Ñ–æ–Ω –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã –¥–ª—è –∑–∞–ø–æ–ª–Ω–µ–Ω–∏—è' }),
      };
    }

    // –ü–æ–ª—É—á–∞–µ–º API –∫–ª—é—á Resend –∏–∑ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã—Ö –æ–∫—Ä—É–∂–µ–Ω–∏—è
    const resendApiKey = process.env.RESEND_API_KEY;
    if (!resendApiKey) {
      console.error('RESEND_API_KEY –Ω–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω');
      return {
        statusCode: 500,
        headers,
        body: JSON.stringify({ error: '–û—à–∏–±–∫–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏ —Å–µ—Ä–≤–µ—Ä–∞' }),
      };
    }

    const resend = new Resend(resendApiKey);

    // –ü–æ–ª—É—á–∞–µ–º email –ø–æ–ª—É—á–∞—Ç–µ–ª—è –∏–∑ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã—Ö –æ–∫—Ä—É–∂–µ–Ω–∏—è –∏–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ–º –¥–µ—Ñ–æ–ª—Ç–Ω—ã–π
    const recipientEmail = process.env.CONTACT_EMAIL || 'info@msk.splitis.ru';

    // –§–æ—Ä–º–∏—Ä—É–µ–º —Ç–µ–º—É –ø–∏—Å—å–º–∞
    const subject = `–ù–æ–≤–∞—è –∑–∞—è–≤–∫–∞ —Å —Å–∞–π—Ç–∞ msk.splitis.ru${name ? ` –æ—Ç ${name}` : ''}`;

    // –§–æ—Ä–º–∏—Ä—É–µ–º —Ç–µ–ª–æ –ø–∏—Å—å–º–∞
    const emailBody = `
      <h2>–ù–æ–≤–∞—è –∑–∞—è–≤–∫–∞ —Å —Å–∞–π—Ç–∞</h2>
      <p><strong>–ò–º—è:</strong> ${name}</p>
      <p><strong>–¢–µ–ª–µ—Ñ–æ–Ω:</strong> ${phone}</p>
      ${email ? `<p><strong>Email:</strong> ${email}</p>` : ''}
      ${message ? `<p><strong>–°–æ–æ–±—â–µ–Ω–∏–µ:</strong><br>${message.replace(/\n/g, '<br>')}</p>` : ''}
      <hr>
      <p><small>–î–∞—Ç–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏: ${new Date().toLocaleString('ru-RU', { timeZone: 'Europe/Moscow' })}</small></p>
      <p><small>IP –∞–¥—Ä–µ—Å: ${event.headers['x-forwarded-for'] || event.headers['x-nf-client-connection-ip'] || '–Ω–µ–∏–∑–≤–µ—Å—Ç–Ω–æ'}</small></p>
    `;

    // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –ø–∏—Å—å–º–æ
    const { data, error } = await resend.emails.send({
      from: 'noreply@msk.splitis.ru', // –ó–∞–º–µ–Ω–∏—Ç–µ –Ω–∞ –≤–∞—à –≤–µ—Ä–∏—Ñ–∏—Ü–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –¥–æ–º–µ–Ω –≤ Resend
      to: recipientEmail,
      replyTo: email || recipientEmail,
      subject: subject,
      html: emailBody,
    });

    if (error) {
      console.error('–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ —á–µ—Ä–µ–∑ Resend:', error);
      return {
        statusCode: 500,
        headers,
        body: JSON.stringify({ error: '–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ –ø–∏—Å—å–º–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ –∏–ª–∏ —Å–≤—è–∂–∏—Ç–µ—Å—å —Å –Ω–∞–º–∏ –ø–æ —Ç–µ–ª–µ—Ñ–æ–Ω—É.' }),
      };
    }

    return {
      statusCode: 200,
      headers,
      body: JSON.stringify({ 
        success: true, 
        message: '–ó–∞—è–≤–∫–∞ —É—Å–ø–µ—à–Ω–æ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∞',
        id: data?.id 
      }),
    };

  } catch (error) {
    console.error('–û—à–∏–±–∫–∞ –æ–±—Ä–∞–±–æ—Ç–∫–∏ –∑–∞–ø—Ä–æ—Å–∞:', error);
    return {
      statusCode: 500,
      headers,
      body: JSON.stringify({ error: '–í–Ω—É—Ç—Ä–µ–Ω–Ω—è—è –æ—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞' }),
    };
  }
};

assets/js/image-optimizer.js.content:
/* ============================================
   Image Optimizer
   Lazy loading, WebP –ø–æ–¥–¥–µ—Ä–∂–∫–∞, –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è
   ============================================ */

(function() {
  'use strict';

  /**
   * –ü—Ä–æ–≤–µ—Ä—è–µ—Ç –ø–æ–¥–¥–µ—Ä–∂–∫—É WebP
   */
  function supportsWebP() {
    return new Promise((resolve) => {
      const webP = new Image();
      webP.onload = webP.onerror = () => {
        resolve(webP.height === 2);
      };
      webP.src = 'data:image/webp;base64,UklGRjoAAABXRUJQVlA4IC4AAACyAgCdASoCAAIALmk0mk0iIiIiIgBoSygABc6WWgAA/veff/0PP8bA//LwYAAA';
    });
  }

  /**
   * –ó–∞–º–µ–Ω—è–µ—Ç —Ä–∞—Å—à–∏—Ä–µ–Ω–∏–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –Ω–∞ WebP –µ—Å–ª–∏ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç—Å—è
   */
  async function optimizeImages() {
    const supportsWebPFormat = await supportsWebP();
    const images = document.querySelectorAll('img[data-src], img[src]');

    images.forEach(img => {
      // Lazy loading –¥–ª—è –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π –±–µ–∑ data-src (–∫—Ä–æ–º–µ hero —Å–µ–∫—Ü–∏–π)
      const isHeroImage = img.closest('.hero') !== null;
      if (img.src && !img.hasAttribute('loading') && !img.hasAttribute('data-src') && !isHeroImage) {
        img.setAttribute('loading', 'lazy');
      }

      // –ó–∞–º–µ–Ω–∞ –Ω–∞ WebP –µ—Å–ª–∏ –ø–æ–¥–¥–µ—Ä–∂–∏–≤–∞–µ—Ç—Å—è
      if (supportsWebPFormat && img.src) {
        const src = img.getAttribute('data-src') || img.src;
        if (src && !src.includes('.webp') && (src.includes('.jpg') || src.includes('.jpeg') || src.includes('.png'))) {
          const webpSrc = src.replace(/\.(jpg|jpeg|png)$/i, '.webp');
          // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Å—É—â–µ—Å—Ç–≤–æ–≤–∞–Ω–∏–µ WebP –≤–µ—Ä—Å–∏–∏ —á–µ—Ä–µ–∑ fetch
          fetch(webpSrc, { method: 'HEAD' })
            .then(response => {
              if (response.ok) {
                img.setAttribute('data-webp', webpSrc);
              }
            })
            .catch(() => {
              // WebP –≤–µ—Ä—Å–∏—è –Ω–µ –Ω–∞–π–¥–µ–Ω–∞, –∏—Å–ø–æ–ª—å–∑—É–µ–º –æ—Ä–∏–≥–∏–Ω–∞–ª
            });
        }
      }
    });

    // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è Intersection Observer –¥–ª—è lazy loading
    initLazyLoading();
  }

  /**
   * –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ—Ç lazy loading –¥–ª—è –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π
   */
  function initLazyLoading() {
    if ('IntersectionObserver' in window) {
      const imageObserver = new IntersectionObserver((entries, observer) => {
        entries.forEach(entry => {
          if (entry.isIntersecting) {
            const img = entry.target;

            // –ó–∞–≥—Ä—É–∂–∞–µ–º WebP –µ—Å–ª–∏ –¥–æ—Å—Ç—É–ø–µ–Ω
            if (img.hasAttribute('data-webp')) {
              const webpImg = new Image();
              webpImg.onload = () => {
                img.src = img.getAttribute('data-webp');
                img.removeAttribute('data-webp');
              };
              webpImg.onerror = () => {
                // Fallback –Ω–∞ –æ—Ä–∏–≥–∏–Ω–∞–ª—å–Ω–æ–µ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ
                img.removeAttribute('data-webp');
              };
              webpImg.src = img.getAttribute('data-webp');
            } else if (img.hasAttribute('data-src')) {
              img.src = img.getAttribute('data-src');
              img.removeAttribute('data-src');
            }

            img.classList.add('loaded');
            observer.unobserve(img);
          }
        });
      }, {
        rootMargin: '50px'
      });

      document.querySelectorAll('img[data-src], img[data-webp]').forEach(img => {
        imageObserver.observe(img);
      });
    } else {
      // Fallback –¥–ª—è —Å—Ç–∞—Ä—ã—Ö –±—Ä–∞—É–∑–µ—Ä–æ–≤
      document.querySelectorAll('img[data-src]').forEach(img => {
        img.src = img.getAttribute('data-src');
      });
    }
  }

  /**
   * –û–ø—Ç–∏–º–∏–∑–∏—Ä—É–µ—Ç –∑–∞–≥—Ä—É–∑–∫—É –∫—Ä–∏—Ç–∏—á–µ—Å–∫–∏—Ö –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π
   */
  function optimizeCriticalImages() {
    // –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∞ hero –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏–π
    const heroImages = document.querySelectorAll('.hero img[src], .hero img[data-src]');
    heroImages.forEach(img => {
      const link = document.createElement('link');
      link.rel = 'preload';
      link.as = 'image';
      link.href = img.getAttribute('data-src') || img.src;
      document.head.appendChild(link);
    });
  }

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ DOM
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', () => {
      optimizeImages();
      optimizeCriticalImages();
    });
  } else {
    optimizeImages();
    optimizeCriticalImages();
  }

  // –≠–∫—Å–ø–æ—Ä—Ç —Ñ—É–Ω–∫—Ü–∏–π
  window.ImageOptimizer = {
    optimizeImages,
    supportsWebP,
    initLazyLoading
  };

})();

assets/css/components/stats.css.content:
/* Stats Component - Premium Enhanced */

.stats {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: var(--spacing-lg);
}

@media (min-width: 768px) {
  .stats {
    grid-template-columns: repeat(4, 1fr);
  }
}

.stat-card {
  text-align: center;
  padding: var(--spacing-xl);
  background: var(--bg-primary);
  border-radius: var(--radius-lg);
  border: 1px solid var(--border-color);
  box-shadow: var(--shadow-sm);
  transition: all var(--transition-base);
  position: relative;
  overflow: hidden;
}

.stat-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 4px;
  background: var(--gradient-primary);
  transform: scaleX(0);
  transform-origin: left;
  transition: transform var(--transition-base);
}

@media (hover: hover) and (pointer: fine) {
  .stat-card:hover {
    transform: translateY(-4px);
    box-shadow: var(--shadow-primary-lg);
    border-color: var(--color-primary);
  }

  .stat-card:hover::before {
    transform: scaleX(1);
  }
}

.stat-card__number {
  display: block;
  font-size: clamp(2rem, 4vw, 3rem);
  font-weight: 700;
  background: var(--gradient-primary);
  -webkit-background-clip: text;
  background-clip: text;
  -webkit-text-fill-color: transparent;
  margin-bottom: var(--spacing-xs);
  line-height: 1.2;
}

.stat-card__label {
  font-size: 0.875rem;
  color: var(--text-secondary);
  font-weight: 500;
}

