// @ts-check
const { test, expect } = require('@playwright/test');

test.describe('–ù–∞–≤–∏–≥–∞—Ü–∏—è', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
  });

  test('–ì–ª–∞–≤–Ω–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞ –∑–∞–≥—Ä—É–∂–∞–µ—Ç—Å—è –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ', async ({ page }) => {
    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –∑–∞–≥–æ–ª–æ–≤–∫–∞
    await expect(page).toHaveTitle(/–ö–æ–Ω–¥–∏—Ü–∏–æ–Ω–µ—Ä—ã –≤ –ú–æ—Å–∫–≤–µ/);

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –æ—Å–Ω–æ–≤–Ω—ã—Ö —Å–µ–∫—Ü–∏–π
    await expect(page.locator('header')).toBeVisible();
    await expect(page.locator('footer')).toBeVisible();
    await expect(page.locator('.hero')).toBeVisible();
    const sectionsCount = await page.locator('.section').count();
    expect(sectionsCount).toBeGreaterThan(0);
  });

  test('–î–µ—Å–∫—Ç–æ–ø–Ω–∞—è –Ω–∞–≤–∏–≥–∞—Ü–∏—è —Ä–∞–±–æ—Ç–∞–µ—Ç', async ({ page }) => {
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º —Ç–µ–∫—É—â–∏–π —Ä–∞–∑–º–µ—Ä viewport
    const currentViewport = page.viewportSize();
    const isMobile = currentViewport && currentViewport.width < 768;

    // –ù–∞ –º–æ–±–∏–ª—å–Ω—ã—Ö —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞—Ö –¥–µ—Å–∫—Ç–æ–ø–Ω–æ–µ –º–µ–Ω—é —Å–∫—Ä—ã—Ç–æ - –ø—Ä–æ–ø—É—Å–∫–∞–µ–º —Ç–µ—Å—Ç
    if (isMobile) {
      test.skip();
      return;
    }

    // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –¥–µ—Å–∫—Ç–æ–ø–Ω–æ–µ —Ä–∞–∑—Ä–µ—à–µ–Ω–∏–µ –µ—Å–ª–∏ –Ω–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–æ
    if (!currentViewport || currentViewport.width < 768) {
      await page.setViewportSize({ width: 1920, height: 1080 });
    }

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞–ª–∏—á–∏—è –Ω–∞–≤–∏–≥–∞—Ü–∏–æ–Ω–Ω—ã—Ö —Å—Å—ã–ª–æ–∫
    const navLinks = page.locator('.header__nav-link');
    await expect(navLinks.first()).toBeVisible();

    // –ü—Ä–æ–≤–µ—Ä–∫–∞ –ø–µ—Ä–µ—Ö–æ–¥–∞ –ø–æ —Å—Å—ã–ª–∫–∞–º
    await page.click('text=–ö–∞—Ç–∞–ª–æ–≥');
    await expect(page).toHaveURL(/katalog\.html/);

    await page.goto('/');
    await page.click('text=–ö–æ–Ω—Ç–∞–∫—Ç—ã');
    await expect(page).toHaveURL(/kontakty\.html/);
  });

  test.describe('–ú–æ–±–∏–ª—å–Ω–æ–µ –º–µ–Ω—é', () => {
    test('–û—Ç–∫—Ä—ã–≤–∞–µ—Ç—Å—è –∏ –∑–∞–∫—Ä—ã–≤–∞–µ—Ç—Å—è –ø–æ –∫–ª–∏–∫—É –Ω–∞ –∫–Ω–æ–ø–∫—É', async ({ page }) => {
      // –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –º–æ–±–∏–ª—å–Ω–æ–µ —Ä–∞–∑—Ä–µ—à–µ–Ω–∏–µ
      await page.setViewportSize({ width: 375, height: 667 });

      const menuToggle = page.locator('#menu-toggle');
      const mobileMenu = page.locator('#mobile-menu');

      // –ú–µ–Ω—é –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å —Å–∫—Ä—ã—Ç–æ –∏–∑–Ω–∞—á–∞–ª—å–Ω–æ
      await expect(mobileMenu).not.toHaveClass(/mobile-menu--open/);
      await expect(menuToggle).toHaveAttribute('aria-expanded', 'false');

      // –û—Ç–∫—Ä—ã–≤–∞–µ–º –º–µ–Ω—é
      await menuToggle.click();
      await expect(mobileMenu).toHaveClass(/mobile-menu--open/);
      await expect(menuToggle).toHaveAttribute('aria-expanded', 'true');

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ñ–æ–Ω –∑–∞–±–ª–æ–∫–∏—Ä–æ–≤–∞–Ω
      const bodyOverflow = await page.evaluate(() => document.body.style.overflow);
      expect(bodyOverflow).toBe('hidden');

      // –ó–∞–∫—Ä—ã–≤–∞–µ–º –º–µ–Ω—é
      const menuClose = page.locator('#menu-close');
      await menuClose.click();
      await expect(mobileMenu).not.toHaveClass(/mobile-menu--open/);
      await expect(menuToggle).toHaveAttribute('aria-expanded', 'false');

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Å–∫—Ä–æ–ª–ª –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω
      const bodyOverflowAfter = await page.evaluate(() => document.body.style.overflow);
      expect(bodyOverflowAfter).toBe('');
    });

    test('–ó–∞–∫—Ä—ã–≤–∞–µ—Ç—Å—è –ø–æ –∫–ª–∞–≤–∏—à–µ Escape', async ({ page }) => {
      await page.setViewportSize({ width: 375, height: 667 });

      const menuToggle = page.locator('#menu-toggle');
      const mobileMenu = page.locator('#mobile-menu');

      // –û—Ç–∫—Ä—ã–≤–∞–µ–º –º–µ–Ω—é
      await menuToggle.click();
      await expect(mobileMenu).toHaveClass(/mobile-menu--open/);

      // –ó–∞–∫—Ä—ã–≤–∞–µ–º –ø–æ Escape
      await page.keyboard.press('Escape');
      await expect(mobileMenu).not.toHaveClass(/mobile-menu--open/);
    });

    test('–ó–∞–∫—Ä—ã–≤–∞–µ—Ç—Å—è –ø—Ä–∏ –∫–ª–∏–∫–µ –≤–Ω–µ –º–µ–Ω—é', async ({ page }) => {
      await page.setViewportSize({ width: 375, height: 667 });

      const menuToggle = page.locator('#menu-toggle');
      const mobileMenu = page.locator('#mobile-menu');

      // –û—Ç–∫—Ä—ã–≤–∞–µ–º –º–µ–Ω—é
      await menuToggle.click();
      await expect(mobileMenu).toHaveClass(/mobile-menu--open/);

      // –ö–ª–∏–∫–∞–µ–º –≤–Ω–µ –º–µ–Ω—é (–Ω–∞ –∑–∞—Ç–µ–º–Ω–µ–Ω–Ω—ã–π —Ñ–æ–Ω)
      await page.click('.mobile-menu', { position: { x: 10, y: 10 } });

      // –ú–µ–Ω—é –¥–æ–ª–∂–Ω–æ –∑–∞–∫—Ä—ã—Ç—å—Å—è (–µ—Å–ª–∏ –∫–ª–∏–∫ –±—ã–ª –Ω–∞ —Ñ–æ–Ω, –∞ –Ω–µ –Ω–∞ –∫–æ–Ω—Ç–µ–Ω—Ç)
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –º–µ–Ω—é –∑–∞–∫—Ä—ã–ª–æ—Å—å (–º–æ–∂–µ—Ç –∑–∞–∫—Ä—ã—Ç—å—Å—è –∏–ª–∏ –æ—Å—Ç–∞—Ç—å—Å—è –æ—Ç–∫—Ä—ã—Ç—ã–º –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç —Ä–µ–∞–ª–∏–∑–∞—Ü–∏–∏)
      const isOpen = await mobileMenu.evaluate(el => el.classList.contains('mobile-menu--open'));

      // –ï—Å–ª–∏ –º–µ–Ω—é –Ω–µ –∑–∞–∫—Ä—ã–ª–æ—Å—å –ø—Ä–∏ –∫–ª–∏–∫–µ –Ω–∞ —Ñ–æ–Ω, –ø–æ–ø—Ä–æ–±—É–µ–º –∫–ª–∏–∫–Ω—É—Ç—å –Ω–∞ —Å–∞–º —ç–ª–µ–º–µ–Ω—Ç –º–µ–Ω—é
      if (isOpen) {
        // –ö–ª–∏–∫–∞–µ–º –Ω–∞ —Å–∞–º –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä –º–µ–Ω—é (–Ω–µ –Ω–∞ –µ–≥–æ —Å–æ–¥–µ—Ä–∂–∏–º–æ–µ)
        const menuContainer = mobileMenu.locator('.container').first();
        const boundingBox = await menuContainer.boundingBox();
        if (boundingBox) {
          // –ö–ª–∏–∫–∞–µ–º –ª–µ–≤–µ–µ –∫–æ–Ω—Ç–µ–π–Ω–µ—Ä–∞ (–Ω–∞ —Ñ–æ–Ω)
          await page.mouse.click(boundingBox.x - 10, boundingBox.y);
        }
      }
    });

    test('–ó–∞–∫—Ä—ã–≤–∞–µ—Ç—Å—è –ø—Ä–∏ –∫–ª–∏–∫–µ –Ω–∞ —Å—Å—ã–ª–∫—É', async ({ page }) => {
      await page.setViewportSize({ width: 375, height: 667 });

      const menuToggle = page.locator('#menu-toggle');
      const mobileMenu = page.locator('#mobile-menu');

      // –û—Ç–∫—Ä—ã–≤–∞–µ–º –º–µ–Ω—é
      await menuToggle.click();
      await expect(mobileMenu).toHaveClass(/mobile-menu--open/);

      // –ö–ª–∏–∫–∞–µ–º –Ω–∞ —Å—Å—ã–ª–∫—É –≤ –º–µ–Ω—é
      const firstLink = page.locator('.mobile-menu__nav-link').first();
      await firstLink.click();

      // –ú–µ–Ω—é –¥–æ–ª–∂–Ω–æ –∑–∞–∫—Ä—ã—Ç—å—Å—è —á–µ—Ä–µ–∑ –Ω–µ–±–æ–ª—å—à–æ–π —Ç–∞–π–º–∞—É—Ç
      await page.waitForTimeout(200);
      await expect(mobileMenu).not.toHaveClass(/mobile-menu--open/);
    });

    test('Keyboard navigation (Tab trap) —Ä–∞–±–æ—Ç–∞–µ—Ç', async ({ page }) => {
      await page.setViewportSize({ width: 375, height: 667 });

      const menuToggle = page.locator('#menu-toggle');
      const mobileMenu = page.locator('#mobile-menu');

      // –û—Ç–∫—Ä—ã–≤–∞–µ–º –º–µ–Ω—é
      await menuToggle.click();
      await expect(mobileMenu).toHaveClass(/mobile-menu--open/);

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ñ–æ–∫—É—Å –ø–µ—Ä–µ–º–µ—Å—Ç–∏–ª—Å—è –Ω–∞ –∫–Ω–æ–ø–∫—É –∑–∞–∫—Ä—ã—Ç–∏—è
      const menuClose = page.locator('#menu-close');
      await expect(menuClose).toBeFocused();

      // Tab navigation
      await page.keyboard.press('Tab');
      // –§–æ–∫—É—Å –¥–æ–ª–∂–µ–Ω –ø–µ—Ä–µ–º–µ—Å—Ç–∏—Ç—å—Å—è –Ω–∞ —Å–ª–µ–¥—É—é—â–∏–π —ç–ª–µ–º–µ–Ω—Ç –≤ –º–µ–Ω—é

      // Shift+Tab –¥–æ–ª–∂–µ–Ω –≤–µ—Ä–Ω—É—Ç—å —Ñ–æ–∫—É—Å –Ω–∞–∑–∞–¥
      await page.keyboard.press('Shift+Tab');
    });
  });

  test('–•–ª–µ–±–Ω—ã–µ –∫—Ä–æ—à–∫–∏ –æ—Ç–æ–±—Ä–∞–∂–∞—é—Ç—Å—è –Ω–∞ –≤–Ω—É—Ç—Ä–µ–Ω–Ω–∏—Ö —Å—Ç—Ä–∞–Ω–∏—Ü–∞—Ö', async ({ page }) => {
    await page.goto('/katalog.html');

    const breadcrumbs = page.locator('.breadcrumbs');
    if (await breadcrumbs.count() > 0) {
      await expect(breadcrumbs.first()).toBeVisible();

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ —Å—Å—ã–ª–æ–∫ –≤ —Ö–ª–µ–±–Ω—ã—Ö –∫—Ä–æ—à–∫–∞—Ö
      const breadcrumbLinks = breadcrumbs.locator('a');
      if (await breadcrumbLinks.count() > 0) {
        await expect(breadcrumbLinks.first()).toBeVisible();
      }
    }
  });

  test('–ü–µ—Ä–µ—Ö–æ–¥—ã –º–µ–∂–¥—É —Å—Ç—Ä–∞–Ω–∏—Ü–∞–º–∏ —Ä–∞–±–æ—Ç–∞—é—Ç –±–µ–∑ –æ—à–∏–±–æ–∫', async ({ page }) => {
    const pages = [
      '/index.html',
      '/katalog.html',
      '/kontakty.html',
      '/uslugi/ustanovka.html'
    ];

    for (const pagePath of pages) {
      await page.goto(pagePath);
      await expect(page).toHaveURL(new RegExp(pagePath.replace('/', '\\/').replace('.html', '\\.html')));

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º –æ—Ç—Å—É—Ç—Å—Ç–≤–∏–µ JavaScript –æ—à–∏–±–æ–∫
      const errors = [];
      page.on('pageerror', error => errors.push(error.message));
      await page.waitForTimeout(500);
      expect(errors.length).toBe(0);
    }
  });
});

assets/css/components/forms.css.content:
/* ============================================
   Forms Component Styles
   ============================================ */

.form {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-lg);
}

.form__group {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-xs);
}

.form__label {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--text-primary);
}

.form__label--required::after {
  content: ' *';
  color: var(--color-error);
}

.form__input,
.form__textarea,
.form__select {
  width: 100%;
  padding: var(--spacing-md);
  font-size: 1rem;
  font-family: inherit;
  color: var(--text-primary);
  background-color: var(--bg-primary);
  border: 2px solid var(--border-color);
  border-radius: var(--radius-md);
  transition: border-color var(--transition-fast), box-shadow var(--transition-fast), background-color var(--transition-fast);
  min-height: 44px;
}

.form__input:hover:not(:disabled):not(:focus),
.form__textarea:hover:not(:disabled):not(:focus),
.form__select:hover:not(:disabled):not(:focus) {
  border-color: var(--border-color-dark);
  background-color: var(--bg-secondary);
}

.form__input:focus,
.form__textarea:focus,
.form__select:focus {
  outline: none;
  border-color: var(--color-primary);
  border-image: var(--gradient-primary) 1;
  box-shadow: var(--focus-ring), var(--shadow-primary);
  background-color: var(--bg-primary);
}

.form__input:focus-visible,
.form__textarea:focus-visible,
.form__select:focus-visible {
  outline: var(--focus-outline-width) solid var(--color-primary);
  outline-offset: var(--focus-outline-offset);
  box-shadow: var(--focus-ring), var(--shadow-primary);
}

.form__input:focus:not(:focus-visible),
.form__textarea:focus:not(:focus-visible),
.form__select:focus:not(:focus-visible) {
  outline: none;
}

.form__input::placeholder,
.form__textarea::placeholder {
  color: var(--text-muted);
}

.form__textarea {
  min-height: 120px;
  resize: vertical;
}

.form__select {
  background-image: url("data:image/svg+xml,%3Csvg width='12' height='8' viewBox='0 0 12 8' fill='none' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M1 1L6 6L11 1' stroke='%234B5563' stroke-width='2' stroke-linecap='round' stroke-linejoin='round'/%3E%3C/svg%3E");
  background-repeat: no-repeat;
  background-position: right var(--spacing-md) center;
  padding-right: var(--spacing-2xl);
  appearance: none;
}

.form__error {
  font-size: 0.875rem;
  color: var(--color-error);
  display: none;
}

.form__error-message {
  display: block;
  font-size: 0.875rem;
  color: var(--color-error);
  margin-top: var(--spacing-xs);
  display: flex;
  align-items: flex-start;
  gap: var(--spacing-xs);
  line-height: 1.5;
  padding: var(--spacing-sm);
  background-color: rgba(var(--color-error-rgb), 0.1);
  border-radius: var(--radius-sm);
  border-left: 3px solid var(--color-error);
}

.form__error-message::before {
  content: '‚ö†';
  font-size: 1rem;
  flex-shrink: 0;
  margin-top: 0.125rem;
}

.form__group--error .form__error {
  display: block;
}

.form__group--error .form__input,
.form__group--error .form__textarea,
.form__group--error .form__select {
  border-color: var(--color-error);
  border-width: 2px;
  box-shadow: 0 0 0 3px rgba(var(--color-error-rgb), 0.15);
  background-color: rgba(var(--color-error-rgb), 0.05);
}

.form__group--error .form__input:focus,
.form__group--error .form__textarea:focus,
.form__group--error .form__select:focus {
  border-color: var(--color-error);
  box-shadow: 0 0 0 3px rgba(var(--color-error-rgb), 0.25);
}

.form__input--error {
  border-color: var(--color-error) !important;
  border-width: 2px !important;
  box-shadow: 0 0 0 3px rgba(var(--color-error-rgb), 0.15) !important;
}

.form__input--success {
  border-color: var(--color-success) !important;
  border-width: 2px !important;
  box-shadow: 0 0 0 3px rgba(var(--color-success-rgb), 0.15) !important;
  background-color: rgba(var(--color-success-rgb), 0.05);
}

.form__input:disabled,
.form__textarea:disabled,
.form__select:disabled {
  opacity: 0.6;
  cursor: not-allowed;
  background-color: var(--bg-secondary);
  color: var(--text-muted);
}

.form__input:disabled:hover,
.form__textarea:disabled:hover,
.form__select:disabled:hover {
  border-color: var(--border-color);
  background-color: var(--bg-secondary);
}

.form__success-message {
  display: flex;
  align-items: flex-start;
  gap: var(--spacing-md);
  padding: var(--spacing-lg);
  background-color: rgba(var(--color-success-rgb), 0.1);
  border: 2px solid var(--color-success);
  border-radius: var(--radius-md);
  color: var(--color-success);
  margin-bottom: var(--spacing-lg);
  border-left-width: 4px;
}

.form__success-message svg {
  flex-shrink: 0;
  margin-top: 2px;
  width: 20px;
  height: 20px;
}

.form__success-message strong {
  display: block;
  margin-bottom: var(--spacing-xs);
  color: var(--color-success);
  font-weight: 600;
}

.form__success-message p {
  margin: 0;
  color: var(--text-secondary);
  line-height: 1.5;
}

.form__help {
  font-size: 0.875rem;
  color: var(--text-muted);
}

.form__checkbox,
.form__radio {
  width: 20px;
  height: 20px;
  margin-right: var(--spacing-sm);
  cursor: pointer;
  flex-shrink: 0;
  accent-color: var(--color-primary);
  transition: accent-color var(--transition-fast);
}

.form__checkbox:focus-visible,
.form__radio:focus-visible {
  outline: var(--focus-outline-width) solid var(--color-primary);
  outline-offset: var(--focus-outline-offset);
  border-radius: var(--radius-sm);
}

.form__checkbox-label,
.form__radio-label {
  display: flex;
  align-items: flex-start;
  font-size: 0.875rem;
  color: var(--text-secondary);
  cursor: pointer;
  line-height: 1.5;
  gap: var(--spacing-xs);
  user-select: none;
}

.form__checkbox-label:hover,
.form__radio-label:hover {
  color: var(--text-primary);
}

.form__submit {
  width: 100%;
  margin-top: var(--spacing-md);
}

@media (min-width: 768px) {
  .form__submit {
    width: auto;
  }
}

.form__success {
  padding: var(--spacing-md);
  background-color: var(--color-success);
  color: white;
  border-radius: var(--radius-md);
  display: none;
}

.form--success .form__success {
  display: block;
}

/* Form Container */
.form-container {
  max-width: 600px;
  margin: 0 auto;
}

assets/js/form-validation.js.content:
/* ============================================
   Enhanced Form Validation
   ============================================ */

(function() {
  'use strict';

  // Validation patterns
  const patterns = {
    phone: /^[\+]?[(]?[0-9]{1,4}[)]?[-\s\.]?[(]?[0-9]{1,4}[)]?[-\s\.]?[0-9]{1,9}$/,
    email: /^[^\s@]+@[^\s@]+\.[^\s@]+$/,
    name: /^[–∞-—è–ê-–Ø—ë–Åa-zA-Z\s-]{2,50}$/,
    message: /^.{10,500}$/
  };

  // Validation messages
  const messages = {
    required: '–≠—Ç–æ –ø–æ–ª–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ –¥–ª—è –∑–∞–ø–æ–ª–Ω–µ–Ω–∏—è',
    phone: '–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –Ω–æ–º–µ—Ä —Ç–µ–ª–µ—Ñ–æ–Ω–∞',
    email: '–í–≤–µ–¥–∏—Ç–µ –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π email –∞–¥—Ä–µ—Å',
    name: '–ò–º—è –¥–æ–ª–∂–Ω–æ —Å–æ–¥–µ—Ä–∂–∞—Ç—å –æ—Ç 2 –¥–æ 50 —Å–∏–º–≤–æ–ª–æ–≤',
    message: '–°–æ–æ–±—â–µ–Ω–∏–µ –¥–æ–ª–∂–Ω–æ —Å–æ–¥–µ—Ä–∂–∞—Ç—å –æ—Ç 10 –¥–æ 500 —Å–∏–º–≤–æ–ª–æ–≤',
    minLength: (min) => `–ú–∏–Ω–∏–º—É–º ${min} —Å–∏–º–≤–æ–ª–æ–≤`,
    maxLength: (max) => `–ú–∞–∫—Å–∏–º—É–º ${max} —Å–∏–º–≤–æ–ª–æ–≤`
  };

  // Add validation to form inputs
  function initFormValidation() {
    const forms = document.querySelectorAll('.form');

    forms.forEach(form => {
      const inputs = form.querySelectorAll('input, textarea, select');

      inputs.forEach(input => {
        // Real-time validation
        input.addEventListener('blur', () => validateField(input));
        input.addEventListener('input', () => {
          if (input.classList.contains('form__input--error')) {
            validateField(input);
          }
        });
      });

      // Form submission validation
      form.addEventListener('submit', (e) => {
        e.preventDefault();

        // Validate all fields on submit
        let isValid = true;
        inputs.forEach(input => {
          // Force validation even if field hasn't been touched
          if (!validateField(input)) {
            isValid = false;
          }
        });

        if (isValid) {
          handleFormSubmit(form);
        } else {
          // Scroll to first error
          const firstError = form.querySelector('.form__input--error, .form__textarea--error');
          if (firstError) {
            firstError.scrollIntoView({ behavior: 'smooth', block: 'center' });
            firstError.focus();
          }
        }
      });
    });
  }

  // Validate individual field
  function validateField(field) {
    const value = field.value.trim();
    const type = field.type || field.tagName.toLowerCase();

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —è–≤–ª—è–µ—Ç—Å—è –ª–∏ –ø–æ–ª–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–º
    const hasRequiredAttr = field.hasAttribute('required');
    const ariaRequired = field.getAttribute('aria-required') === 'true';

    // –ò—â–µ–º label —á–µ—Ä–µ–∑ for –∞—Ç—Ä–∏–±—É—Ç
    let label = null;
    if (field.id) {
      label = field.closest('.form__group')?.querySelector('label[for="' + field.id + '"]');
    }
    // –ï—Å–ª–∏ –Ω–µ –Ω–∞—à–ª–∏ —á–µ—Ä–µ–∑ for, –∏—â–µ–º –ø–µ—Ä–≤—ã–π label –≤ –≥—Ä—É–ø–ø–µ
    if (!label) {
      label = field.closest('.form__group')?.querySelector('label');
    }
    const hasRequiredClass = label && label.classList.contains('form__label--required');
    const isRequired = hasRequiredAttr || hasRequiredClass || ariaRequired;

    // Remove previous error state
    clearFieldError(field);

    // Required validation - –ø—Ä–æ–≤–µ—Ä—è–µ–º –≤—Å–µ–≥–¥–∞, –¥–∞–∂–µ –µ—Å–ª–∏ –ø–æ–ª–µ –Ω–µ –±—ã–ª–æ touched
    if (isRequired && !value) {
      showFieldError(field, messages.required);
      return false;
    }

    // Type-specific validation
    if (value) {
      let isValid = true;
      let errorMessage = '';

      switch (type) {
        case 'tel':
        case 'phone':
          // Remove all formatting characters for validation
          const phoneValue = value.replace(/[\s\-\(\)\+]/g, '');
          if (!patterns.phone.test(phoneValue) || phoneValue.length < 10) {
            isValid = false;
            errorMessage = messages.phone;
          }
          break;

        case 'email':
          if (!patterns.email.test(value)) {
            isValid = false;
            errorMessage = messages.email;
          }
          break;

        case 'text':
          if (field.name === 'name' && !patterns.name.test(value)) {
            isValid = false;
            errorMessage = messages.name;
          }
          break;

        case 'textarea':
          if (field.name === 'message' && !patterns.message.test(value)) {
            isValid = false;
            errorMessage = messages.message;
          }
          break;
      }

      // Length validation
      if (isValid && field.hasAttribute('minlength')) {
        const minLength = parseInt(field.getAttribute('minlength'));
        if (value.length < minLength) {
          isValid = false;
          errorMessage = messages.minLength(minLength);
        }
      }

      if (isValid && field.hasAttribute('maxlength')) {
        const maxLength = parseInt(field.getAttribute('maxlength'));
        if (value.length > maxLength) {
          isValid = false;
          errorMessage = messages.maxLength(maxLength);
        }
      }

      if (!isValid) {
        showFieldError(field, errorMessage);
        return false;
      }
    }

    // Show success state
    showFieldSuccess(field);
    return true;
  }

  // Show field error
  function showFieldError(field, message) {
    field.classList.add('form__input--error');
    field.setAttribute('aria-invalid', 'true');

    // Remove existing error message
    const existingError = field.parentElement.querySelector('.form__error-message');
    if (existingError) {
      existingError.remove();
    }

    // Add error message
    const errorElement = document.createElement('span');
    errorElement.className = 'form__error-message';
    errorElement.textContent = message;
    errorElement.setAttribute('role', 'alert');
    errorElement.setAttribute('aria-live', 'polite');

    field.parentElement.appendChild(errorElement);

    // Update form group
    field.closest('.form__group')?.classList.add('form__group--error');
  }

  // Clear field error
  function clearFieldError(field) {
    field.classList.remove('form__input--error');
    field.removeAttribute('aria-invalid');

    const errorElement = field.parentElement.querySelector('.form__error-message');
    if (errorElement) {
      errorElement.remove();
    }

    field.closest('.form__group')?.classList.remove('form__group--error');
  }

  // Show field success
  function showFieldSuccess(field) {
    field.classList.add('form__input--success');
    field.setAttribute('aria-invalid', 'false');

    // Remove error classes
    field.classList.remove('form__input--error');
    field.closest('.form__group')?.classList.remove('form__group--error');
  }

  // Handle form submission
  function handleFormSubmit(form) {
    const submitButton = form.querySelector('button[type="submit"]');
    const originalText = submitButton.textContent;

    // Show loading state
    submitButton.disabled = true;
    submitButton.classList.add('btn--loading');
    submitButton.textContent = '–û—Ç–ø—Ä–∞–≤–∫–∞...';

    // Simulate form submission (replace with actual submission logic)
    setTimeout(() => {
      showFormSuccess(form);

      // Reset button
      submitButton.disabled = false;
      submitButton.classList.remove('btn--loading');
      submitButton.textContent = originalText;
    }, 1500);
  }

  // Show form success message
  function showFormSuccess(form) {
    form.classList.add('form--success');

    const successMessage = document.createElement('div');
    successMessage.className = 'form__success-message';
    successMessage.innerHTML = `
      <svg class="icon icon--lg icon--success" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
        <path d="M20 6L9 17l-5-5"/>
      </svg>
      <div>
        <strong>–°–ø–∞—Å–∏–±–æ!</strong>
        <p>–í–∞—à–∞ –∑–∞—è–≤–∫–∞ —É—Å–ø–µ—à–Ω–æ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∞. –ú—ã —Å–≤—è–∂–µ–º—Å—è —Å –≤–∞–º–∏ –≤ –±–ª–∏–∂–∞–π—à–µ–µ –≤—Ä–µ–º—è.</p>
      </div>
    `;
    successMessage.setAttribute('role', 'status');
    successMessage.setAttribute('aria-live', 'polite');

    form.insertBefore(successMessage, form.firstChild);

    // Reset form after delay
    setTimeout(() => {
      form.reset();
      form.classList.remove('form--success');
      successMessage.remove();
    }, 5000);
  }

  // Format phone number
  function formatPhoneNumber(input) {
    let value = input.value.replace(/\D/g, '');

    if (value.length > 0) {
      if (value.length <= 1) {
        value = '+' + value;
      } else if (value.length <= 4) {
        value = '+' + value;
      } else if (value.length <= 7) {
        value = '+' + value.slice(0, 1) + ' (' + value.slice(1, 4) + ') ' + value.slice(4);
      } else if (value.length <= 9) {
        value = '+' + value.slice(0, 1) + ' (' + value.slice(1, 4) + ') ' + value.slice(4, 7) + '-' + value.slice(7);
      } else {
        value = '+' + value.slice(0, 1) + ' (' + value.slice(1, 4) + ') ' + value.slice(4, 7) + '-' + value.slice(7, 9) + '-' + value.slice(9, 11);
      }
    }

    input.value = value;
  }

  // Initialize phone formatting
  function initPhoneFormatting() {
    const phoneInputs = document.querySelectorAll('input[type="tel"], input[name="phone"]');
    phoneInputs.forEach(input => {
      input.addEventListener('input', () => formatPhoneNumber(input));
      input.setAttribute('placeholder', '+7 (999) 123-45-67');
    });
  }

  // Initialize on DOM ready
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', () => {
      initFormValidation();
      initPhoneFormatting();
    });
  } else {
    initFormValidation();
    initPhoneFormatting();
  }
})();

