/* ============================================
   Components Loader
   –ó–∞–≥—Ä—É–∑–∫–∞ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤ header, footer, breadcrumbs
   ============================================ */

(function() {
  'use strict';

  /**
   * –ó–∞–≥—Ä—É–∂–∞–µ—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –∏ –≤—Å—Ç–∞–≤–ª—è–µ—Ç –µ–≥–æ –≤ —É–∫–∞–∑–∞–Ω–Ω—ã–π —ç–ª–µ–º–µ–Ω—Ç
   * @param {string} componentName - –ò–º—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞ (header, footer, breadcrumbs)
   * @param {HTMLElement} targetElement - –¶–µ–ª–µ–≤–æ–π —ç–ª–µ–º–µ–Ω—Ç –¥–ª—è –≤—Å—Ç–∞–≤–∫–∏
   * @param {Object} options - –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω—ã–µ –æ–ø—Ü–∏–∏ (–Ω–∞–ø—Ä–∏–º–µ—Ä, –∞–∫—Ç–∏–≤–Ω–∞—è —Å—Ç—Ä–∞–Ω–∏—Ü–∞ –¥–ª—è –Ω–∞–≤–∏–≥–∞—Ü–∏–∏)
   */
  async function loadComponent(componentName, targetElement, options = {}) {
    if (!targetElement) {
      console.warn(`Target element not found for component: ${componentName}`);
      return;
    }

    try {
      const response = await fetch(`components/${componentName}.html`);
      if (!response.ok) {
        throw new Error(`Failed to load component: ${componentName}`);
      }

      let html = await response.text();

      // –ü—Ä–∏–º–µ–Ω—è–µ–º –æ–ø—Ü–∏–∏ (–Ω–∞–ø—Ä–∏–º–µ—Ä, —É—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ–º –∞–∫—Ç–∏–≤–Ω—É—é —Å—Å—ã–ª–∫—É –≤ –Ω–∞–≤–∏–≥–∞—Ü–∏–∏)
      if (options.activePage) {
        html = setActiveNavLink(html, options.activePage);
      }

      // –í—Å—Ç–∞–≤–ª—è–µ–º HTML
      targetElement.innerHTML = html;

      // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ–º –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –ø–æ—Å–ª–µ –∑–∞–≥—Ä—É–∑–∫–∏
      initializeComponent(componentName, targetElement);

    } catch (error) {
      console.error(`Error loading component ${componentName}:`, error);
      // Fallback: –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± –æ—à–∏–±–∫–µ
      targetElement.innerHTML = `<!-- Component ${componentName} failed to load -->`;
    }
  }

  /**
   * –£—Å—Ç–∞–Ω–∞–≤–ª–∏–≤–∞–µ—Ç –∞–∫—Ç–∏–≤–Ω—É—é —Å—Å—ã–ª–∫—É –≤ –Ω–∞–≤–∏–≥–∞—Ü–∏–∏
   * @param {string} html - HTML –∫–æ–¥ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
   * @param {string} activePage - URL –∞–∫—Ç–∏–≤–Ω–æ–π —Å—Ç—Ä–∞–Ω–∏—Ü—ã
   * @returns {string} - –û–±–Ω–æ–≤–ª–µ–Ω–Ω—ã–π HTML
   */
  function setActiveNavLink(html, activePage) {
    // –°–æ–∑–¥–∞–µ–º –≤—Ä–µ–º–µ–Ω–Ω—ã–π —ç–ª–µ–º–µ–Ω—Ç –¥–ª—è –ø–∞—Ä—Å–∏–Ω–≥–∞
    const tempDiv = document.createElement('div');
    tempDiv.innerHTML = html;

    // –ù–∞—Ö–æ–¥–∏–º –≤—Å–µ —Å—Å—ã–ª–∫–∏ –Ω–∞–≤–∏–≥–∞—Ü–∏–∏
    const navLinks = tempDiv.querySelectorAll('.header__nav-link, .mobile-menu__nav-link');

    navLinks.forEach(link => {
      const href = link.getAttribute('href');
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É–µ—Ç –ª–∏ —Å—Å—ã–ª–∫–∞ —Ç–µ–∫—É—â–µ–π —Å—Ç—Ä–∞–Ω–∏—Ü–µ
      if (href === activePage || (activePage !== '/' && href === activePage)) {
        link.classList.add('header__nav-link--active');
      }
    });

    return tempDiv.innerHTML;
  }

  /**
   * –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –ø–æ—Å–ª–µ –∑–∞–≥—Ä—É–∑–∫–∏
   * @param {string} componentName - –ò–º—è –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
   * @param {HTMLElement} element - –≠–ª–µ–º–µ–Ω—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–∞
   */
  function initializeComponent(componentName, element) {
    switch(componentName) {
      case 'header':
        // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –º–æ–±–∏–ª—å–Ω–æ–≥–æ –º–µ–Ω—é (–µ—Å–ª–∏ —Å–∫—Ä–∏–ø—Ç –µ—â–µ –Ω–µ –∑–∞–≥—Ä—É–∂–µ–Ω)
        if (typeof window.initMobileMenu === 'function') {
          window.initMobileMenu();
        }
        break;
      case 'footer':
        // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è —Ñ—É—Ç–µ—Ä–∞ (–µ—Å–ª–∏ –Ω—É–∂–Ω–æ)
        break;
      case 'breadcrumbs':
        // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è breadcrumbs (–µ—Å–ª–∏ –Ω—É–∂–Ω–æ)
        break;
    }
  }

  /**
   * –ó–∞–≥—Ä—É–∂–∞–µ—Ç –∫–æ–º–ø–æ–Ω–µ–Ω—Ç –ø–æ ID –ø–ª–µ–π—Å—Ö–æ–ª–¥–µ—Ä–∞
   * @param {string} placeholderId - ID –ø–ª–µ–π—Å—Ö–æ–ª–¥–µ—Ä–∞
   * @param {string} componentPath - –ü—É—Ç—å –∫ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—É
   */
  async function loadComponentById(placeholderId, componentPath) {
    const placeholder = document.getElementById(placeholderId);
    if (placeholder) {
      try {
        const response = await fetch(componentPath);
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const html = await response.text();
        placeholder.innerHTML = html;
      } catch (error) {
        console.error(`Error loading component ${componentPath}:`, error);
      }
    }
  }

  /**
   * –ó–∞–≥—Ä—É–∂–∞–µ—Ç –≤—Å–µ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –Ω–∞ —Å—Ç—Ä–∞–Ω–∏—Ü–µ
   */
  function loadAllComponents() {
    // –û–ø—Ä–µ–¥–µ–ª—è–µ–º —Ç–µ–∫—É—â—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É –¥–ª—è –∞–∫—Ç–∏–≤–Ω–æ–π —Å—Å—ã–ª–∫–∏
    const currentPage = window.location.pathname;

    // –ó–∞–≥—Ä—É–∂–∞–µ–º header (–ø–æ–¥–¥–µ—Ä–∂–∫–∞ –æ–±–æ–∏—Ö –º–µ—Ç–æ–¥–æ–≤)
    const headerContainer = document.querySelector('[data-component="header"]') || document.getElementById('header-placeholder');
    if (headerContainer) {
      if (headerContainer.id === 'header-placeholder') {
        loadComponentById('header-placeholder', '/components/header.html');
      } else {
        loadComponent('header', headerContainer, { activePage: currentPage });
      }
    }

    // –ó–∞–≥—Ä—É–∂–∞–µ–º footer (–ø–æ–¥–¥–µ—Ä–∂–∫–∞ –æ–±–æ–∏—Ö –º–µ—Ç–æ–¥–æ–≤)
    const footerContainer = document.querySelector('[data-component="footer"]') || document.getElementById('footer-placeholder');
    if (footerContainer) {
      if (footerContainer.id === 'footer-placeholder') {
        loadComponentById('footer-placeholder', '/components/footer.html');
      } else {
        loadComponent('footer', footerContainer);
      }
    }

    // –ó–∞–≥—Ä—É–∂–∞–µ–º breadcrumbs (–ø–æ–¥–¥–µ—Ä–∂–∫–∞ –æ–±–æ–∏—Ö –º–µ—Ç–æ–¥–æ–≤)
    const breadcrumbsContainer = document.querySelector('[data-component="breadcrumbs"]') || document.getElementById('breadcrumbs-placeholder');
    if (breadcrumbsContainer) {
      if (breadcrumbsContainer.id === 'breadcrumbs-placeholder') {
        loadComponentById('breadcrumbs-placeholder', '/components/breadcrumbs.html');
      } else {
        loadComponent('breadcrumbs', breadcrumbsContainer);
      }
    }
  }

  // –ó–∞–≥—Ä—É–∂–∞–µ–º –∫–æ–º–ø–æ–Ω–µ–Ω—Ç—ã –∫–æ–≥–¥–∞ DOM –≥–æ—Ç–æ–≤
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', loadAllComponents);
  } else {
    loadAllComponents();
  }

  // –≠–∫—Å–ø–æ—Ä—Ç–∏—Ä—É–µ–º —Ñ—É–Ω–∫—Ü–∏—é –¥–ª—è —Ä—É—á–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–∏ –∫–æ–º–ø–æ–Ω–µ–Ω—Ç–æ–≤
  window.loadComponent = loadComponent;

})();

