assets/css/components/cta.css.content:
/* CTA Component - Premium Enhanced */

.cta {
  background: var(--gradient-primary);
  background-size: var(--gradient-animated-size);
  color: var(--text-inverse);
  padding: var(--spacing-3xl) 0;
  text-align: center;
  position: relative;
  overflow: hidden;
  animation: gradientShift 15s ease infinite;
}

.cta::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: var(--gradient-radial-hero);
  opacity: 0.3;
  animation: float 20s ease-in-out infinite;
  pointer-events: none;
}

.cta__container {
  position: relative;
  z-index: 2;
}

.cta__title {
  font-size: clamp(1.75rem, 4vw, 2.5rem);
  margin-bottom: var(--spacing-md);
  color: var(--text-inverse);
  text-shadow: 0 2px 8px rgba(0, 0, 0, 0.2);
}

.cta__description {
  font-size: clamp(1rem, 2vw, 1.25rem);
  margin-bottom: var(--spacing-xl);
  opacity: 0.95;
  max-width: 600px;
  margin-left: auto;
  margin-right: auto;
}

.cta__actions {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-md);
  align-items: center;
  justify-content: center;
}

@media (min-width: 768px) {
  .cta__actions {
    flex-direction: row;
  }
}

.cta__button {
  min-width: 200px;
}

.cta--inverse {
  background: var(--bg-secondary);
  color: var(--text-primary);
}

.cta--inverse .cta__title {
  color: var(--text-primary);
}

/* CTA Button Variants */
.cta__button--light {
  background: white !important;
  color: var(--color-primary) !important;
}

.cta__button--light:hover {
  background: var(--bg-secondary) !important;
  color: var(--color-primary) !important;
}

.cta__button--outline-light {
  border-color: white !important;
  color: white !important;
}

.cta__button--outline-light:hover {
  background: white !important;
  color: var(--color-primary) !important;
}

/* Hero Button Variants */
.hero__button--outline {
  color: white;
  border-color: white;
}

/* Reduced Motion Support */
@media (prefers-reduced-motion: reduce) {
  .cta {
    animation: none !important;
    background-size: 100% 100% !important;
  }

  .cta::before {
    animation: none !important;
  }
}

/* Fallback for browsers without backdrop-filter */
@supports not (backdrop-filter: blur(20px)) {
  .cta {
    background: var(--gradient-primary);
  }
}

