/* ============================================
   Performance Optimizer
   –û–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏ —Å—Ç—Ä–∞–Ω–∏—Ü—ã
   ============================================ */

(function() {
  'use strict';

  /**
   * Debounce —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏ —Å–æ–±—ã—Ç–∏–π
   */
  function debounce(func, wait) {
    let timeout;
    return function executedFunction(...args) {
      const later = () => {
        clearTimeout(timeout);
        func(...args);
      };
      clearTimeout(timeout);
      timeout = setTimeout(later, wait);
    };
  }

  /**
   * Throttle —Ñ—É–Ω–∫—Ü–∏—è –¥–ª—è –æ–≥—Ä–∞–Ω–∏—á–µ–Ω–∏—è —á–∞—Å—Ç–æ—Ç—ã –≤—ã–∑–æ–≤–æ–≤
   */
  function throttle(func, limit) {
    let inThrottle;
    return function(...args) {
      if (!inThrottle) {
        func.apply(this, args);
        inThrottle = true;
        setTimeout(() => inThrottle = false, limit);
      }
    };
  }

  /**
   * –û–ø—Ç–∏–º–∏–∑–∏—Ä—É–µ—Ç –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π scroll
   */
  function optimizeScrollHandlers() {
    const scrollHandlers = [];

    // –ù–∞—Ö–æ–¥–∏–º –≤—Å–µ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ scroll
    document.querySelectorAll('[data-scroll-handler]').forEach(element => {
      const handlerName = element.getAttribute('data-scroll-handler');
      if (window[handlerName]) {
        scrollHandlers.push({
          element,
          handler: window[handlerName]
        });
      }
    });

    // –û–±—ä–µ–¥–∏–Ω—è–µ–º –≤—Å–µ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ scroll –≤ –æ–¥–∏–Ω –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π
    const optimizedScrollHandler = throttle(() => {
      scrollHandlers.forEach(({ element, handler }) => {
        handler(element);
      });
    }, 16); // ~60fps

    window.addEventListener('scroll', optimizedScrollHandler, { passive: true });
  }

  /**
   * –û–ø—Ç–∏–º–∏–∑–∏—Ä—É–µ—Ç –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ —Å–æ–±—ã—Ç–∏–π resize
   */
  function optimizeResizeHandlers() {
    const resizeHandlers = [];

    // –ù–∞—Ö–æ–¥–∏–º –≤—Å–µ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ resize
    document.querySelectorAll('[data-resize-handler]').forEach(element => {
      const handlerName = element.getAttribute('data-resize-handler');
      if (window[handlerName]) {
        resizeHandlers.push({
          element,
          handler: window[handlerName]
        });
      }
    });

    // –û–±—ä–µ–¥–∏–Ω—è–µ–º –≤—Å–µ –æ–±—Ä–∞–±–æ—Ç—á–∏–∫–∏ resize –≤ –æ–¥–∏–Ω –æ–ø—Ç–∏–º–∏–∑–∏—Ä–æ–≤–∞–Ω–Ω—ã–π
    const optimizedResizeHandler = debounce(() => {
      resizeHandlers.forEach(({ element, handler }) => {
        handler(element);
      });
    }, 250);

    window.addEventListener('resize', optimizedResizeHandler, { passive: true });
  }

  /**
   * –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∂–∞–µ—Ç –∫—Ä–∏—Ç–∏—á–Ω—ã–µ —Ä–µ—Å—É—Ä—Å—ã
   */
  function preloadCriticalResources() {
    // –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∞ —à—Ä–∏—Ñ—Ç–æ–≤
    const fontLink = document.createElement('link');
    fontLink.rel = 'preload';
    fontLink.as = 'style';
    fontLink.href = 'https://fonts.googleapis.com/css2?family=Inter:wght@400;500;600;700&display=swap';
    document.head.appendChild(fontLink);

    // –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∞ –∫—Ä–∏—Ç–∏—á–Ω—ã—Ö CSS
    const criticalCSS = document.createElement('link');
    criticalCSS.rel = 'preload';
    criticalCSS.as = 'style';
    criticalCSS.href = 'assets/css/main.css';
    document.head.appendChild(criticalCSS);
  }

  /**
   * –û—Ç–∫–ª–∞–¥—ã–≤–∞–µ—Ç –∑–∞–≥—Ä—É–∑–∫—É –Ω–µ–∫—Ä–∏—Ç–∏—á–Ω—ã—Ö —Å–∫—Ä–∏–ø—Ç–æ–≤
   */
  function deferNonCriticalScripts() {
    // –°–∫—Ä–∏–ø—Ç—ã —Å –∞—Ç—Ä–∏–±—É—Ç–æ–º data-defer –∑–∞–≥—Ä—É–∂–∞—é—Ç—Å—è –ø–æ—Å–ª–µ –ø–æ–ª–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–∏ —Å—Ç—Ä–∞–Ω–∏—Ü—ã
    document.querySelectorAll('script[data-defer]').forEach(script => {
      const newScript = document.createElement('script');
      newScript.src = script.src;
      newScript.defer = true;
      script.parentNode.insertBefore(newScript, script);
      script.remove();
    });
  }

  /**
   * –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∏—Ä—É–µ—Ç –≤—Å–µ –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–∏
   */
  function init() {
    optimizeScrollHandlers();
    optimizeResizeHandlers();
    preloadCriticalResources();
    deferNonCriticalScripts();
  }

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', init);
  } else {
    init();
  }

  // –≠–∫—Å–ø–æ—Ä—Ç —É—Ç–∏–ª–∏—Ç
  window.PerformanceOptimizer = {
    debounce,
    throttle,
    optimizeScrollHandlers,
    optimizeResizeHandlers
  };

})();

