// @ts-check
const { test, expect } = require('@playwright/test');

test.describe('–ê–¥–∞–ø—Ç–∏–≤–Ω–æ—Å—Ç—å', () => {
  test('–û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –Ω–∞ –º–æ–±–∏–ª—å–Ω–æ–º —É—Å—Ç—Ä–æ–π—Å—Ç–≤–µ (375px)', async ({ page }) => {
    await page.setViewportSize({ width: 375, height: 667 });
    await page.goto('/');

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –æ—Å–Ω–æ–≤–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã
    await expect(page.locator('header')).toBeVisible();
    await expect(page.locator('footer')).toBeVisible();

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –º–æ–±–∏–ª—å–Ω–æ–µ –º–µ–Ω—é –¥–æ—Å—Ç—É–ø–Ω–æ
    const menuToggle = page.locator('#menu-toggle');
    await expect(menuToggle).toBeVisible();

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –¥–µ—Å–∫—Ç–æ–ø–Ω–æ–µ –º–µ–Ω—é —Å–∫—Ä—ã—Ç–æ –∏–ª–∏ –Ω–µ –≤–∏–¥–Ω–æ
    const desktopNav = page.locator('.header__nav');
    const desktopNavVisibility = await desktopNav.evaluate(el => {
      const style = window.getComputedStyle(el);
      return style.display !== 'none' && style.visibility !== 'hidden';
    });

    // –ù–∞ –º–æ–±–∏–ª—å–Ω—ã—Ö –¥–µ—Å–∫—Ç–æ–ø–Ω–æ–µ –º–µ–Ω—é –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å —Å–∫—Ä—ã—Ç–æ –∏–ª–∏ –Ω–µ–≤–∏–¥–∏–º–æ
    // (–º–æ–∂–µ—Ç –±—ã—Ç—å —Å–∫—Ä—ã—Ç–æ —á–µ—Ä–µ–∑ CSS –∏–ª–∏ –∏–º–µ—Ç—å display: none)
  });

  test('–û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –Ω–∞ –ø–ª–∞–Ω—à–µ—Ç–µ (768px)', async ({ page }) => {
    await page.setViewportSize({ width: 768, height: 1024 });
    await page.goto('/');

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –æ—Å–Ω–æ–≤–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã
    await expect(page.locator('header')).toBeVisible();
    await expect(page.locator('footer')).toBeVisible();

    // –ù–∞ –ø–ª–∞–Ω—à–µ—Ç–µ –º–æ–∂–µ—Ç –±—ã—Ç—å –≤–∏–¥–Ω–æ –ª–∏–±–æ –º–æ–±–∏–ª—å–Ω–æ–µ, –ª–∏–±–æ –¥–µ—Å–∫—Ç–æ–ø–Ω–æ–µ –º–µ–Ω—é
    // –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç breakpoint
  });

  test('–û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –Ω–∞ –¥–µ—Å–∫—Ç–æ–ø–µ (1920px)', async ({ page }) => {
    await page.setViewportSize({ width: 1920, height: 1080 });
    await page.goto('/');
    await page.waitForTimeout(1000); // –ñ–¥–µ–º –ø—Ä–∏–º–µ–Ω–µ–Ω–∏—è CSS

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –æ—Å–Ω–æ–≤–Ω—ã–µ —ç–ª–µ–º–µ–Ω—Ç—ã
    await expect(page.locator('header')).toBeVisible();
    await expect(page.locator('footer')).toBeVisible();

    // –ù–∞ –¥–µ—Å–∫—Ç–æ–ø–µ –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å –≤–∏–¥–Ω–æ –¥–µ—Å–∫—Ç–æ–ø–Ω–æ–µ –º–µ–Ω—é
    const desktopNav = page.locator('.header__nav');
    await expect(desktopNav).toBeVisible();

    // –ú–æ–±–∏–ª—å–Ω–æ–µ –º–µ–Ω—é –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å —Å–∫—Ä—ã—Ç–æ –∏–ª–∏ –Ω–µ –≤–∏–¥–Ω–æ
    const menuToggle = page.locator('#menu-toggle');
    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ø—Ä—è–º—É—é —á–µ—Ä–µ–∑ isVisible (—É—á–∏—Ç—ã–≤–∞–µ—Ç display: none)
    const isVisible = await menuToggle.isVisible();

    // –ù–∞ –¥–µ—Å–∫—Ç–æ–ø–µ –º–æ–±–∏–ª—å–Ω–æ–µ –º–µ–Ω—é –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å —Å–∫—Ä—ã—Ç–æ
    expect(isVisible).toBeFalsy();
  });

  test('–ú–æ–±–∏–ª—å–Ω–æ–µ –º–µ–Ω—é –≤–∏–¥–Ω–æ —Ç–æ–ª—å–∫–æ –Ω–∞ –º–æ–±–∏–ª—å–Ω—ã—Ö —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞—Ö', async ({ page }) => {
    // –¢–µ—Å—Ç –Ω–∞ –º–æ–±–∏–ª—å–Ω–æ–º
    await page.setViewportSize({ width: 375, height: 667 });
    await page.goto('/');
    await page.waitForTimeout(1000);

    const menuToggle = page.locator('#menu-toggle');
    const isVisibleMobile = await menuToggle.isVisible();
    expect(isVisibleMobile).toBeTruthy();

    // –¢–µ—Å—Ç –Ω–∞ –¥–µ—Å–∫—Ç–æ–ø–µ
    await page.setViewportSize({ width: 1920, height: 1080 });
    await page.reload();
    await page.waitForTimeout(1000);

    const isVisibleDesktop = await menuToggle.isVisible();

    // –ù–∞ –¥–µ—Å–∫—Ç–æ–ø–µ –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å —Å–∫—Ä—ã—Ç–æ —á–µ—Ä–µ–∑ display: none
    expect(isVisibleDesktop).toBeFalsy();

    // –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞ —á–µ—Ä–µ–∑ computed style
    const computedStyle = await menuToggle.evaluate(el => {
      const style = window.getComputedStyle(el);
      return style.display;
    });
    expect(computedStyle).toBe('none');
  });

  test('–î–µ—Å–∫—Ç–æ–ø–Ω–æ–µ –º–µ–Ω—é —Å–∫—Ä—ã—Ç–æ –Ω–∞ –º–æ–±–∏–ª—å–Ω—ã—Ö —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞—Ö', async ({ page }) => {
    // –¢–µ—Å—Ç –Ω–∞ –º–æ–±–∏–ª—å–Ω–æ–º
    await page.setViewportSize({ width: 375, height: 667 });
    await page.goto('/');
    await page.waitForTimeout(1000);

    const desktopNav = page.locator('.header__nav');

    // –ù–∞ –º–æ–±–∏–ª—å–Ω—ã—Ö –¥–µ—Å–∫—Ç–æ–ø–Ω–æ–µ –º–µ–Ω—é –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å —Å–∫—Ä—ã—Ç–æ —á–µ—Ä–µ–∑ display: none
    const isVisible = await desktopNav.isVisible();
    expect(isVisible).toBeFalsy();

    // –î–æ–ø–æ–ª–Ω–∏—Ç–µ–ª—å–Ω–∞—è –ø—Ä–æ–≤–µ—Ä–∫–∞ —á–µ—Ä–µ–∑ computed style
    const navDisplay = await desktopNav.evaluate(el => {
      return window.getComputedStyle(el).display;
    });
    expect(navDisplay).toBe('none');

    // –¢–µ—Å—Ç –Ω–∞ –¥–µ—Å–∫—Ç–æ–ø–µ
    await page.setViewportSize({ width: 1920, height: 1080 });
    await page.reload();
    await page.waitForTimeout(1000);

    await expect(desktopNav).toBeVisible();
  });

  test('–ö–æ–Ω—Ç–µ–Ω—Ç –∞–¥–∞–ø—Ç–∏—Ä—É–µ—Ç—Å—è –ø–æ–¥ —Ä–∞–∑–º–µ—Ä —ç–∫—Ä–∞–Ω–∞', async ({ page }) => {
    const viewports = [
      { width: 375, height: 667, name: 'mobile' },
      { width: 768, height: 1024, name: 'tablet' },
      { width: 1920, height: 1080, name: 'desktop' }
    ];

    for (const viewport of viewports) {
      await page.setViewportSize({ width: viewport.width, height: viewport.height });
      await page.goto('/');
      await page.waitForTimeout(500);

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –æ—Å–Ω–æ–≤–Ω—ã–µ —Å–µ–∫—Ü–∏–∏ –≤–∏–¥–Ω—ã
      await expect(page.locator('.hero').or(page.locator('main')).first()).toBeVisible();

      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –Ω–µ—Ç –≥–æ—Ä–∏–∑–æ–Ω—Ç–∞–ª—å–Ω–æ–≥–æ —Å–∫—Ä–æ–ª–ª–∞ (overflow)
      const hasHorizontalScroll = await page.evaluate(() => {
        return document.documentElement.scrollWidth > document.documentElement.clientWidth;
      });

      // –ù–∞ –º–∞–ª–µ–Ω—å–∫–∏—Ö —ç–∫—Ä–∞–Ω–∞—Ö –º–æ–∂–µ—Ç –±—ã—Ç—å –Ω–µ–±–æ–ª—å—à–æ–π overflow, –Ω–æ –Ω–µ –∫—Ä–∏—Ç–∏—á–Ω—ã–π
      // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ overflow –Ω–µ —Å–ª–∏—à–∫–æ–º –±–æ–ª—å—à–æ–π
      if (hasHorizontalScroll) {
        const scrollDifference = await page.evaluate(() => {
          return document.documentElement.scrollWidth - document.documentElement.clientWidth;
        });
        // –î–æ–ø—É—Å–∫–∞–µ–º –Ω–µ–±–æ–ª—å—à–æ–π overflow (–º–µ–Ω–µ–µ 50px) –∏–∑-–∑–∞ padding/margins
        expect(scrollDifference).toBeLessThan(50);
      }
    }
  });

  test('–ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –∞–¥–∞–ø—Ç–∏–≤–Ω—ã', async ({ page }) => {
    await page.setViewportSize({ width: 375, height: 667 });
    await page.goto('/');

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –∏–∑–æ–±—Ä–∞–∂–µ–Ω–∏—è –∑–∞–≥—Ä—É–∂–∞—é—Ç—Å—è –∏ –∏–º–µ—é—Ç —Ä–∞–∑—É–º–Ω—ã–µ —Ä–∞–∑–º–µ—Ä—ã
    const images = page.locator('img');
    const imageCount = await images.count();

    if (imageCount > 0) {
      for (let i = 0; i < Math.min(imageCount, 5); i++) {
        const img = images.nth(i);
        const isVisible = await img.isVisible();

        if (isVisible) {
          const boundingBox = await img.boundingBox();
          if (boundingBox) {
            // –ò–∑–æ–±—Ä–∞–∂–µ–Ω–∏–µ –Ω–µ –¥–æ–ª–∂–Ω–æ –±—ã—Ç—å —à–∏—Ä–µ —ç–∫—Ä–∞–Ω–∞
            expect(boundingBox.width).toBeLessThanOrEqual(400);
          }
        }
      }
    }
  });
});

