   * @returns {Object} Service schema
   */
  function generateService(options) {
    return {
      '@context': 'https://schema.org',
      '@type': 'Service',
      serviceType: options.serviceType || '–£—Å–ª—É–≥–∞',
      name: options.name || '–£—Å–ª—É–≥–∞',
      description: options.description || '',
      provider: {
        '@type': 'Organization',
        name: ORGANIZATION_DATA.name,
        url: ORGANIZATION_DATA.url,
        logo: ORGANIZATION_DATA.logo
      },
      areaServed: options.areaServed || [
        { '@type': 'City', name: '–ú–æ—Å–∫–≤–∞' },
        { '@type': 'State', name: '–ú–æ—Å–∫–æ–≤—Å–∫–∞—è –æ–±–ª–∞—Å—Ç—å' }
      ],
      offers: options.offers || {
        '@type': 'Offer',
        priceCurrency: 'RUB',
        price: '0',
        availability: 'https://schema.org/InStock'
      }
    };
  }

  /**
   * –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç Product Schema.org
   * @param {Object} options - –û–ø—Ü–∏–∏ —Ç–æ–≤–∞—Ä–∞
   * @returns {Object} Product schema
   */
  function generateProduct(options) {
    return {
      '@context': 'https://schema.org',
      '@type': 'Product',
      name: options.name || '–¢–æ–≤–∞—Ä',
      description: options.description || '',
      image: options.image || 'https://msk.splitis.ru/assets/images/products/product.jpg',
      sku: options.sku || '',
      brand: options.brand ? {
        '@type': 'Brand',
        name: options.brand
      } : undefined,
      offers: options.offers || {
        '@type': 'Offer',
        url: options.url || ORGANIZATION_DATA.url,
        priceCurrency: 'RUB',
        price: '0',
        priceValidUntil: '2025-12-31',
        availability: 'https://schema.org/InStock',
        itemCondition: 'https://schema.org/NewCondition',
        seller: {
          '@type': 'Organization',
          name: ORGANIZATION_DATA.name
        }
      },
      aggregateRating: options.rating || {
        '@type': 'AggregateRating',
        ratingValue: '4.9',
        reviewCount: '0',
        bestRating: '5',
        worstRating: '1'
      },
      additionalProperty: options.properties || []
    };
  }

  /**
   * –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç Article Schema.org
   * @param {Object} options - –û–ø—Ü–∏–∏ —Å—Ç–∞—Ç—å–∏
   * @returns {Object} Article schema
   */
  function generateArticle(options) {
    return {
      '@context': 'https://schema.org',
      '@type': 'Article',
      headline: options.headline || '–°—Ç–∞—Ç—å—è',
      description: options.description || '',
      image: options.image || 'https://msk.splitis.ru/assets/images/og-image.jpg',
      datePublished: options.datePublished || new Date().toISOString(),
      dateModified: options.dateModified || new Date().toISOString(),
      author: {
        '@type': 'Organization',
        name: ORGANIZATION_DATA.name,
        url: ORGANIZATION_DATA.url
      },
      publisher: {
        '@type': 'Organization',
        name: ORGANIZATION_DATA.name,
        url: ORGANIZATION_DATA.url,
        logo: {
          '@type': 'ImageObject',
          url: ORGANIZATION_DATA.logo
        }
      },
      articleSection: options.section || '–ë–ª–æ–≥'
    };
  }

  /**
   * –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç WebSite Schema.org
   * @param {Object} options - –û–ø—Ü–∏–∏
   * @returns {Object} WebSite schema
   */
  function generateWebSite(options = {}) {
    return {
      '@context': 'https://schema.org',
      '@type': 'WebSite',
      name: options.name || 'msk.splitis.ru',
      url: ORGANIZATION_DATA.url,
      potentialAction: {
        '@type': 'SearchAction',
        target: {
          '@type': 'EntryPoint',
          urlTemplate: `${ORGANIZATION_DATA.url}/search?q={search_term_string}`
        },
        'query-input': 'required name=search_term_string'
      }
    };
  }

  /**
   * –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç BreadcrumbList Schema.org
   * @param {Array} items - –ú–∞—Å—Å–∏–≤ —ç–ª–µ–º–µ–Ω—Ç–æ–≤ breadcrumbs
   * @returns {Object} BreadcrumbList schema
   */
  function generateBreadcrumbList(items) {
    return {
      '@context': 'https://schema.org',
      '@type': 'BreadcrumbList',
      itemListElement: items.map((item, index) => ({
        '@type': 'ListItem',
        position: index + 1,
        name: item.name,
        item: item.url
      }))
    };
  }

  /**
   * –ì–µ–Ω–µ—Ä–∏—Ä—É–µ—Ç FAQPage Schema.org
   * @param {Array} faqs - –ú–∞—Å—Å–∏–≤ –≤–æ–ø—Ä–æ—Å–æ–≤-–æ—Ç–≤–µ—Ç–æ–≤
   * @returns {Object} FAQPage schema
   */
  function generateFAQPage(faqs) {
    return {
      '@context': 'https://schema.org',
      '@type': 'FAQPage',
      mainEntity: faqs.map(faq => ({
        '@type': 'Question',
        name: faq.question,
        acceptedAnswer: {
          '@type': 'Answer',
          text: faq.answer
        }
      }))
    };
  }

  /**
   * –í—Å—Ç–∞–≤–ª—è–µ—Ç Schema.org —Ä–∞–∑–º–µ—Ç–∫—É –≤ –¥–æ–∫—É–º–µ–Ω—Ç
   * @param {Object} schema - Schema –æ–±—ä–µ–∫—Ç
   * @param {string} id - ID –¥–ª—è —Å–∫—Ä–∏–ø—Ç–∞ (–æ–ø—Ü–∏–æ–Ω–∞–ª—å–Ω–æ)
   */
  function insertSchema(schema, id = null) {
    const script = document.createElement('script');
    script.type = 'application/ld+json';
    if (id) {
      script.id = id;
    }
    script.textContent = JSON.stringify(schema, null, 2);
    document.head.appendChild(script);
  }

  // –≠–∫—Å–ø–æ—Ä—Ç —Ñ—É–Ω–∫—Ü–∏–π
  window.SchemaGenerator = {
    generateOrganization,
    generateLocalBusiness,
    generateService,
    generateProduct,
    generateArticle,
    generateWebSite,
    generateBreadcrumbList,
    generateFAQPage,
    insertSchema,
    ORGANIZATION_DATA
  };

})();

