/* ============================================
   Conversion Optimizer
   –£–ª—É—á—à–µ–Ω–∏–µ –∫–æ–Ω–≤–µ—Ä—Å–∏–∏: –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ, –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏—è CTA
   ============================================ */

(function() {
  'use strict';

  /**
   * –û—Ç—Å–ª–µ–∂–∏–≤–∞–µ—Ç –∫–ª–∏–∫–∏ –ø–æ CTA –∫–Ω–æ–ø–∫–∞–º
   */
  function trackCTAClicks() {
    const ctaButtons = document.querySelectorAll('.btn--primary, .cta__button, .floating-cta__button, [data-cta="true"]');

    ctaButtons.forEach(button => {
      button.addEventListener('click', (e) => {
        const ctaText = button.textContent.trim();
        const ctaType = button.getAttribute('data-cta-type') || 'button';
        const ctaLocation = button.closest('section')?.className || 'unknown';

        // –û—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ —Å–æ–±—ã—Ç–∏—è (–º–æ–∂–Ω–æ –∏–Ω—Ç–µ–≥—Ä–∏—Ä–æ–≤–∞—Ç—å —Å –∞–Ω–∞–ª–∏—Ç–∏–∫–æ–π)
        if (typeof gtag !== 'undefined') {
          gtag('event', 'cta_click', {
            'cta_text': ctaText,
            'cta_type': ctaType,
            'cta_location': ctaLocation
          });
        }

        // –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –≤ localStorage –¥–ª—è –∞–Ω–∞–ª–∏–∑–∞
        const ctaData = {
          text: ctaText,
          type: ctaType,
          location: ctaLocation,
          timestamp: new Date().toISOString()
        };

        const existingData = JSON.parse(localStorage.getItem('cta_clicks') || '[]');
        existingData.push(ctaData);
        localStorage.setItem('cta_clicks', JSON.stringify(existingData.slice(-50))); // –•—Ä–∞–Ω–∏–º –ø–æ—Å–ª–µ–¥–Ω–∏–µ 50 –∫–ª–∏–∫–æ–≤
      });
    });
  }

  /**
   * –û–ø—Ç–∏–º–∏–∑–∏—Ä—É–µ—Ç –≤–∏–¥–∏–º–æ—Å—Ç—å CTA –∫–Ω–æ–ø–æ–∫ –ø—Ä–∏ —Å–∫—Ä–æ–ª–ª–µ
   */
  function optimizeCTAVisibility() {
    const floatingCTA = document.querySelector('.floating-cta');
    if (!floatingCTA) return;

    let lastScrollTop = 0;
    const scrollThreshold = 300;

    window.addEventListener('scroll', () => {
      const scrollTop = window.pageYOffset || document.documentElement.scrollTop;

      // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º floating CTA –ø–æ—Å–ª–µ –ø—Ä–æ–∫—Ä—É—Ç–∫–∏ –≤–Ω–∏–∑
      if (scrollTop > scrollThreshold && scrollTop > lastScrollTop) {
        floatingCTA.classList.add('floating-cta--visible');
      } else if (scrollTop < scrollThreshold || scrollTop < lastScrollTop) {
        floatingCTA.classList.remove('floating-cta--visible');
      }

      lastScrollTop = scrollTop;
    }, { passive: true });
  }

  /**
   * –£–ª—É—á—à–∞–µ—Ç —Ñ–æ—Ä–º—ã –¥–ª—è —É–≤–µ–ª–∏—á–µ–Ω–∏—è –∫–æ–Ω–≤–µ—Ä—Å–∏–∏
   */
  function optimizeForms() {
    const forms = document.querySelectorAll('.form');

    forms.forEach(form => {
      // –ê–≤—Ç–æ–∑–∞–ø–æ–ª–Ω–µ–Ω–∏–µ –∏–∑ localStorage
      const formInputs = form.querySelectorAll('input[type="text"], input[type="tel"], input[type="email"]');

      formInputs.forEach(input => {
        const fieldName = input.name || input.id;
        const savedValue = localStorage.getItem(`form_${fieldName}`);

        if (savedValue && !input.value) {
          input.value = savedValue;
        }

        // –°–æ—Ö—Ä–∞–Ω–µ–Ω–∏–µ –ø—Ä–∏ –≤–≤–æ–¥–µ
        input.addEventListener('input', debounce(() => {
          if (input.value) {
            localStorage.setItem(`form_${fieldName}`, input.value);
          }
        }, 500));
      });

      // –£–ª—É—á—à–µ–Ω–Ω–∞—è –æ—Ç–ø—Ä–∞–≤–∫–∞ —Ñ–æ—Ä–º—ã
      form.addEventListener('submit', (e) => {
        // –ù–µ –ø—Ä–µ–¥–æ—Ç–≤—Ä–∞—â–∞–µ–º –æ—Ç–ø—Ä–∞–≤–∫—É, –Ω–æ –¥–æ–±–∞–≤–ª—è–µ–º –æ—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ
        const formData = new FormData(form);
        const formType = form.getAttribute('data-form-type') || 'contact';

        // –û—Ç—Å–ª–µ–∂–∏–≤–∞–Ω–∏–µ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ä–º—ã
        if (typeof gtag !== 'undefined') {
          gtag('event', 'form_submit', {
            'form_type': formType,
            'form_location': window.location.pathname
          });
        }
      });
    });
  }

  /**
   * –û–ø—Ç–∏–º–∏–∑–∏—Ä—É–µ—Ç –≤—Ä–µ–º—è –∑–∞–≥—Ä—É–∑–∫–∏ —Å—Ç—Ä–∞–Ω–∏—Ü—ã –¥–ª—è —É–ª—É—á—à–µ–Ω–∏—è –∫–æ–Ω–≤–µ—Ä—Å–∏–∏
   */
  function optimizePageLoad() {
    // –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –∫–æ–Ω—Ç–µ–Ω—Ç –∫–∞–∫ –º–æ–∂–Ω–æ –±—ã—Å—Ç—Ä–µ–µ
    document.body.classList.add('loaded');

    // –ü—Ä–µ–¥–∑–∞–≥—Ä—É–∑–∫–∞ —Å–ª–µ–¥—É—é—â–∏—Ö –≤–µ—Ä–æ—è—Ç–Ω—ã—Ö —Å—Ç—Ä–∞–Ω–∏—Ü
    const mainLinks = document.querySelectorAll('.header__nav-link, .footer__nav-link');
    mainLinks.forEach(link => {
      link.addEventListener('mouseenter', () => {
        const href = link.getAttribute('href');
        if (href && !href.startsWith('#')) {
          const prefetchLink = document.createElement('link');
          prefetchLink.rel = 'prefetch';
          prefetchLink.href = href;
          document.head.appendChild(prefetchLink);
        }
      }, { once: true });
    });
  }

  /**
   * Debounce helper
   */
  function debounce(func, wait) {
    let timeout;
    return function executedFunction(...args) {
      const later = () => {
        clearTimeout(timeout);
        func(...args);
      };
      clearTimeout(timeout);
      timeout = setTimeout(later, wait);
    };
  }

  /**
   * –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –≤—Å–µ—Ö –æ–ø—Ç–∏–º–∏–∑–∞—Ü–∏–π –∫–æ–Ω–≤–µ—Ä—Å–∏–∏
   */
  function init() {
    trackCTAClicks();
    optimizeCTAVisibility();
    optimizeForms();
    optimizePageLoad();
  }

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', init);
  } else {
    init();
  }

  // –≠–∫—Å–ø–æ—Ä—Ç –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –≤ –¥—Ä—É–≥–∏—Ö —Å–∫—Ä–∏–ø—Ç–∞—Ö
  window.ConversionOptimizer = {
    trackCTAClicks,
    optimizeCTAVisibility,
    optimizeForms
  };

})();

