assets/css/components/breadcrumbs.css.content:
/* Breadcrumbs Component */

.breadcrumbs {
  padding: var(--spacing-md) 0;
  background-color: var(--bg-secondary);
  border-bottom: 1px solid var(--border-color);
}

.breadcrumbs ol {
  display: flex;
  align-items: center;
  gap: var(--spacing-sm);
  flex-wrap: wrap;
  list-style: none;
  margin: 0;
  padding: 0;
  font-size: 0.875rem;
}

.breadcrumbs li {
  display: flex;
  align-items: center;
  gap: var(--spacing-sm);
}

.breadcrumbs a {
  color: var(--text-secondary);
  text-decoration: none;
  transition: color var(--transition-fast);
}

.breadcrumbs a:hover {
  color: var(--color-primary);
  text-decoration: underline;
}

.breadcrumbs li::after {
  content: '/';
  color: var(--text-muted);
  margin-left: var(--spacing-sm);
}

.breadcrumbs li:last-child::after {
  display: none;
}

.breadcrumbs li:last-child a {
  color: var(--text-primary);
  font-weight: 500;
  pointer-events: none;
}

assets/css/components/faq.css.content:
/* FAQ Component - Premium Enhanced */

.faq {
  max-width: 800px;
  margin: 0 auto;
}

.faq__item {
  border-bottom: 1px solid var(--border-color);
  padding: var(--spacing-lg) 0;
  transition: all var(--transition-fast);
  position: relative;
}

.faq__item::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: var(--gradient-primary);
  transform: scaleX(0);
  transform-origin: left;
  transition: transform var(--transition-base);
}

.faq__item--open::before {
  transform: scaleX(1);
}

.faq__question {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: var(--spacing-md);
  width: 100%;
  background: none;
  border: none;
  padding: var(--spacing-sm) 0;
  font-size: clamp(1rem, 2.5vw, 1.125rem);
  font-weight: 600;
  color: var(--text-primary);
  text-align: left;
  cursor: pointer;
  transition: color var(--transition-fast);
  min-height: 44px;
}

.faq__question:hover {
  color: var(--color-primary);
}

.faq__question:focus-visible {
  outline: var(--focus-outline-width) solid var(--color-primary);
  outline-offset: var(--focus-outline-offset);
  border-radius: var(--radius-sm);
}

.faq__icon {
  width: 24px;
  height: 24px;
  fill: var(--color-primary);
  flex-shrink: 0;
  transition: transform var(--transition-base);
  will-change: transform;
}

.faq__item--open .faq__icon {
  transform: rotate(180deg);
}

.faq__answer {
  max-height: 0;
  overflow: hidden;
  transition: max-height var(--duration-slow) var(--ease-out), padding-top var(--transition-base);
  padding-top: 0;
  color: var(--text-secondary);
  line-height: 1.6;
}

.faq__item--open .faq__answer {
  max-height: 1000px;
  padding-top: var(--spacing-md);
}

@media (max-width: 767px) {
  .faq {
    max-width: 100%;
  }

  .faq__item {
    padding: var(--spacing-md) 0;
  }
}

.faq__section {
  padding: var(--spacing-3xl) 0;
}

.faq__title {
  text-align: center;
  margin-bottom: var(--spacing-2xl);
}

assets/css/utilities/themes.css.content:
/* Theme Utilities */

.theme-anchor {
  position: absolute;
  opacity: 0;
  pointer-events: none;
}

/* Dark theme via :target */
body:has(#dark:target) {
  --bg-primary: var(--color-gray-900);
  --bg-secondary: var(--color-gray-800);
  --bg-tertiary: var(--color-gray-700);
  --text-primary: #FFFFFF;
  --text-secondary: var(--color-gray-300);
  --text-muted: var(--color-gray-400);
  --border-color: var(--color-gray-700);
  --border-color-dark: var(--color-gray-600);
}

/* Fallback for browsers without :has() support */
html:has(#dark:target) body {
  --bg-primary: var(--color-gray-900);
  --bg-secondary: var(--color-gray-800);
  --bg-tertiary: var(--color-gray-700);
  --text-primary: #FFFFFF;
  --text-secondary: var(--color-gray-300);
  --text-muted: var(--color-gray-400);
  --border-color: var(--color-gray-700);
  --border-color-dark: var(--color-gray-600);
}

assets/css/components/animations.css.content:
/* ============================================
   Animations Component
   ============================================ */

/* Fade In Animation */
@keyframes fadeIn {
  from {
    opacity: 0;
  }
  to {
    opacity: 1;
  }
}

.fade-in {
  animation: fadeIn var(--duration-base) var(--ease-out);
}

/* Slide Up Animation */
@keyframes slideUp {
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.slide-up {
  animation: slideUp var(--duration-base) var(--ease-out);
}

/* Slide Down Animation */
@keyframes slideDown {
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.slide-down {
  animation: slideDown var(--duration-base) var(--ease-out);
}

/* Scale In Animation */
@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.95);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.scale-in {
  animation: scaleIn var(--duration-base) var(--ease-spring);
}

/* Spin Animation */
@keyframes spin {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

.spin {
  animation: spin 1s linear infinite;
}

/* Pulse Animation */
@keyframes pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

.pulse {
  animation: pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Bounce Animation */
@keyframes bounce {
  0%, 100% {
    transform: translateY(-25%);
    animation-timing-function: cubic-bezier(0.8, 0, 1, 1);
  }
  50% {
    transform: translateY(0);
    animation-timing-function: cubic-bezier(0, 0, 0.2, 1);
  }
}

.bounce {
  animation: bounce 1s infinite;
}

/* Shake Animation */
@keyframes shake {
  0%, 100% {
    transform: translateX(0);
  }
  10%, 30%, 50%, 70%, 90% {
    transform: translateX(-4px);
  }
  20%, 40%, 60%, 80% {
    transform: translateX(4px);
  }
}

.shake {
  animation: shake 0.5s ease-in-out;
}

/* Fade In Up (Stagger) */
@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(30px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Staggered Animation Utilities */
.animate-stagger > * {
  animation: fadeInUp var(--duration-slow) var(--ease-out) both;
}

.animate-stagger > *:nth-child(1) { animation-delay: 0.1s; }
.animate-stagger > *:nth-child(2) { animation-delay: 0.2s; }
.animate-stagger > *:nth-child(3) { animation-delay: 0.3s; }
.animate-stagger > *:nth-child(4) { animation-delay: 0.4s; }
.animate-stagger > *:nth-child(5) { animation-delay: 0.5s; }
.animate-stagger > *:nth-child(6) { animation-delay: 0.6s; }

/* Hover Lift Effect */
.hover-lift {
  transition: transform var(--duration-base) var(--ease-out), box-shadow var(--duration-base) var(--ease-out);
}

.hover-lift:hover {
  transform: translateY(-4px);
  box-shadow: var(--shadow-lg);
}

/* Hover Scale Effect */
.hover-scale {
  transition: transform var(--duration-fast) var(--ease-out);
}

.hover-scale:hover {
  transform: scale(1.05);
}

/* Smooth Reveal on Scroll */
.reveal {
  opacity: 0;
  transform: translateY(30px);
  transition: opacity var(--duration-slow) var(--ease-out), transform var(--duration-slow) var(--ease-out);
  will-change: opacity, transform;
}

.reveal.revealed {
  opacity: 1;
  transform: translateY(0);
}

/* Parallax Effect */
.parallax {
  will-change: transform;
}

/* Float Animation */
@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-20px);
  }
}

.float {
  animation: float 3s ease-in-out infinite;
}

/* Glow Pulse */
@keyframes glowPulse {
  0%, 100% {
    box-shadow: 0 0 20px rgba(0, 163, 255, 0.5);
  }
  50% {
    box-shadow: 0 0 40px rgba(0, 163, 255, 0.8);
  }
}

.glow-pulse {
  animation: glowPulse 2s ease-in-out infinite;
}

/* Slide In from Left */
@keyframes slideInLeft {
  from {
    opacity: 0;
    transform: translateX(-50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.slide-in-left {
  animation: slideInLeft var(--duration-base) var(--ease-out);
}

/* Slide In from Right */
@keyframes slideInRight {
  from {
    opacity: 0;
    transform: translateX(50px);
  }
  to {
    opacity: 1;
    transform: translateX(0);
  }
}

.slide-in-right {
  animation: slideInRight var(--duration-base) var(--ease-out);
}

/* Zoom In */
@keyframes zoomIn {
  from {
    opacity: 0;
    transform: scale(0.8);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

.zoom-in {
  animation: zoomIn var(--duration-base) var(--ease-out);
}

/* Rotate In */
@keyframes rotateIn {
  from {
    opacity: 0;
    transform: rotate(-180deg) scale(0.8);
  }
  to {
    opacity: 1;
    transform: rotate(0deg) scale(1);
  }
}

.rotate-in {
  animation: rotateIn var(--duration-base) var(--ease-out);
}

/* Reduced Motion Support - Enhanced */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }

  .animate-stagger > * {
    animation-delay: 0s !important;
  }

  /* –û—Ç–∫–ª—é—á–∞–µ–º transform –∞–Ω–∏–º–∞—Ü–∏–∏ */
  .card:hover,
  .btn:hover,
  .product-card:hover .product-card__image {
    transform: none !important;
  }

  /* –û—Ç–∫–ª—é—á–∞–µ–º will-change –¥–ª—è –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏ */
  * {
    will-change: auto !important;
  }
}

assets/css/components/print.css.content:
/* ============================================
   Print Styles (@media print)
   ============================================ */

@media print {
  /* Hide non-essential elements */
  .header,
  .footer,
  .mobile-menu,
  .theme-switcher,
  .btn,
  .hero__actions,
  .cta__actions,
  .form,
  .hero__image,
  .breadcrumbs,
  .hero__trust-badges,
  .hero__badge,
  .faq__icon,
  .pricing-table__row:has(.pricing-table__cell:empty) {
    display: none !important;
  }

  /* Optimize colors for printing */
  body {
    background: white !important;
    color: black !important;
    font-size: 12pt;
    line-height: 1.5;
  }

  /* Remove backgrounds and shadows */
  .card,
  .hero,
  .cta,
  .section--gray {
    background: white !important;
    border: 1px solid #ccc !important;
    box-shadow: none !important;
  }

  .hero {
    color: black !important;
    padding: var(--spacing-xl) 0 !important;
  }

  /* Improve readability */
  a {
    color: black !important;
    text-decoration: underline !important;
  }

  a[href^="http"]:after {
    content: " (" attr(href) ")";
    font-size: 0.875rem;
    color: #666;
  }

  a[href^="tel:"]:after {
    content: " (" attr(href) ")";
    font-size: 0.875rem;
    color: #666;
  }

  /* Page breaks */
  section {
    page-break-inside: avoid;
    page-break-before: auto;
  }

  .card,
  .review-card,
  .product-card,
  .benefit-card,
  .testimonial-card {
    page-break-inside: avoid;
    margin-bottom: var(--spacing-md);
    border: 1px solid #ddd !important;
  }

  /* Remove padding/margins */
  .container {
    padding: 0 !important;
    max-width: 100% !important;
  }

  .section {
    padding: var(--spacing-lg) 0 !important;
  }

  /* Optimize images */
  img {
    max-width: 100% !important;
    height: auto !important;
    page-break-inside: avoid;
  }

  /* Typography */
  h1, h2, h3, h4, h5, h6 {
    page-break-after: avoid;
    page-break-inside: avoid;
    color: black !important;
    margin-top: var(--spacing-md);
    margin-bottom: var(--spacing-sm);
  }

  h1 {
    font-size: 24pt !important;
  }

  h2 {
    font-size: 20pt !important;
  }

  h3 {
    font-size: 16pt !important;
  }

  p, li {
    color: black !important;
    orphans: 3;
    widows: 3;
  }

  /* Tables */
  table {
    page-break-inside: avoid;
    border-collapse: collapse;
    width: 100%;
    margin-bottom: var(--spacing-md);
  }

  th, td {
    border: 1px solid #ddd !important;
    padding: var(--spacing-sm) !important;
    color: black !important;
  }

  th {
    background: #f5f5f5 !important;
    font-weight: 700;
  }

  tr {
    page-break-inside: avoid;
  }

  /* Lists */
  ul, ol {
    page-break-inside: avoid;
  }

  /* Statistics */
  .stat-card__number {
    color: black !important;
  }

  /* Links in footer */
  .footer__legal-link {
    color: black !important;
  }

  /* FAQ */
  .faq__answer {
    display: block !important;
    max-height: none !important;
  }

  /* Pricing tables */
  .pricing-table {
    border: 1px solid #ddd !important;
  }

  .pricing-table__cell {
    color: black !important;
    border: 1px solid #ddd !important;
  }

  /* Hero content */
  .hero__title {
    color: black !important;
    font-size: 24pt !important;
  }

  .hero__subtitle {
    color: #333 !important;
  }

  /* Print URLs */
  @page {
    margin: 2cm;
    size: A4;
  }

  /* Avoid breaking */
  .hero__content,
  article {
    page-break-inside: avoid;
  }
}

assets/css/components/header.css.content:
/* ============================================
   Header Component Styles
   ============================================ */

.header {
  background-color: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
  position: sticky;
  top: 0;
  z-index: var(--z-sticky);
  box-shadow: var(--shadow-sm);
  transition: background-color var(--transition-base), box-shadow var(--transition-base), transform var(--transition-base);
}

.header--scrolled {
  box-shadow: var(--shadow-md);
  background-color: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(30px);
  -webkit-backdrop-filter: blur(30px);
  border-bottom-color: rgba(0, 0, 0, 0.1);
}

@media (prefers-color-scheme: dark) {
  .header--scrolled {
    background-color: rgba(17, 24, 39, 0.98);
  }
}

.header__container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: var(--spacing-md) var(--spacing-md);
  gap: var(--spacing-lg);
}

.header__logo {
  display: flex;
  align-items: center;
  gap: var(--spacing-sm);
  font-size: clamp(1.125rem, 2vw, 1.5rem);
  font-weight: 700;
  color: var(--color-primary);
  text-decoration: none;
}

.header__logo:hover {
  text-decoration: none;
}

.header__logo-brand {
  color: var(--color-primary);
}

.header__logo-company {
  color: var(--text-muted);
  font-size: 0.875em;
  font-weight: 500;
}

.header__nav {
  display: none !important;
}

@media (min-width: 768px) {
  .header__nav {
    display: flex !important;
    align-items: center;
    gap: var(--spacing-lg);
  }
}

.header__nav-list {
  display: flex;
  align-items: center;
  gap: var(--spacing-lg);
  list-style: none;
  margin: 0;
  padding: 0;
}

.header__nav-link {
  color: var(--text-secondary);
  font-weight: 500;
  text-decoration: none;
  padding: var(--spacing-sm) var(--spacing-md);
  border-radius: var(--radius-md);
  transition: color var(--transition-fast), background-color var(--transition-fast);
  position: relative;
}

.header__nav-link:focus-visible {
  outline: var(--focus-outline-width) solid var(--color-primary);
  outline-offset: var(--focus-outline-offset);
  background-color: var(--bg-secondary);
}

@media (hover: hover) and (pointer: fine) {
  .header__nav-link:hover {
    color: var(--color-primary);
    background-color: var(--bg-secondary);
    text-decoration: none;
  }
}

.header__nav-link--active {
  color: var(--color-primary);
  background-color: var(--bg-secondary);
}

.header__nav-link--active::after {
  content: '';
  position: absolute;
  bottom: 0;
  left: var(--spacing-md);
  right: var(--spacing-md);
  height: 2px;
  background-color: var(--color-primary);
  border-radius: var(--radius-sm);
}

.header__contacts {
  display: flex;
  align-items: center;
  gap: var(--spacing-md);
  flex-wrap: wrap;
}

.header__phone {
  display: flex;
  align-items: center;
  gap: var(--spacing-xs);
  color: var(--text-primary);
  font-weight: 600;
  text-decoration: none;
  font-size: clamp(0.875rem, 1.5vw, 1rem);
}

.header__phone:hover {
  color: var(--color-primary);
  text-decoration: none;
}

.header__phone-icon {
  width: 20px;
  height: 20px;
  fill: currentColor;
}

.header__cta {
  display: none;
}

@media (min-width: 768px) {
  .header__cta {
    display: inline-flex;
  }
}

/* Mobile Menu Button */
.header__menu-toggle {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 44px;
  height: 44px;
  background: none;
  border: 2px solid var(--border-color);
  border-radius: var(--radius-md);
  cursor: pointer;
  transition: background-color var(--transition-fast), border-color var(--transition-fast);
  position: relative;
}

@media (min-width: 768px) {
  .header__menu-toggle {
    display: none !important;
  }
}

.header__menu-toggle:focus-visible {
  outline: var(--focus-outline-width) solid var(--color-primary);
  outline-offset: var(--focus-outline-offset);
  box-shadow: var(--focus-ring);
}

@media (hover: hover) and (pointer: fine) {
  .header__menu-toggle:hover {
    background-color: var(--bg-secondary);
    border-color: var(--border-color-dark);
  }
}

.header__menu-toggle:active {
  background-color: var(--bg-tertiary);
  transform: scale(0.98);
}

.header__menu-icon {
  width: 24px;
  height: 24px;
  fill: var(--text-primary);
}

/* Mobile Menu */
.mobile-menu {
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: var(--bg-primary);
  z-index: var(--z-modal);
  padding: var(--spacing-xl);
  overflow-y: auto;
  opacity: 0;
  visibility: hidden;
  transition: opacity var(--duration-base) var(--ease-out), visibility var(--duration-base) var(--ease-out);
}

@media (min-width: 768px) {
  .mobile-menu {
    display: none !important;
    visibility: hidden !important;
    opacity: 0 !important;
  }
}

.mobile-menu--open {
  display: block;
  opacity: 1;
  visibility: visible;
}

@media (min-width: 768px) {
  .mobile-menu--open {
    display: none !important;
    visibility: hidden !important;
    opacity: 0 !important;
  }
}

.mobile-menu__header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  margin-bottom: var(--spacing-xl);
}

.mobile-menu__close {
  width: 44px;
  height: 44px;
  background: none;
  border: 1px solid var(--border-color);
  border-radius: var(--radius-md);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
}

.mobile-menu__nav-list {
  list-style: none;
  margin: 0;
  padding: 0;
  display: flex;
  flex-direction: column;
  gap: var(--spacing-md);
}

.mobile-menu__nav-link {
  display: block;
  padding: var(--spacing-md);
  color: var(--text-primary);
  font-weight: 500;
  text-decoration: none;
  border-radius: var(--radius-md);
  transition: background-color var(--transition-fast), color var(--transition-fast);
  min-height: 44px;
  display: flex;
  align-items: center;
}

.mobile-menu__nav-link:focus-visible {
  outline: var(--focus-outline-width) solid var(--color-primary);
  outline-offset: var(--focus-outline-offset);
  box-shadow: var(--focus-ring);
}

.mobile-menu__nav-link:active {
  background-color: var(--bg-tertiary);
}

@media (hover: hover) and (pointer: fine) {
  .mobile-menu__nav-link:hover {
    background-color: var(--bg-secondary);
    text-decoration: none;
  }
}

.mobile-menu__contacts {
  margin-top: var(--spacing-xl);
  padding-top: var(--spacing-xl);
  border-top: 1px solid var(--border-color);
}

.mobile-menu__phone {
  display: block;
  padding: var(--spacing-md);
  color: var(--color-primary);
  font-weight: 600;
  text-decoration: none;
  font-size: 1.125rem;
}

.mobile-menu__phone:hover {
  text-decoration: none;
}

/* Mobile Menu Social Links */
.mobile-menu__social {
  display: flex;
  align-items: center;
  gap: var(--spacing-md);
  margin-bottom: var(--spacing-lg);
  justify-content: center;
}

/* Prevent any SVG from growing */
.mobile-menu__social svg,
.mobile-menu__contacts svg {
  box-sizing: border-box;
  overflow: visible;
}

.mobile-menu__social-link {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 48px;
  height: 48px;
  background-color: var(--color-primary);
  border-radius: 50%;
  color: #FFFFFF;
  text-decoration: none;
  transition: background-color var(--transition-fast), transform var(--transition-fast);
  flex-shrink: 0;
}

.mobile-menu__social-link:hover {
  background-color: var(--color-primary-hover);
  text-decoration: none;
  transform: scale(1.05);
}

.mobile-menu__social-link:active {
  transform: scale(0.95);
}

.mobile-menu__social-icon {
  width: 24px !important;
  height: 24px !important;
  max-width: 24px !important;
  max-height: 24px !important;
  min-width: 24px !important;
  min-height: 24px !important;
  fill: currentColor;
  flex-shrink: 0;
  display: block;
  box-sizing: content-box;
  object-fit: contain;
}

.mobile-menu__social-link svg.mobile-menu__social-icon {
  width: 24px !important;
  height: 24px !important;
  max-width: 24px !important;
  max-height: 24px !important;
  min-width: 24px !important;
  min-height: 24px !important;
}

.mobile-menu__social-link svg {
  width: 24px !important;
  height: 24px !important;
  max-width: 24px !important;
  max-height: 24px !important;
  min-width: 24px !important;
  min-height: 24px !important;
}

/* Mobile Menu Contact Links */
.mobile-menu__contact-link {
  display: flex;
  align-items: center;
  gap: var(--spacing-md);
  padding: var(--spacing-md);
  color: var(--text-secondary);
  text-decoration: none;
  border-radius: var(--radius-md);
  transition: background-color var(--transition-fast), color var(--transition-fast);
  font-size: 0.9375rem;
  margin-top: var(--spacing-sm);
}

.mobile-menu__contact-link:hover {
  background-color: var(--bg-secondary);
  color: var(--color-primary);
  text-decoration: none;
}

.mobile-menu__contact-icon {
  width: 20px !important;
  height: 20px !important;
  max-width: 20px !important;
  max-height: 20px !important;
  min-width: 20px !important;
  min-height: 20px !important;
  stroke: currentColor;
  fill: none;
  flex-shrink: 0;
  display: block;
  box-sizing: content-box;
  object-fit: contain;
}

.mobile-menu__contact-link svg.mobile-menu__contact-icon {
  width: 20px !important;
  height: 20px !important;
  max-width: 20px !important;
  max-height: 20px !important;
  min-width: 20px !important;
  min-height: 20px !important;
}

.mobile-menu__contact-link svg {
  width: 20px !important;
  height: 20px !important;
  max-width: 20px !important;
  max-height: 20px !important;
  min-width: 20px !important;
  min-height: 20px !important;
}

.mobile-menu__contact-link span {
  flex: 1;
  line-height: 1.5;
}

/* Theme Switcher */
.theme-switcher {
  display: flex;
  align-items: center;
  gap: var(--spacing-xs);
  margin-left: var(--spacing-md);
}

.theme-switcher__button {
  width: 44px;
  height: 44px;
  background: var(--bg-secondary);
  border: 1px solid var(--border-color);
  border-radius: var(--radius-md);
  cursor: pointer;
  display: flex;
  align-items: center;
  justify-content: center;
  transition: background-color var(--transition-fast), border-color var(--transition-fast);
}

.theme-switcher__button:hover {
  background-color: var(--bg-tertiary);
  border-color: var(--border-color-dark);
}

.theme-switcher__icon {
  width: 20px;
  height: 20px;
  fill: var(--text-primary);
}

.theme-anchor {
  position: absolute;
  opacity: 0;
  pointer-events: none;
}

assets/css/components/floating-cta.css.content:
/* ============================================
   Floating CTA Component
   –ü–ª–∞–≤–∞—é—â–∞—è –∫–Ω–æ–ø–∫–∞ –∑–≤–æ–Ω–∫–∞ –¥–ª—è –∫–æ–Ω–≤–µ—Ä—Å–∏–π
   ============================================ */

.floating-cta {
  position: fixed;
  bottom: 24px;
  right: 24px;
  z-index: 999;
  display: flex;
  flex-direction: column;
  gap: 12px;
  align-items: flex-end;
}

.floating-cta__button {
  display: flex;
  align-items: center;
  justify-content: center;
  width: 64px;
  height: 64px;
  border-radius: 50%;
  background: var(--gradient-primary);
  color: white;
  text-decoration: none;
  box-shadow: 0 4px 12px rgba(var(--color-primary-rgb), 0.4);
  transition: all var(--transition-base);
  position: relative;
  overflow: hidden;
}

.floating-cta__button:hover {
  transform: translateY(-2px);
  box-shadow: 0 6px 16px rgba(var(--color-primary-rgb), 0.5);
  text-decoration: none;
  color: white;
}

.floating-cta__button:active {
  transform: translateY(0);
}

.floating-cta__button::before {
  content: '';
  position: absolute;
  top: 50%;
  left: 50%;
  width: 0;
  height: 0;
  border-radius: 50%;
  background: rgba(255, 255, 255, 0.3);
  transform: translate(-50%, -50%);
  transition: width 0.6s, height 0.6s;
}

.floating-cta__button:hover::before {
  width: 200px;
  height: 200px;
}

.floating-cta__icon {
  width: 28px;
  height: 28px;
  fill: currentColor;
  position: relative;
  z-index: 1;
}

.floating-cta__label {
  position: absolute;
  right: 80px;
  top: 50%;
  transform: translateY(-50%);
  background: var(--bg-primary);
  color: var(--text-primary);
  padding: 8px 16px;
  border-radius: var(--radius-md);
  font-size: 0.875rem;
  font-weight: 600;
  white-space: nowrap;
  box-shadow: var(--shadow-md);
  border: 1px solid var(--border-color);
  opacity: 0;
  visibility: hidden;
  transition: all var(--transition-base);
  pointer-events: none;
}

.floating-cta__button:hover .floating-cta__label {
  opacity: 1;
  visibility: visible;
  right: 90px;
}

.floating-cta__pulse {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  border-radius: 50%;
  background: var(--color-primary);
  animation: pulse 2s infinite;
  opacity: 0.6;
}

@keyframes pulse {
  0% {
    transform: scale(1);
    opacity: 0.6;
  }
  50% {
    transform: scale(1.2);
    opacity: 0.3;
  }
  100% {
    transform: scale(1.4);
    opacity: 0;
  }
}

/* Hide on mobile menu open */
.mobile-menu--open ~ .floating-cta {
  display: none;
}

@media (max-width: 767px) {
  .floating-cta {
    bottom: 16px;
    right: 16px;
  }

  .floating-cta__button {
    width: 56px;
    height: 56px;
  }

  .floating-cta__icon {
    width: 24px;
    height: 24px;
  }

  .floating-cta__label {
    display: none;
  }
}

assets/css/components/glass-effects.css.content:
/* ============================================
   Glassmorphism Effects Component
   Premium Visual Design System
   ============================================ */

/* Glass Card Base */
.glass {
  background: var(--gradient-glass);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: var(--shadow-multi-layer);
  position: relative;
  overflow: hidden;
}

.glass::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: var(--gradient-glass);
  opacity: 0.5;
  pointer-events: none;
  z-index: 1;
}

.glass__content {
  position: relative;
  z-index: 2;
}

/* Glass Dark Variant */
.glass--dark {
  background: var(--gradient-glass-dark);
  border-color: rgba(255, 255, 255, 0.1);
}

/* Glass Card with Enhanced Blur */
.glass--blur-lg {
  backdrop-filter: blur(30px);
  -webkit-backdrop-filter: blur(30px);
}

.glass--blur-xl {
  backdrop-filter: blur(40px);
  -webkit-backdrop-filter: blur(40px);
}

/* Glass Header */
.header--glass {
  background: rgba(255, 255, 255, 0.8);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border-bottom: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
}

.header--glass.header--scrolled {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(30px);
  -webkit-backdrop-filter: blur(30px);
  box-shadow: var(--shadow-md);
}

@media (prefers-color-scheme: dark) {
  .header--glass {
    background: rgba(17, 24, 39, 0.8);
    border-bottom-color: rgba(255, 255, 255, 0.1);
  }

  .header--glass.header--scrolled {
    background: rgba(17, 24, 39, 0.95);
  }
}

/* Glass Badge */
.glass-badge {
  background: rgba(255, 255, 255, 0.2);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.3);
  border-radius: var(--radius-full);
  padding: var(--spacing-xs) var(--spacing-sm);
  font-size: 0.875rem;
  color: var(--text-inverse);
  box-shadow: var(--shadow-sm);
}

/* Glass Button */
.btn--glass {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  color: var(--text-inverse);
}

.btn--glass:hover:not(:disabled) {
  background: rgba(255, 255, 255, 0.2);
  border-color: rgba(255, 255, 255, 0.3);
  box-shadow: var(--shadow-glow);
}

/* Glass Modal/Overlay */
.glass-overlay {
  background: rgba(0, 0, 0, 0.5);
  backdrop-filter: blur(8px);
  -webkit-backdrop-filter: blur(8px);
}

.glass-modal {
  background: var(--gradient-glass);
  backdrop-filter: blur(30px);
  -webkit-backdrop-filter: blur(30px);
  border: 1px solid rgba(255, 255, 255, 0.2);
  box-shadow: var(--shadow-multi-layer-lg);
  border-radius: var(--radius-xl);
}

/* Glass Card Hover Effects */
.glass:hover {
  background: rgba(255, 255, 255, 0.15);
  border-color: rgba(255, 255, 255, 0.3);
  transform: translateY(-2px);
  box-shadow: var(--shadow-hover);
}

@media (hover: none) {
  .glass:hover {
    transform: none;
  }
}

/* Neumorphism Base */
.neu {
  background: var(--bg-primary);
  border: none;
  box-shadow: var(--shadow-neumorphism);
  transition: all var(--transition-base);
}

.neu:hover {
  box-shadow: var(--shadow-neumorphism-lg);
  transform: translateY(-2px);
}

.neu:active {
  box-shadow: var(--shadow-inner);
  transform: translateY(0);
}

/* Neumorphism Button */
.btn--neu {
  background: var(--bg-primary);
  border: none;
  box-shadow: var(--shadow-neumorphism);
  color: var(--text-primary);
}

.btn--neu:hover:not(:disabled) {
  box-shadow: var(--shadow-neumorphism-lg);
  transform: translateY(-2px);
}

.btn--neu:active:not(:disabled) {
  box-shadow: var(--shadow-inner);
  transform: translateY(0);
}

/* Neumorphism Card */
.card--neu {
  background: var(--bg-primary);
  border: none;
  box-shadow: var(--shadow-neumorphism);
}

.card--neu:hover {
  box-shadow: var(--shadow-neumorphism-lg);
}

/* Support for reduced motion */
@media (prefers-reduced-motion: reduce) {
  .glass,
  .neu,
  .btn--glass,
  .btn--neu {
    animation: none !important;
    transition: none !important;
  }

  .glass:hover,
  .neu:hover {
    transform: none !important;
  }
}

/* Fallback for browsers without backdrop-filter support */
@supports not (backdrop-filter: blur(20px)) {
  .glass {
    background: rgba(255, 255, 255, 0.95);
  }

  .glass--dark {
    background: rgba(0, 0, 0, 0.8);
  }

  .header--glass {
    background: rgba(255, 255, 255, 0.98);
  }

  .glass-badge {
    background: rgba(255, 255, 255, 0.9);
  }

  .btn--glass {
    background: rgba(255, 255, 255, 0.2);
  }
}

assets/css/components/patterns.css.content:
/* ============================================
   Background Patterns Component
   ============================================ */

/* Pattern Base */
.pattern {
  position: relative;
  overflow: hidden;
}

/* Dots Pattern */
.pattern--dots {
  background-image: radial-gradient(circle, var(--color-gray-300) 1px, transparent 1px);
  background-size: 20px 20px;
  background-position: 0 0;
}

.pattern--dots-dark {
  background-image: radial-gradient(circle, var(--color-gray-600) 1px, transparent 1px);
}

/* Grid Pattern */
.pattern--grid {
  background-image: 
    linear-gradient(var(--color-gray-200) 1px, transparent 1px),
    linear-gradient(90deg, var(--color-gray-200) 1px, transparent 1px);
  background-size: 40px 40px;
}

.pattern--grid-dark {
  background-image: 
    linear-gradient(var(--color-gray-700) 1px, transparent 1px),
    linear-gradient(90deg, var(--color-gray-700) 1px, transparent 1px);
}

/* Waves Pattern */
.pattern--waves {
  background-image: url("data:image/svg+xml,%3Csvg width='100' height='100' xmlns='http://www.w3.org/2000/svg'%3E%3Cpath d='M0 50 Q 25 30, 50 50 T 100 50' stroke='rgba(0,102,204,0.1)' fill='none' stroke-width='1'/%3E%3C/svg%3E");
  background-size: 100px 100px;
  opacity: 0.5;
}

/* Geometric Pattern */
.pattern--geometric::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: 
    repeating-linear-gradient(45deg, transparent, transparent 10px, rgba(0, 102, 204, 0.03) 10px, rgba(0, 102, 204, 0.03) 20px),
    repeating-linear-gradient(-45deg, transparent, transparent 10px, rgba(0, 163, 255, 0.03) 10px, rgba(0, 163, 255, 0.03) 20px);
  pointer-events: none;
}

/* Noise Texture */
.pattern--noise::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-image: url("data:image/svg+xml,%3Csvg viewBox='0 0 400 400' xmlns='http://www.w3.org/2000/svg'%3E%3Cfilter id='noiseFilter'%3E%3CfeTurbulence type='fractalNoise' baseFrequency='0.9' numOctaves='4' stitchTiles='stitch'/%3E%3C/filter%3E%3Crect width='100%25' height='100%25' filter='url(%23noiseFilter)' opacity='0.05'/%3E%3C/svg%3E");
  opacity: 0.3;
  pointer-events: none;
}

/* Gradient Overlay */
.pattern-overlay {
  position: relative;
}

.pattern-overlay::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: var(--gradient-card);
  pointer-events: none;
  z-index: 1;
}

/* Section Backgrounds - Enhanced */
.section--patterned {
  position: relative;
  overflow: hidden;
}

.section--patterned::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  opacity: 0.05;
  pointer-events: none;
  z-index: 0;
}

.section--patterned > * {
  position: relative;
  z-index: 1;
}

.section--patterned-dots::before {
  background-image: radial-gradient(circle, var(--color-primary) 1px, transparent 1px);
  background-size: 30px 30px;
}

.section--patterned-grid::before {
  background-image: 
    linear-gradient(var(--color-primary) 1px, transparent 1px),
    linear-gradient(90deg, var(--color-primary) 1px, transparent 1px);
  background-size: 50px 50px;
}

.section--patterned-gradient::before {
  background: var(--gradient-multicolor-soft);
  opacity: 0.1;
}

/* Animated Pattern Background */
.section--patterned-animated::before {
  background-image: radial-gradient(circle, var(--color-primary) 1px, transparent 1px);
  background-size: 30px 30px;
  animation: patternMove 20s linear infinite;
}

@keyframes patternMove {
  0% {
    background-position: 0 0;
  }
  100% {
    background-position: 30px 30px;
  }
}

/* Reduced Motion Support */
@media (prefers-reduced-motion: reduce) {
  .section--patterned-animated::before {
    animation: none !important;
  }
}

