/* ============================================
   Cards Component Styles
   ============================================ */

.card {
  background-color: var(--bg-primary);
  border: 1px solid var(--border-color);
  border-radius: var(--radius-lg);
  padding: var(--spacing-lg);
  box-shadow: var(--shadow-sm);
  transition: all var(--transition-base);
  position: relative;
  will-change: transform, box-shadow;
}

.card:focus-visible {
  outline: var(--focus-outline-width) solid var(--color-primary);
  outline-offset: var(--focus-outline-offset);
  box-shadow: var(--focus-ring), var(--shadow-md);
}

@media (hover: hover) and (pointer: fine) {
  .card:hover {
    box-shadow: var(--shadow-md);
    transform: translateY(-2px);
    border-color: var(--border-color-dark);
  }
}

/* Card with Gradient Border */
.card--gradient-border {
  position: relative;
  background: var(--bg-primary);
  border: none;
  padding: var(--spacing-lg);
  border-radius: var(--radius-lg);
}

.card--gradient-border::before {
  content: '';
  position: absolute;
  inset: 0;
  padding: 2px;
  background: var(--gradient-primary);
  border-radius: var(--radius-lg);
  -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
  -webkit-mask-composite: xor;
  mask-composite: exclude;
  opacity: 0;
  transition: opacity var(--transition-base);
}

.card--gradient-border:hover::before {
  opacity: 1;
}

/* Card with 3D Effect */
.card--3d {
  transform-style: preserve-3d;
  perspective: 1000px;
}

@media (hover: hover) and (pointer: fine) {
  .card--3d:hover {
    transform: translateY(-8px) rotateX(2deg);
    box-shadow: var(--shadow-multi-layer-lg);
  }
}

/* Card with Image Overlay */
.card--overlay {
  position: relative;
  overflow: hidden;
}

.card--overlay::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: var(--gradient-card);
  opacity: 0;
  transition: opacity var(--transition-base);
  pointer-events: none;
}

.card--overlay:hover::after {
  opacity: 1;
}

/* Premium Card Styles */
.card--premium {
  background: var(--bg-primary);
  border: 1px solid var(--border-color);
  box-shadow: var(--shadow-multi-layer);
  position: relative;
  overflow: hidden;
}

.card--premium::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(
    90deg,
    transparent,
    rgba(255, 255, 255, 0.3),
    transparent
  );
  transition: left var(--duration-slow);
}

@media (hover: hover) and (pointer: fine) {
  .card--premium:hover::before {
    left: 100%;
  }

  .card--premium:hover {
    box-shadow: var(--shadow-multi-layer-lg);
    transform: translateY(-4px);
  }
}

.card__header {
  margin-bottom: var(--spacing-md);
}

.card__title {
  font-size: 1.25rem;
  font-weight: 600;
  color: var(--text-primary);
  margin-bottom: var(--spacing-xs);
}

.card__subtitle {
  font-size: 0.875rem;
  color: var(--text-muted);
}

.card__body {
  color: var(--text-secondary);
  line-height: 1.6;
}

.card__footer {
  margin-top: var(--spacing-lg);
  padding-top: var(--spacing-md);
  border-top: 1px solid var(--border-color);
}

.card__image {
  width: 100%;
  height: auto;
  border-radius: var(--radius-md);
  margin-bottom: var(--spacing-md);
}

.product-card {
  display: flex;
  flex-direction: column;
  height: 100%;
  overflow: hidden;
  contain: layout style paint;
  position: relative;
}

/* Product Card with Gradient Border */
.product-card--gradient {
  border: 2px solid transparent;
  background-image: 
    linear-gradient(var(--bg-primary), var(--bg-primary)),
    var(--gradient-primary);
  background-origin: border-box;
  background-clip: padding-box, border-box;
}

@media (hover: hover) and (pointer: fine) {
  .product-card--gradient:hover {
    background-image: 
      linear-gradient(var(--bg-primary), var(--bg-primary)),
      var(--gradient-multicolor);
  }
}

.product-card__image-wrapper {
  position: relative;
  width: 100%;
  aspect-ratio: var(--aspect-product);
  overflow: hidden;
  border-radius: var(--radius-md);
  margin-bottom: var(--spacing-md);
  background-color: var(--bg-secondary);
}

.product-card__image {
  width: 100%;
  height: 100%;
  object-fit: cover;
  transition: transform var(--transition-base);
  will-change: transform;
}

@media (hover: hover) and (pointer: fine) {
  .product-card:hover .product-card__image {
    transform: scale(1.05);
  }
}

.product-card:focus-visible .product-card__image {
  outline: var(--focus-outline-width) solid var(--color-primary);
  outline-offset: var(--focus-outline-offset);
}

.product-card__badges {
  position: absolute;
  top: var(--spacing-sm);
  left: var(--spacing-sm);
  display: flex;
  flex-direction: column;
  gap: var(--spacing-xs);
}

.product-card__title {
  font-size: 1.125rem;
  font-weight: 600;
  color: var(--text-primary);
  margin-bottom: var(--spacing-xs);
}

.product-card__specs {
  font-size: 0.875rem;
  color: var(--text-muted);
  margin-bottom: var(--spacing-md);
}

.product-card__price {
  margin-top: auto;
  padding-top: var(--spacing-md);
  border-top: 1px solid var(--border-color);
}

.product-card__price-old {
  font-size: 0.875rem;
  color: var(--text-muted);
  text-decoration: line-through;
}

.product-card__price-new {
  font-size: 1.5rem;
  font-weight: 700;
  color: var(--color-primary);
  margin-top: var(--spacing-xs);
}

.product-card__price-install {
  font-size: 0.875rem;
  color: var(--text-muted);
  margin-top: var(--spacing-xs);
}

/* Step Number */
.step-number {
  width: 64px;
  height: 64px;
  margin: 0 auto var(--spacing-md);
  display: flex;
  align-items: center;
  justify-content: center;
  background: var(--gradient-primary);
  color: white;
  border-radius: 50%;
  font-size: 1.75rem;
  font-weight: 700;
  box-shadow: var(--shadow-primary);
}

