// @ts-check
const { test, expect } = require('@playwright/test');

test.describe('–§–æ—Ä–º—ã –æ–±—Ä–∞—Ç–Ω–æ–π —Å–≤—è–∑–∏', () => {
  test.beforeEach(async ({ page }) => {
    await page.goto('/');
    // –ñ–¥–µ–º –∑–∞–≥—Ä—É–∑–∫–∏ —Å—Ç—Ä–∞–Ω–∏—Ü—ã
    await page.waitForLoadState('networkidle');
    // –ü—Ä–æ–∫—Ä—É—á–∏–≤–∞–µ–º –∫ —Ñ–æ—Ä–º–µ
    const formSection = page.locator('.form').first();
    if (await formSection.count() > 0) {
      await formSection.scrollIntoViewIfNeeded();
      await page.waitForTimeout(500);
    }
  });

  test('–§–æ—Ä–º–∞ –≤–∏–¥–Ω–∞ –∏ —Å–æ–¥–µ—Ä–∂–∏—Ç –≤—Å–µ –ø–æ–ª—è', async ({ page }) => {
    const form = page.locator('.form').first();
    const formCount = await form.count();

    if (formCount === 0) {
      test.skip();
      return;
    }

    await expect(form).toBeVisible();

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –ø–æ–ª–µ–π
    const nameInput = page.locator('input[name="name"]');
    const phoneInput = page.locator('input[name="phone"]');
    const messageInput = page.locator('textarea[name="message"]');
    const submitButton = page.locator('button[type="submit"]');

    if (await nameInput.count() > 0) {
      await expect(nameInput).toBeVisible();
    }
    if (await phoneInput.count() > 0) {
      await expect(phoneInput).toBeVisible();
    }
    if (await messageInput.count() > 0) {
      await expect(messageInput).toBeVisible();
    }
    if (await submitButton.count() > 0) {
      await expect(submitButton).toBeVisible();
    }
  });

  test('–í–∞–ª–∏–¥–∞—Ü–∏—è: –æ—Ç–ø—Ä–∞–≤–∫–∞ –ø—É—Å—Ç–æ–π —Ñ–æ—Ä–º—ã –±–ª–æ–∫–∏—Ä—É–µ—Ç—Å—è', async ({ page }) => {
    const form = page.locator('.form').first();
    const formCount = await form.count();

    if (formCount === 0) {
      test.skip();
      return;
    }

    const submitButton = form.locator('button[type="submit"]');
    const submitCount = await submitButton.count();

    if (submitCount === 0) {
      test.skip();
      return;
    }

    // –ü—ã—Ç–∞–µ–º—Å—è –æ—Ç–ø—Ä–∞–≤–∏—Ç—å –ø—É—Å—Ç—É—é —Ñ–æ—Ä–º—É
    await submitButton.click();

    // –ñ–¥–µ–º –≤—ã–ø–æ–ª–Ω–µ–Ω–∏—è –≤–∞–ª–∏–¥–∞—Ü–∏–∏
    await page.waitForTimeout(1000);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ —Ñ–æ—Ä–º–∞ –Ω–µ –æ—Ç–ø—Ä–∞–≤–∏–ª–∞—Å—å (–Ω–µ –ø–æ—è–≤–∏–ª–æ—Å—å —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± —É—Å–ø–µ—Ö–µ)
    const successMessage = form.locator('.form__success-message');
    const successCount = await successMessage.count();
    if (successCount > 0) {
      await expect(successMessage).not.toBeVisible({ timeout: 2000 });
    }

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –æ—à–∏–±–æ–∫ –≤–∞–ª–∏–¥–∞—Ü–∏–∏ - –ª–∏–±–æ —á–µ—Ä–µ–∑ –∫–ª–∞—Å—Å –æ—à–∏–±–∫–∏, –ª–∏–±–æ —á–µ—Ä–µ–∑ —Å–æ–æ–±—â–µ–Ω–∏—è
    const errorMessages = form.locator('.form__error-message, .form__error');
    const errorCount = await errorMessages.count();

    // –¢–∞–∫–∂–µ –ø—Ä–æ–≤–µ—Ä—è–µ–º, –µ—Å—Ç—å –ª–∏ –ø–æ–ª—è —Å –∫–ª–∞—Å—Å–æ–º –æ—à–∏–±–∫–∏
    const errorInputs = form.locator('.form__input--error, .form__textarea--error, input:invalid, textarea:invalid');
    const errorInputsCount = await errorInputs.count();

    // –î–æ–ª–∂–Ω–∞ –±—ã—Ç—å —Ö–æ—Ç—è –±—ã –æ–¥–Ω–∞ –æ—à–∏–±–∫–∞ (–ª–∏–±–æ —Å–æ–æ–±—â–µ–Ω–∏–µ, –ª–∏–±–æ –∫–ª–∞—Å—Å –æ—à–∏–±–∫–∏, –ª–∏–±–æ HTML5 validation)
    // HTML5 validation –º–æ–∂–µ—Ç –Ω–µ –ø–æ–∫–∞–∑—ã–≤–∞—Ç—å –≤–∏–∑—É–∞–ª—å–Ω—ã–µ –∏–Ω–¥–∏–∫–∞—Ç–æ—Ä—ã, –ø–æ—ç—Ç–æ–º—É –ø—Ä–æ–≤–µ—Ä—è–µ–º —á–µ—Ä–µ–∑ invalid
    const hasValidationErrors = errorCount > 0 || errorInputsCount > 0;

    // –ï—Å–ª–∏ –Ω–µ—Ç –≤–∏–∑—É–∞–ª—å–Ω—ã—Ö –æ—à–∏–±–æ–∫, –ø—Ä–æ–≤–µ—Ä—è–µ–º —á–µ—Ä–µ–∑ HTML5 validation API
    if (!hasValidationErrors) {
      const hasInvalidFields = await form.evaluate((formEl) => {
        const inputs = formEl.querySelectorAll('input, textarea, select');
        for (const input of inputs) {
          if (input.hasAttribute('required') && !input.value.trim()) {
            return true;
          }
        }
        return false;
      });
      expect(hasInvalidFields || hasValidationErrors).toBeTruthy();
    } else {
      expect(hasValidationErrors).toBeTruthy();
    }
  });

  test('–í–∞–ª–∏–¥–∞—Ü–∏—è –∏–º–µ–Ω–∏: –º–∏–Ω–∏–º—É–º 2 —Å–∏–º–≤–æ–ª–∞', async ({ page }) => {
    const nameInput = page.locator('input[name="name"]');

    // –í–≤–æ–¥–∏–º 1 —Å–∏–º–≤–æ–ª (–Ω–µ–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ)
    await nameInput.fill('–ò');
    await nameInput.blur();
    await page.waitForTimeout(300);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –æ—à–∏–±–∫–∏
    const errorMessage = nameInput.locator('..').locator('.form__error-message');
    const hasError = await errorMessage.count() > 0 || await nameInput.evaluate(el => el.classList.contains('form__input--error'));
    expect(hasError).toBeTruthy();

    // –í–≤–æ–¥–∏–º 2 —Å–∏–º–≤–æ–ª–∞ (–¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ)
    await nameInput.fill('–ò–≤–∞–Ω');
    await nameInput.blur();
    await page.waitForTimeout(300);

    // –û—à–∏–±–∫–∞ –¥–æ–ª–∂–Ω–∞ –∏—Å—á–µ–∑–Ω—É—Ç—å –∏–ª–∏ –ø–æ–∫–∞–∑–∞—Ç—å—Å—è —É—Å–ø–µ—Ö
    const hasSuccess = await nameInput.evaluate(el => el.classList.contains('form__input--success'));
    // –ú–æ–∂–µ—Ç –±—ã—Ç—å —É—Å–ø–µ—Ö –∏–ª–∏ –ø—Ä–æ—Å—Ç–æ –æ—Ç—Å—É—Ç—Å—Ç–≤–∏–µ –æ—à–∏–±–∫–∏
  });

  test('–í–∞–ª–∏–¥–∞—Ü–∏—è —Ç–µ–ª–µ—Ñ–æ–Ω–∞: –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç', async ({ page }) => {
    const phoneInput = page.locator('input[name="phone"]');

    // –í–≤–æ–¥–∏–º –Ω–µ–∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –Ω–æ–º–µ—Ä
    await phoneInput.fill('123');
    await phoneInput.blur();
    await page.waitForTimeout(300);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –æ—à–∏–±–∫–∏
    const hasError = await phoneInput.evaluate(el => {
      const errorMsg = el.parentElement?.querySelector('.form__error-message');
      return el.classList.contains('form__input--error') || errorMsg !== null;
    });
    expect(hasError).toBeTruthy();

    // –í–≤–æ–¥–∏–º –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã–π –Ω–æ–º–µ—Ä
    await phoneInput.fill('+7 (999) 123-45-67');
    await phoneInput.blur();
    await page.waitForTimeout(300);
  });

  test('–§–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏–µ —Ç–µ–ª–µ—Ñ–æ–Ω–∞ –ø—Ä–∏ –≤–≤–æ–¥–µ', async ({ page }) => {
    const phoneInput = page.locator('input[name="phone"]');

    // –í–≤–æ–¥–∏–º —Ü–∏—Ñ—Ä—ã –±–µ–∑ —Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω–∏—è
    await phoneInput.fill('79991234567');
    await page.waitForTimeout(500);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –Ω–æ–º–µ—Ä –æ—Ç—Ñ–æ—Ä–º–∞—Ç–∏—Ä–æ–≤–∞–Ω
    const value = await phoneInput.inputValue();
    // –§–æ—Ä–º–∞—Ç –º–æ–∂–µ—Ç –±—ã—Ç—å —Ä–∞–∑–Ω—ã–º, –Ω–æ –¥–æ–ª–∂–µ–Ω —Å–æ–¥–µ—Ä–∂–∞—Ç—å —Å–∫–æ–±–∫–∏ –∏ –¥–µ—Ñ–∏—Å—ã
    expect(value).toMatch(/[\+\(\)\-\s]/);
  });

  test('–í–∞–ª–∏–¥–∞—Ü–∏—è —Å–æ–æ–±—â–µ–Ω–∏—è (–æ–ø—Ü–∏–æ–Ω–∞–ª—å–Ω–æ–µ –ø–æ–ª–µ)', async ({ page }) => {
    const messageTextarea = page.locator('textarea[name="message"]');

    // –°–æ–æ–±—â–µ–Ω–∏–µ –Ω–µ–æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ–µ, –Ω–æ –µ—Å–ª–∏ –≤–≤–µ–¥–µ–Ω–æ –º–µ–Ω–µ–µ 10 —Å–∏–º–≤–æ–ª–æ–≤, –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –æ—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ
    await messageTextarea.fill('–ö–æ—Ä–æ—Ç–∫–æ');
    await messageTextarea.blur();
    await page.waitForTimeout(300);

    // –ü—ã—Ç–∞–µ–º—Å—è –æ—Ç–ø—Ä–∞–≤–∏—Ç—å —Ñ–æ—Ä–º—É —Å –∫–æ—Ä–æ—Ç–∫–∏–º —Å–æ–æ–±—â–µ–Ω–∏–µ–º
    const submitButton = page.locator('button[type="submit"]');
    await submitButton.click();
    await page.waitForTimeout(500);

    // –ï—Å–ª–∏ –≤–∞–ª–∏–¥–∞—Ü–∏—è —Ä–∞–±–æ—Ç–∞–µ—Ç, –¥–æ–ª–∂–Ω–∞ –±—ã—Ç—å –æ—à–∏–±–∫–∞ –¥–ª—è —Å–æ–æ–±—â–µ–Ω–∏—è –∏–ª–∏ —Ñ–æ—Ä–º–∞ –Ω–µ –æ—Ç–ø—Ä–∞–≤–∏—Ç—Å—è
    const form = page.locator('.form').first();
    const hasErrors = await form.locator('.form__error-message').count() > 0;

    // –í–≤–æ–¥–∏–º –¥–æ—Å—Ç–∞—Ç–æ—á–Ω–æ–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–æ —Å–∏–º–≤–æ–ª–æ–≤
    await messageTextarea.fill('–≠—Ç–æ —Å–æ–æ–±—â–µ–Ω–∏–µ —Å–æ–¥–µ—Ä–∂–∏—Ç –±–æ–ª–µ–µ –¥–µ—Å—è—Ç–∏ —Å–∏–º–≤–æ–ª–æ–≤');
    await messageTextarea.blur();
    await page.waitForTimeout(300);
  });

  test('–û—Ç–ø—Ä–∞–≤–∫–∞ –≤–∞–ª–∏–¥–Ω–æ–π —Ñ–æ—Ä–º—ã', async ({ page }) => {
    const form = page.locator('.form').first();

    // –ó–∞–ø–æ–ª–Ω—è–µ–º –≤—Å–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è
    await page.locator('input[name="name"]').fill('–ò–≤–∞–Ω –ò–≤–∞–Ω–æ–≤');
    await page.locator('input[name="phone"]').fill('+7 (999) 123-45-67');
    await page.locator('textarea[name="message"]').fill('–•–æ—á—É —É—Å—Ç–∞–Ω–æ–≤–∏—Ç—å –∫–æ–Ω–¥–∏—Ü–∏–æ–Ω–µ—Ä –≤ –∫–≤–∞—Ä—Ç–∏—Ä—É');

    // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º —Ñ–æ—Ä–º—É
    const submitButton = form.locator('button[type="submit"]');
    await submitButton.click();

    // –ñ–¥–µ–º –ø–æ—è–≤–ª–µ–Ω–∏—è —Å–æ–æ–±—â–µ–Ω–∏—è –æ–± —É—Å–ø–µ—Ö–µ
    await page.waitForTimeout(2000);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ —Å–æ–æ–±—â–µ–Ω–∏—è –æ–± —É—Å–ø–µ—Ö–µ
    const successMessage = form.locator('.form__success-message');
    // –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º timeout –¥–ª—è –ø–æ—è–≤–ª–µ–Ω–∏—è —Å–æ–æ–±—â–µ–Ω–∏—è
    await expect(successMessage).toBeVisible({ timeout: 10000 });

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º, —á—Ç–æ –∫–Ω–æ–ø–∫–∞ –≤ —Å–æ—Å—Ç–æ—è–Ω–∏–∏ –∑–∞–≥—Ä—É–∑–∫–∏ –∏–ª–∏ –±—ã–ª–∞ –æ—Ç–∫–ª—é—á–µ–Ω–∞
    const buttonText = await submitButton.textContent();
    // –ú–æ–∂–µ—Ç –±—ã—Ç—å "–û—Ç–ø—Ä–∞–≤–∫–∞..." –∏–ª–∏ –∏—Å—Ö–æ–¥–Ω—ã–π —Ç–µ–∫—Å—Ç
  });

  test('–ö–Ω–æ–ø–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ –º–µ–Ω—è–µ—Ç —Å–æ—Å—Ç–æ—è–Ω–∏–µ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ', async ({ page }) => {
    const form = page.locator('.form').first();
    const submitButton = form.locator('button[type="submit"]');
    const originalText = await submitButton.textContent();

    // –ó–∞–ø–æ–ª–Ω—è–µ–º —Ñ–æ—Ä–º—É
    await page.locator('input[name="name"]').fill('–¢–µ—Å—Ç –¢–µ—Å—Ç–æ–≤');
    await page.locator('input[name="phone"]').fill('+7 (999) 123-45-67');

    // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º
    await submitButton.click();

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –∏–∑–º–µ–Ω–µ–Ω–∏–µ —Å–æ—Å—Ç–æ—è–Ω–∏—è –∫–Ω–æ–ø–∫–∏
    await page.waitForTimeout(100);
    const buttonDisabled = await submitButton.isDisabled();
    expect(buttonDisabled).toBeTruthy();

    // –ñ–¥–µ–º –∑–∞–≤–µ—Ä—à–µ–Ω–∏—è –æ—Ç–ø—Ä–∞–≤–∫–∏
    await page.waitForTimeout(2000);
  });

  test('–û—à–∏–±–∫–∏ –≤–∞–ª–∏–¥–∞—Ü–∏–∏ –æ—Ç–æ–±—Ä–∞–∂–∞—é—Ç—Å—è –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ', async ({ page }) => {
    const nameInput = page.locator('input[name="name"]');

    // –û—Å—Ç–∞–≤–ª—è–µ–º –ø–æ–ª–µ –ø—É—Å—Ç—ã–º –∏ –ø—ã—Ç–∞–µ–º—Å—è –æ—Ç–ø—Ä–∞–≤–∏—Ç—å
    const submitButton = page.locator('button[type="submit"]');
    await submitButton.click();
    await page.waitForTimeout(500);

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ —Å–æ–æ–±—â–µ–Ω–∏—è –æ–± –æ—à–∏–±–∫–µ
    const errorMessage = nameInput.locator('..').locator('.form__error-message');
    const hasError = await errorMessage.count() > 0;

    if (hasError) {
      await expect(errorMessage.first()).toBeVisible();
      const errorText = await errorMessage.first().textContent();
      expect(errorText).toContain('–æ–±—è–∑–∞—Ç–µ–ª—å–Ω–æ');
    }
  });

  test('ARIA –∞—Ç—Ä–∏–±—É—Ç—ã —Ñ–æ—Ä–º—ã –∫–æ—Ä—Ä–µ–∫—Ç–Ω—ã', async ({ page }) => {
    const form = page.locator('.form').first();

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º aria-label —É —Ñ–æ—Ä–º—ã
    const formAriaLabel = await form.getAttribute('aria-label');
    expect(formAriaLabel).toBeTruthy();

    // –ü—Ä–æ–≤–µ—Ä—è–µ–º aria-required —É –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã—Ö –ø–æ–ª–µ–π
    const nameInput = page.locator('input[name="name"]');
    const phoneInput = page.locator('input[name="phone"]');

    const nameRequired = await nameInput.getAttribute('aria-required');
    const phoneRequired = await phoneInput.getAttribute('aria-required');

    expect(nameRequired).toBe('true');
    expect(phoneRequired).toBe('true');
  });
});

assets/css/components/tables.css.content:
/* ============================================
   Responsive Tables Component
   ============================================ */

/* Table Wrapper for Horizontal Scroll */
.table-wrapper {
  overflow-x: auto;
  -webkit-overflow-scrolling: touch;
  margin: var(--spacing-xl) 0;
  border-radius: var(--radius-lg);
}

@media (max-width: 767px) {
  .table-wrapper {
    margin-left: calc(var(--spacing-md) * -1);
    margin-right: calc(var(--spacing-md) * -1);
    padding-left: var(--spacing-md);
    padding-right: var(--spacing-md);
    border-radius: 0;
  }
}

/* Responsive Table Base */
.responsive-table {
  width: 100%;
  border-collapse: collapse;
  background-color: var(--bg-primary);
  border-radius: var(--radius-lg);
  overflow: hidden;
  box-shadow: var(--shadow-sm);
  min-width: 600px;
}

.responsive-table th,
.responsive-table td {
  padding: var(--spacing-md);
  text-align: left;
  border-bottom: 1px solid var(--border-color);
}

.responsive-table th {
  background-color: var(--bg-secondary);
  font-weight: 600;
  color: var(--text-primary);
  position: sticky;
  top: 0;
  z-index: 1;
}

.responsive-table tr:hover {
  background-color: var(--bg-secondary);
}

.responsive-table tr:last-child td {
  border-bottom: none;
}

/* Card-based table for mobile */
@media (max-width: 767px) {
  .responsive-table,
  .responsive-table thead,
  .responsive-table tbody,
  .responsive-table th,
  .responsive-table td,
  .responsive-table tr {
    display: block;
  }

  .responsive-table thead {
    display: none;
  }

  .responsive-table tr {
    margin-bottom: var(--spacing-md);
    border: 1px solid var(--border-color);
    border-radius: var(--radius-md);
    padding: var(--spacing-md);
    background-color: var(--bg-primary);
    box-shadow: var(--shadow-sm);
  }

  .responsive-table td {
    border: none;
    padding: var(--spacing-sm) 0;
    position: relative;
    padding-left: 50%;
    text-align: right;
  }

  .responsive-table td::before {
    content: attr(data-label);
    position: absolute;
    left: 0;
    width: 45%;
    padding-right: var(--spacing-sm);
    text-align: left;
    font-weight: 600;
    color: var(--text-primary);
  }
}

/* Alternative: Keep table structure, add scroll */
.table-scroll {
  display: block;
  overflow-x: auto;
  white-space: nowrap;
  -webkit-overflow-scrolling: touch;
}

@media (min-width: 768px) {
  .table-scroll {
    display: table;
    overflow-x: visible;
    white-space: normal;
  }
}

assets/css/components/filters.css.content:
/* ============================================
   Filters Component (E-commerce)
   ============================================ */

.filters {
  background-color: var(--bg-primary);
  border: 1px solid var(--border-color);
  border-radius: var(--radius-lg);
  padding: var(--spacing-lg);
  margin-bottom: var(--spacing-xl);
}

.filters__header {
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: var(--spacing-lg);
  padding-bottom: var(--spacing-md);
  border-bottom: 1px solid var(--border-color);
}

.filters__title {
  font-size: 1.25rem;
  font-weight: 600;
  color: var(--text-primary);
  margin: 0;
}

.filters__clear {
  font-size: 0.875rem;
  color: var(--color-primary);
  background: none;
  border: none;
  cursor: pointer;
  padding: var(--spacing-xs) var(--spacing-sm);
  text-decoration: underline;
  transition: color var(--transition-fast);
}

.filters__clear:hover {
  color: var(--color-primary-hover);
}

/* Filter Group */
.filter-group {
  margin-bottom: var(--spacing-xl);
}

.filter-group:last-child {
  margin-bottom: 0;
}

.filter-group__title {
  font-size: 1rem;
  font-weight: 600;
  color: var(--text-primary);
  margin-bottom: var(--spacing-md);
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.filter-group__toggle {
  display: none;
}

@media (max-width: 767px) {
  .filter-group__toggle {
    display: block;
    background: none;
    border: none;
    color: var(--color-primary);
    cursor: pointer;
    padding: 0;
    font-size: 0.875rem;
  }

  .filter-group__content {
    display: none;
  }

  .filter-group__toggle[aria-expanded="true"] + .filter-group__content {
    display: block;
    margin-top: var(--spacing-md);
  }
}

/* Filter Options */
.filter-options {
  list-style: none;
  padding: 0;
  margin: 0;
}

.filter-option {
  margin-bottom: var(--spacing-sm);
}

.filter-option:last-child {
  margin-bottom: 0;
}

.filter-checkbox,
.filter-radio {
  display: flex;
  align-items: center;
  cursor: pointer;
  padding: var(--spacing-xs) 0;
  user-select: none;
}

.filter-checkbox input,
.filter-radio input {
  width: 18px;
  height: 18px;
  margin-right: var(--spacing-sm);
  cursor: pointer;
  accent-color: var(--color-primary);
}

.filter-checkbox label,
.filter-radio label {
  font-size: 0.875rem;
  color: var(--text-secondary);
  cursor: pointer;
  flex: 1;
  display: flex;
  align-items: center;
  justify-content: space-between;
}

.filter-checkbox:hover label,
.filter-radio:hover label {
  color: var(--text-primary);
}

.filter-checkbox input:checked + label,
.filter-radio input:checked + label {
  color: var(--text-primary);
  font-weight: 500;
}

/* Filter Count */
.filter-count {
  font-size: 0.75rem;
  color: var(--text-muted);
  background-color: var(--bg-secondary);
  padding: 0.125rem 0.5rem;
  border-radius: var(--radius-full);
  margin-left: var(--spacing-xs);
}

/* Price Range Filter */
.price-range {
  display: flex;
  gap: var(--spacing-md);
  align-items: center;
}

.price-range__input {
  flex: 1;
  padding: var(--spacing-sm) var(--spacing-md);
  font-size: 0.875rem;
  border: 1px solid var(--border-color);
  border-radius: var(--radius-md);
  min-width: 0;
}

.price-range__separator {
  color: var(--text-muted);
  font-size: 0.875rem;
}

/* Active Filters */
.active-filters {
  display: flex;
  flex-wrap: wrap;
  gap: var(--spacing-sm);
  margin-bottom: var(--spacing-lg);
  padding: var(--spacing-md);
  background-color: var(--bg-secondary);
  border-radius: var(--radius-md);
}

.active-filter {
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-xs);
  padding: var(--spacing-xs) var(--spacing-sm);
  background-color: var(--color-primary);
  color: var(--text-inverse);
  border-radius: var(--radius-full);
  font-size: 0.875rem;
  font-weight: 500;
}

.active-filter__remove {
  background: none;
  border: none;
  color: var(--text-inverse);
  cursor: pointer;
  padding: 0;
  width: 16px;
  height: 16px;
  display: flex;
  align-items: center;
  justify-content: center;
  opacity: 0.8;
  transition: opacity var(--transition-fast);
}

.active-filter__remove:hover {
  opacity: 1;
}

/* Sort Controls */
.sort-controls {
  display: flex;
  align-items: center;
  gap: var(--spacing-md);
  margin-bottom: var(--spacing-lg);
  flex-wrap: wrap;
}

.sort-controls__label {
  font-size: 0.875rem;
  color: var(--text-secondary);
  font-weight: 500;
}

.sort-controls__select {
  padding: var(--spacing-sm) var(--spacing-md);
  font-size: 0.875rem;
  border: 1px solid var(--border-color);
  border-radius: var(--radius-md);
  background-color: var(--bg-primary);
  color: var(--text-primary);
  cursor: pointer;
  min-width: 150px;
}

/* Mobile Filters Toggle */
.mobile-filters-toggle {
  display: none;
  width: 100%;
  margin-bottom: var(--spacing-lg);
}

@media (max-width: 767px) {
  .mobile-filters-toggle {
    display: flex;
  }

  .filters {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    z-index: var(--z-modal);
    overflow-y: auto;
    margin: 0;
    border-radius: 0;
    display: none;
  }

  .filters--open {
    display: block;
  }

  .filters__close {
    position: absolute;
    top: var(--spacing-md);
    right: var(--spacing-md);
    background: none;
    border: none;
    color: var(--text-primary);
    cursor: pointer;
    width: 40px;
    height: 40px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: var(--radius-full);
    background-color: var(--bg-secondary);
  }
}

assets/css/components/hero.css.content:
/* ============================================
   Hero Component Styles
   ============================================ */

.hero {
  position: relative;
  width: 100%;
  min-height: 60vh;
  display: flex;
  align-items: center;
  background: var(--gradient-hero);
  background-size: var(--gradient-animated-size);
  color: var(--text-inverse);
  overflow: hidden;
  padding: var(--spacing-3xl) 0;
  margin-left: 0;
  margin-right: 0;
  box-sizing: border-box;
  animation: gradientShift 15s ease infinite;
  will-change: background-position;
}

@keyframes gradientShift {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

@media (min-width: 768px) {
  .hero {
    min-height: 80vh;
  }
}

@media (min-width: 1024px) {
  .hero {
    min-height: 100vh;
  }
}

.hero::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, var(--opacity-overlay));
  z-index: 1;
}

.hero::after {
  content: '';
  position: absolute;
  top: -50%;
  left: -50%;
  width: 200%;
  height: 200%;
  background: var(--gradient-radial-hero);
  opacity: 0.3;
  animation: float 20s ease-in-out infinite;
  z-index: 1;
  pointer-events: none;
}

@keyframes float {
  0%, 100% {
    transform: translate(0, 0) rotate(0deg);
  }
  33% {
    transform: translate(30px, -30px) rotate(120deg);
  }
  66% {
    transform: translate(-20px, 20px) rotate(240deg);
  }
}

.hero__container {
  position: relative;
  z-index: 2;
  display: grid;
  grid-template-columns: 1fr;
  gap: var(--spacing-2xl);
  align-items: center;
  max-width: 100% !important;
  width: 100%;
}

/* –ü–µ—Ä–µ–æ–ø—Ä–µ–¥–µ–ª—è–µ–º max-width –¥–ª—è container –≤–Ω—É—Ç—Ä–∏ hero */
.hero .container,
.hero .container.hero__container {
  max-width: 100% !important;
  width: 100% !important;
  padding-left: var(--spacing-lg);
  padding-right: var(--spacing-lg);
}

@media (min-width: 768px) {
  .hero .container,
  .hero .container.hero__container {
    max-width: 100% !important;
    width: 100% !important;
    padding-left: var(--spacing-xl);
    padding-right: var(--spacing-xl);
  }
}

@media (min-width: 1024px) {
  .hero .container,
  .hero .container.hero__container {
    max-width: 100% !important;
    width: 100% !important;
    padding-left: var(--spacing-2xl);
    padding-right: var(--spacing-2xl);
  }
}

@media (min-width: 1024px) {
  .hero__container {
    grid-template-columns: 1fr 1fr;
  }
}

.hero__content {
  text-align: left;
  width: 100%;
  max-width: 100%;
}

@media (min-width: 1024px) {
  .hero__content {
    text-align: left;
    width: 100%;
    max-width: 100%;
  }
}

.hero__title {
  font-size: clamp(2rem, 5vw, 3.5rem);
  font-weight: 700;
  line-height: 1.2;
  color: var(--text-inverse);
  margin-bottom: var(--spacing-lg);
  text-align: left;
  width: 100%;
}

.hero__subtitle {
  font-size: clamp(1.125rem, 2vw, 1.5rem);
  line-height: 1.6;
  color: rgba(255, 255, 255, var(--opacity-text-secondary));
  margin-bottom: var(--spacing-xl);
  width: 100%;
  text-align: left;
}

@media (min-width: 1024px) {
  .hero__subtitle {
    text-align: left;
    width: 100%;
  }
}

.hero__actions {
  display: flex;
  flex-direction: column;
  gap: var(--spacing-md);
  align-items: flex-start;
}

@media (min-width: 768px) {
  .hero__actions {
    flex-direction: row;
  }
}

@media (min-width: 1024px) {
  .hero__actions {
    justify-content: flex-start;
  }
}

.hero__button {
  min-width: 200px;
}

.hero__stats {
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: var(--spacing-lg);
  margin-top: var(--spacing-2xl);
}

@media (min-width: 768px) {
  .hero__stats {
    grid-template-columns: repeat(4, 1fr);
  }
}

.hero__stat {
  text-align: center;
}

.hero__stat-number {
  display: block;
  font-size: clamp(1.5rem, 3vw, 2.5rem);
  font-weight: 700;
  color: var(--text-inverse);
  margin-bottom: var(--spacing-xs);
}

.hero__stat-label {
  font-size: 0.875rem;
  color: rgba(255, 255, 255, var(--opacity-text-muted));
}

.hero__image {
  display: none;
}

@media (min-width: 1024px) {
  .hero__image {
    display: block;
    width: 100%;
    height: auto;
    border-radius: var(--radius-lg);
    box-shadow: var(--shadow-xl);
  }
}

.hero__trust-badges {
  display: flex;
  flex-wrap: wrap;
  gap: var(--spacing-md);
  justify-content: flex-start;
  margin-top: var(--spacing-xl);
}

@media (min-width: 1024px) {
  .hero__trust-badges {
    justify-content: flex-start;
  }
}

.hero__badge {
  display: inline-flex;
  align-items: center;
  gap: var(--spacing-xs);
  padding: var(--spacing-sm) var(--spacing-md);
  background-color: rgba(255, 255, 255, var(--opacity-backdrop));
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.3);
  border-radius: var(--radius-full);
  font-size: 0.875rem;
  color: var(--text-inverse);
  box-shadow: var(--shadow-sm);
  transition: all var(--transition-fast);
}

@media (hover: hover) and (pointer: fine) {
  .hero__badge:hover {
    background-color: rgba(255, 255, 255, 0.3);
    transform: translateY(-2px);
    box-shadow: var(--shadow-md);
  }
}

/* Reduced Motion Support */
@media (prefers-reduced-motion: reduce) {
  .hero {
    animation: none !important;
    background-size: 100% 100% !important;
  }

  .hero::after {
    animation: none !important;
  }
}

assets/js/mobile-menu.js.content:
/* ============================================
   Mobile Menu Enhancement
   ============================================ */

(function() {
  'use strict';

  const menuToggle = document.getElementById('menu-toggle');
  const menuClose = document.getElementById('menu-close');
  const mobileMenu = document.getElementById('mobile-menu');
  const menuLinks = mobileMenu ? mobileMenu.querySelectorAll('.mobile-menu__nav-link, .mobile-menu__phone, .mobile-menu__social-link, .mobile-menu__contact-link') : [];

  // Fix SVG icon sizes (prevent giant icons issue)
  function fixIconSizes() {
    const socialIcons = document.querySelectorAll('.mobile-menu__social-icon, .mobile-menu__social-link svg');
    const contactIcons = document.querySelectorAll('.mobile-menu__contact-icon, .mobile-menu__contact-link svg');

    socialIcons.forEach(icon => {
      icon.setAttribute('width', '24');
      icon.setAttribute('height', '24');
      icon.style.setProperty('width', '24px', 'important');
      icon.style.setProperty('height', '24px', 'important');
      icon.style.setProperty('max-width', '24px', 'important');
      icon.style.setProperty('max-height', '24px', 'important');
      icon.style.setProperty('min-width', '24px', 'important');
      icon.style.setProperty('min-height', '24px', 'important');
      icon.style.setProperty('display', 'block', 'important');
      icon.style.setProperty('flex-shrink', '0', 'important');
    });

    contactIcons.forEach(icon => {
      icon.setAttribute('width', '20');
      icon.setAttribute('height', '20');
      icon.style.setProperty('width', '20px', 'important');
      icon.style.setProperty('height', '20px', 'important');
      icon.style.setProperty('max-width', '20px', 'important');
      icon.style.setProperty('max-height', '20px', 'important');
      icon.style.setProperty('min-width', '20px', 'important');
      icon.style.setProperty('min-height', '20px', 'important');
      icon.style.setProperty('display', 'block', 'important');
      icon.style.setProperty('flex-shrink', '0', 'important');
    });
  }

  // Open menu
  function openMenu() {
    if (!mobileMenu || !menuToggle) return;

    mobileMenu.classList.add('mobile-menu--open');
    document.body.style.overflow = 'hidden';
    menuToggle.setAttribute('aria-expanded', 'true');

    // Fix icon sizes when menu opens
    setTimeout(fixIconSizes, 0);

    // Focus management
    menuClose?.focus();

    // Prevent background scroll
    document.addEventListener('touchmove', preventScroll, { passive: false });
  }

  // Close menu
  function closeMenu() {
    if (!mobileMenu || !menuToggle) return;

    mobileMenu.classList.remove('mobile-menu--open');
    document.body.style.overflow = '';
    menuToggle.setAttribute('aria-expanded', 'false');

    // Return focus to toggle button
    menuToggle.focus();

    // Remove scroll prevention
    document.removeEventListener('touchmove', preventScroll);
  }

  // Prevent scroll on background
  function preventScroll(e) {
    if (!mobileMenu?.classList.contains('mobile-menu--open')) return;
    if (mobileMenu.contains(e.target)) return;
    e.preventDefault();
  }

  // Toggle menu
  if (menuToggle) {
    menuToggle.addEventListener('click', (e) => {
      e.preventDefault();
      if (mobileMenu.classList.contains('mobile-menu--open')) {
        closeMenu();
      } else {
        openMenu();
      }
    });

    // Keyboard support for toggle
    menuToggle.addEventListener('keydown', (e) => {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        if (mobileMenu.classList.contains('mobile-menu--open')) {
          closeMenu();
        } else {
          openMenu();
        }
      }
    });
  }

  // Close button
  if (menuClose) {
    menuClose.addEventListener('click', (e) => {
      e.preventDefault();
      closeMenu();
    });

    menuClose.addEventListener('keydown', (e) => {
      if (e.key === 'Enter' || e.key === ' ') {
        e.preventDefault();
        closeMenu();
      }
      if (e.key === 'Escape') {
        closeMenu();
      }
    });
  }

  // Close menu when clicking outside
  if (mobileMenu) {
    mobileMenu.addEventListener('click', (e) => {
      if (e.target === mobileMenu) {
        closeMenu();
      }
    });
  }

  // Close menu when clicking on links
  menuLinks.forEach(link => {
    link.addEventListener('click', () => {
      // Small delay for better UX
      setTimeout(closeMenu, 150);
    });
  });

  // Close menu on Escape key
  document.addEventListener('keydown', (e) => {
    if (e.key === 'Escape' && mobileMenu?.classList.contains('mobile-menu--open')) {
      closeMenu();
    }
  });

  // Trap focus within menu when open
  if (mobileMenu) {
    const focusableElements = mobileMenu.querySelectorAll(
      'a, button, [tabindex]:not([tabindex="-1"])'
    );
    const firstFocusable = focusableElements[0];
    const lastFocusable = focusableElements[focusableElements.length - 1];

    mobileMenu.addEventListener('keydown', (e) => {
      if (!mobileMenu.classList.contains('mobile-menu--open')) return;

      if (e.key === 'Tab') {
        if (e.shiftKey) {
          if (document.activeElement === firstFocusable) {
            e.preventDefault();
            lastFocusable?.focus();
          }
        } else {
          if (document.activeElement === lastFocusable) {
            e.preventDefault();
            firstFocusable?.focus();
          }
        }
      }
    });
  }

  // Initialize aria-expanded
  if (menuToggle) {
    menuToggle.setAttribute('aria-expanded', 'false');
  }

  // Initialize icon sizes on DOM ready
  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', fixIconSizes);
  } else {
    fixIconSizes();
  }
})();

