/* Layout Utilities */

.section {
  padding: var(--spacing-2xl) 0;
  position: relative;
}

@media (min-width: 768px) {
  .section {
    padding: var(--spacing-3xl) 0;
  }
}

.section--sm {
  padding: var(--spacing-xl) 0;
}

@media (min-width: 768px) {
  .section--sm {
    padding: var(--spacing-2xl) 0;
  }
}

.section--lg {
  padding: var(--spacing-3xl) 0;
}

@media (min-width: 1024px) {
  .section--lg {
    padding: calc(var(--spacing-3xl) * 1.5) 0;
  }
}

.section--gray {
  background-color: var(--bg-secondary);
}

.grid {
  display: grid;
  gap: var(--spacing-lg);
}

.grid--cols-1 {
  grid-template-columns: 1fr;
}

.grid--cols-2 {
  grid-template-columns: repeat(2, 1fr);
}

.grid--cols-3 {
  grid-template-columns: repeat(3, 1fr);
}

.grid--cols-4 {
  grid-template-columns: repeat(4, 1fr);
}

@media (min-width: 768px) {
  .grid--sm-cols-2 {
    grid-template-columns: repeat(2, 1fr);
  }
}

@media (min-width: 1024px) {
  .grid--lg-cols-3 {
    grid-template-columns: repeat(3, 1fr);
  }

  .grid--lg-cols-4 {
    grid-template-columns: repeat(4, 1fr);
  }
}

.flex {
  display: flex;
}

.flex--column {
  flex-direction: column;
}

.flex--wrap {
  flex-wrap: wrap;
}

.flex--center {
  align-items: center;
  justify-content: center;
}

.flex--between {
  justify-content: space-between;
}

.flex--gap-md {
  gap: var(--spacing-md);
}

.flex--gap-lg {
  gap: var(--spacing-lg);
}

.flex--gap-xl {
  gap: var(--spacing-xl);
}

