assets/js/theme-switcher.js.content:
// Theme Switcher
(function() {
  const themeButtons = document.querySelectorAll('[data-theme]');

  themeButtons.forEach(button => {
    button.addEventListener('click', function(e) {
      e.preventDefault();
      const theme = this.getAttribute('data-theme');
      const currentUrl = window.location.href.split('#')[0];
      window.location.href = currentUrl + '#' + theme;
    });
  });
})();

api/contact.js.content:
/**
 * Vercel Serverless Function –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ä–º—ã —á–µ—Ä–µ–∑ Resend
 * –ü—É—Ç—å: /api/contact
 */

const { Resend } = require('resend');

module.exports = async (req, res) => {
  // –†–∞–∑—Ä–µ—à–∞–µ–º CORS
  res.setHeader('Access-Control-Allow-Origin', '*');
  res.setHeader('Access-Control-Allow-Headers', 'Content-Type');
  res.setHeader('Access-Control-Allow-Methods', 'POST, OPTIONS');
  res.setHeader('Content-Type', 'application/json');

  // –û–±—Ä–∞–±–æ—Ç–∫–∞ OPTIONS –∑–∞–ø—Ä–æ—Å–∞ (preflight)
  if (req.method === 'OPTIONS') {
    return res.status(200).end();
  }

  // –¢–æ–ª—å–∫–æ POST –∑–∞–ø—Ä–æ—Å—ã
  if (req.method !== 'POST') {
    return res.status(405).json({ error: 'Method not allowed' });
  }

  try {
    // –ü–æ–ª—É—á–∞–µ–º –¥–∞–Ω–Ω—ã–µ —Ñ–æ—Ä–º—ã
    const { name, phone, email, message } = req.body;

    // –í–∞–ª–∏–¥–∞—Ü–∏—è
    if (!name || !phone) {
      return res.status(400).json({ error: '–ò–º—è –∏ —Ç–µ–ª–µ—Ñ–æ–Ω –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã –¥–ª—è –∑–∞–ø–æ–ª–Ω–µ–Ω–∏—è' });
    }

    // –ü–æ–ª—É—á–∞–µ–º API –∫–ª—é—á Resend –∏–∑ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã—Ö –æ–∫—Ä—É–∂–µ–Ω–∏—è
    const resendApiKey = process.env.RESEND_API_KEY;
    if (!resendApiKey) {
      console.error('RESEND_API_KEY –Ω–µ —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω');
      return res.status(500).json({ error: '–û—à–∏–±–∫–∞ –∫–æ–Ω—Ñ–∏–≥—É—Ä–∞—Ü–∏–∏ —Å–µ—Ä–≤–µ—Ä–∞' });
    }

    const resend = new Resend(resendApiKey);

    // –ü–æ–ª—É—á–∞–µ–º email –ø–æ–ª—É—á–∞—Ç–µ–ª—è –∏–∑ –ø–µ—Ä–µ–º–µ–Ω–Ω—ã—Ö –æ–∫—Ä—É–∂–µ–Ω–∏—è –∏–ª–∏ –∏—Å–ø–æ–ª—å–∑—É–µ–º –¥–µ—Ñ–æ–ª—Ç–Ω—ã–π
    const recipientEmail = process.env.CONTACT_EMAIL || 'info@msk.splitis.ru';

    // –§–æ—Ä–º–∏—Ä—É–µ–º —Ç–µ–º—É –ø–∏—Å—å–º–∞
    const subject = `–ù–æ–≤–∞—è –∑–∞—è–≤–∫–∞ —Å —Å–∞–π—Ç–∞ msk.splitis.ru${name ? ` –æ—Ç ${name}` : ''}`;

    // –§–æ—Ä–º–∏—Ä—É–µ–º —Ç–µ–ª–æ –ø–∏—Å—å–º–∞
    const emailBody = `
      <h2>–ù–æ–≤–∞—è –∑–∞—è–≤–∫–∞ —Å —Å–∞–π—Ç–∞</h2>
      <p><strong>–ò–º—è:</strong> ${name}</p>
      <p><strong>–¢–µ–ª–µ—Ñ–æ–Ω:</strong> ${phone}</p>
      ${email ? `<p><strong>Email:</strong> ${email}</p>` : ''}
      ${message ? `<p><strong>–°–æ–æ–±—â–µ–Ω–∏–µ:</strong><br>${message.replace(/\n/g, '<br>')}</p>` : ''}
      <hr>
      <p><small>–î–∞—Ç–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏: ${new Date().toLocaleString('ru-RU', { timeZone: 'Europe/Moscow' })}</small></p>
      <p><small>IP –∞–¥—Ä–µ—Å: ${req.headers['x-forwarded-for'] || req.headers['x-real-ip'] || '–Ω–µ–∏–∑–≤–µ—Å—Ç–Ω–æ'}</small></p>
    `;

    // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –ø–∏—Å—å–º–æ
    const { data, error } = await resend.emails.send({
      from: 'noreply@msk.splitis.ru', // –ó–∞–º–µ–Ω–∏—Ç–µ –Ω–∞ –≤–∞—à –≤–µ—Ä–∏—Ñ–∏—Ü–∏—Ä–æ–≤–∞–Ω–Ω—ã–π –¥–æ–º–µ–Ω –≤ Resend
      to: recipientEmail,
      replyTo: email || recipientEmail,
      subject: subject,
      html: emailBody,
    });

    if (error) {
      console.error('–û—à–∏–±–∫–∞ –æ—Ç–ø—Ä–∞–≤–∫–∏ —á–µ—Ä–µ–∑ Resend:', error);
      return res.status(500).json({ error: '–û—à–∏–±–∫–∞ –ø—Ä–∏ –æ—Ç–ø—Ä–∞–≤–∫–µ –ø–∏—Å—å–º–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ –∏–ª–∏ —Å–≤—è–∂–∏—Ç–µ—Å—å —Å –Ω–∞–º–∏ –ø–æ —Ç–µ–ª–µ—Ñ–æ–Ω—É.' });
    }

    return res.status(200).json({ 
      success: true, 
      message: '–ó–∞—è–≤–∫–∞ —É—Å–ø–µ—à–Ω–æ –æ—Ç–ø—Ä–∞–≤–ª–µ–Ω–∞',
      id: data?.id 
    });

  } catch (error) {
    console.error('–û—à–∏–±–∫–∞ –æ–±—Ä–∞–±–æ—Ç–∫–∏ –∑–∞–ø—Ä–æ—Å–∞:', error);
    return res.status(500).json({ error: '–í–Ω—É—Ç—Ä–µ–Ω–Ω—è—è –æ—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞' });
  }
};

assets/js/calculator-power.js.content:
/* ============================================
   –ö–∞–ª—å–∫—É–ª—è—Ç–æ—Ä –º–æ—â–Ω–æ—Å—Ç–∏ –∫–æ–Ω–¥–∏—Ü–∏–æ–Ω–µ—Ä–∞
   –†–∞—Å—á–µ—Ç –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ–π –º–æ—â–Ω–æ—Å—Ç–∏ –≤ –∫–í—Ç –∏ BTU
   ============================================ */

(function() {
  'use strict';

  /**
   * –ö–∞–ª—å–∫—É–ª—è—Ç–æ—Ä –º–æ—â–Ω–æ—Å—Ç–∏ –∫–æ–Ω–¥–∏—Ü–∏–æ–Ω–µ—Ä–∞
   */
  class PowerCalculator {
    constructor(container) {
      this.container = container;
      this.form = container?.querySelector('.calculator-power__form');
      this.resultContainer = container?.querySelector('.calculator-power__result');
      this.init();
    }

    init() {
      if (!this.form) return;

      // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –æ—Ç–ø—Ä–∞–≤–∫–∏ —Ñ–æ—Ä–º—ã
      this.form.addEventListener('submit', (e) => {
        e.preventDefault();
        this.calculate();
      });

      // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –∏–∑–º–µ–Ω–µ–Ω–∏–π –≤ –ø–æ–ª—è—Ö
      const inputs = this.form.querySelectorAll('input, select');
      inputs.forEach(input => {
        input.addEventListener('change', () => {
          if (this.form.checkValidity()) {
            this.calculate();
          }
        });
      });
    }

    /**
     * –†–∞—Å—Å—á–∏—Ç—ã–≤–∞–µ—Ç –Ω–µ–æ–±—Ö–æ–¥–∏–º—É—é –º–æ—â–Ω–æ—Å—Ç—å –∫–æ–Ω–¥–∏—Ü–∏–æ–Ω–µ—Ä–∞
     */
    calculate() {
      const formData = new FormData(this.form);
      const area = parseFloat(formData.get('area')) || 0;
      const roomType = formData.get('roomType') || 'apartment';
      const ceilingHeight = parseFloat(formData.get('ceilingHeight')) || 2.5;
      const windows = parseInt(formData.get('windows')) || 1;
      const floor = parseInt(formData.get('floor')) || 1;
      const equipment = formData.get('equipment') === 'yes';
      const sunExposure = formData.get('sunExposure') || 'normal';

      // –ë–∞–∑–æ–≤—ã–π —Ä–∞—Å—á–µ—Ç: 1 –∫–í—Ç –Ω–∞ 10 –º¬≤ (–¥–ª—è —Å—Ç–∞–Ω–¥–∞—Ä—Ç–Ω–æ–π –∫–≤–∞—Ä—Ç–∏—Ä—ã)
      let basePower = area / 10;

      // –ö–æ—ç—Ñ—Ñ–∏—Ü–∏–µ–Ω—Ç—ã –¥–ª—è —Ä–∞–∑–Ω—ã—Ö —Ç–∏–ø–æ–≤ –ø–æ–º–µ—â–µ–Ω–∏–π
      const roomCoefficients = {
        apartment: 1.0,
        office: 1.2,
        shop: 1.5,
        restaurant: 1.8,
        server: 2.5
      };
      basePower *= roomCoefficients[roomType] || 1.0;

      // –£—á–µ—Ç –≤—ã—Å–æ—Ç—ã –ø–æ—Ç–æ–ª–∫–æ–≤
      if (ceilingHeight > 3) {
        basePower *= (ceilingHeight / 2.5);
      }

      // –£—á–µ—Ç –æ–∫–æ–Ω
      basePower += (windows - 1) * 0.1;

      // –£—á–µ—Ç —ç—Ç–∞–∂–∞ (–≤–µ—Ä—Ö–Ω–∏–µ —ç—Ç–∞–∂–∏ —Ç–µ–ø–ª–µ–µ)
      if (floor >= 5) {
        basePower *= 1.1;
      }

      // –£—á–µ—Ç —Ç–µ—Ö–Ω–∏–∫–∏
      if (equipment) {
        basePower += 0.3;
      }

      // –£—á–µ—Ç —Å–æ–ª–Ω–µ—á–Ω–æ–π —Å—Ç–æ—Ä–æ–Ω—ã
      const sunCoefficients = {
        north: 0.9,
        normal: 1.0,
        south: 1.2
      };
      basePower *= sunCoefficients[sunExposure] || 1.0;

      // –û–∫—Ä—É–≥–ª–µ–Ω–∏–µ –¥–æ –±–ª–∏–∂–∞–π—à–µ–≥–æ —Å—Ç–∞–Ω–¥–∞—Ä—Ç–Ω–æ–≥–æ –∑–Ω–∞—á–µ–Ω–∏—è
      const standardPowers = [2.0, 2.5, 3.5, 5.0, 7.0, 9.0, 12.0, 14.0, 18.0, 24.0];
      let recommendedPower = standardPowers.find(p => p >= basePower) || standardPowers[standardPowers.length - 1];

      // –ö–æ–Ω–≤–µ—Ä—Ç–∞—Ü–∏—è –≤ BTU (1 –∫–í—Ç ‚âà 3412 BTU)
      const btu = Math.round(recommendedPower * 3412);

      // –†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏—è –º–æ–¥–µ–ª–∏
      const recommendation = this.getRecommendation(recommendedPower, area);

      // –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
      this.showResult({
        area,
        calculatedPower: basePower.toFixed(1),
        recommendedPower,
        btu,
        recommendation
      });
    }

    /**
     * –ü–æ–ª—É—á–∞–µ—Ç —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏—é –º–æ–¥–µ–ª–∏ –Ω–∞ –æ—Å–Ω–æ–≤–µ –º–æ—â–Ω–æ—Å—Ç–∏
     */
    getRecommendation(power, area) {
      if (power <= 2.5) {
        return {
          type: '–ù–∞—Å—Ç–µ–Ω–Ω–∞—è —Å–ø–ª–∏—Ç-—Å–∏—Å—Ç–µ–º–∞',
          models: ['Daikin FTXS25K', 'Mitsubishi MSZ-LN25'],
          coverage: '–¥–æ 25 –º¬≤'
        };
      } else if (power <= 5.0) {
        return {
          type: '–ù–∞—Å—Ç–µ–Ω–Ω–∞—è —Å–ø–ª–∏—Ç-—Å–∏—Å—Ç–µ–º–∞',
          models: ['LG S12EQ3', 'Samsung AR12'],
          coverage: '25-35 –º¬≤'
        };
      } else if (power <= 7.0) {
        return {
          type: '–ù–∞–ø–æ–ª—å–Ω–æ-–ø–æ—Ç–æ–ª–æ—á–Ω–∞—è —Å–∏—Å—Ç–µ–º–∞',
          models: ['Daikin FVXS', 'Mitsubishi Electric MSZ'],
          coverage: '35-50 –º¬≤'
        };
      } else {
        return {
          type: '–ö–∞—Å—Å–µ—Ç–Ω–∞—è –∏–ª–∏ –∫–∞–Ω–∞–ª—å–Ω–∞—è —Å–∏—Å—Ç–µ–º–∞',
          models: ['Daikin FDTQ', 'Mitsubishi Electric PLFY'],
          coverage: '50+ –º¬≤'
        };
      }
    }

    /**
     * –û—Ç–æ–±—Ä–∞–∂–∞–µ—Ç —Ä–µ–∑—É–ª—å—Ç–∞—Ç —Ä–∞—Å—á–µ—Ç–∞
     */
    showResult(result) {
      if (!this.resultContainer) return;

      const html = `
        <div class="calculator-power__result-content">
          <h3 class="calculator-power__result-title">–†–µ–∑—É–ª—å—Ç–∞—Ç —Ä–∞—Å—á–µ—Ç–∞</h3>

          <div class="calculator-power__result-stats">
            <div class="calculator-power__stat">
              <span class="calculator-power__stat-label">–†–∞—Å—á–µ—Ç–Ω–∞—è –º–æ—â–Ω–æ—Å—Ç—å</span>
              <span class="calculator-power__stat-value">${result.calculatedPower} –∫–í—Ç</span>
            </div>
            <div class="calculator-power__stat">
              <span class="calculator-power__stat-label">–†–µ–∫–æ–º–µ–Ω–¥—É–µ–º–∞—è –º–æ—â–Ω–æ—Å—Ç—å</span>
              <span class="calculator-power__stat-value calculator-power__stat-value--highlight">${result.recommendedPower} –∫–í—Ç</span>
            </div>
            <div class="calculator-power__stat">
              <span class="calculator-power__stat-label">–í BTU</span>
              <span class="calculator-power__stat-value">${result.btu.toLocaleString('ru-RU')} BTU</span>
            </div>
          </div>

          <div class="calculator-power__recommendation">
            <h4 class="calculator-power__recommendation-title">–†–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏—è</h4>
            <p class="calculator-power__recommendation-text">
              –î–ª—è –ø–æ–º–µ—â–µ–Ω–∏—è –ø–ª–æ—â–∞–¥—å—é <strong>${result.area} –º¬≤</strong> —Ä–µ–∫–æ–º–µ–Ω–¥—É–µ—Ç—Å—è 
              <strong>${result.recommendation.type.toLowerCase()}</strong> –º–æ—â–Ω–æ—Å—Ç—å—é 
              <strong>${result.recommendedPower} –∫–í—Ç</strong> (–ø–æ–∫—Ä—ã—Ç–∏–µ: ${result.recommendation.coverage}).
            </p>
            <p class="calculator-power__recommendation-models">
              –ü–æ–¥—Ö–æ–¥—è—â–∏–µ –º–æ–¥–µ–ª–∏: ${result.recommendation.models.join(', ')}
            </p>
          </div>

          <div class="calculator-power__actions">
            <a href="/katalog.html" class="btn btn--primary">–ü–æ—Å–º–æ—Ç—Ä–µ—Ç—å –∫–∞—Ç–∞–ª–æ–≥</a>
            <a href="tel:+74997575719" class="btn btn--outline">–ü–æ–∑–≤–æ–Ω–∏—Ç—å –∫–æ–Ω—Å—É–ª—å—Ç–∞–Ω—Ç—É</a>
          </div>

          <p class="calculator-power__disclaimer">
            <small>
              * –†–∞—Å—á–µ—Ç —è–≤–ª—è–µ—Ç—Å—è –ø—Ä–∏–±–ª–∏–∑–∏—Ç–µ–ª—å–Ω—ã–º. –î–ª—è —Ç–æ—á–Ω–æ–≥–æ –ø–æ–¥–±–æ—Ä–∞ –º–æ–¥–µ–ª–∏ —Ä–µ–∫–æ–º–µ–Ω–¥—É–µ—Ç—Å—è –≤—ã–∑–æ–≤ —Å–ø–µ—Ü–∏–∞–ª–∏—Å—Ç–∞ –¥–ª—è –±–µ—Å–ø–ª–∞—Ç–Ω–æ–≥–æ –∑–∞–º–µ—Ä–∞.
            </small>
          </p>
        </div>
      `;

      this.resultContainer.innerHTML = html;
      this.resultContainer.style.display = 'block';

      // –ü–ª–∞–≤–Ω–æ–µ –ø–æ—è–≤–ª–µ–Ω–∏–µ —Ä–µ–∑—É–ª—å—Ç–∞—Ç–∞
      setTimeout(() => {
        this.resultContainer.classList.add('calculator-power__result--visible');
      }, 10);
    }
  }

  // –ò–Ω–∏—Ü–∏–∞–ª–∏–∑–∞—Ü–∏—è –∫–∞–ª—å–∫—É–ª—è—Ç–æ—Ä–∞ –ø—Ä–∏ –∑–∞–≥—Ä—É–∑–∫–µ DOM
  function initCalculator() {
    const calculatorContainer = document.querySelector('.calculator-power');
    if (calculatorContainer) {
      new PowerCalculator(calculatorContainer);
    }
  }

  if (document.readyState === 'loading') {
    document.addEventListener('DOMContentLoaded', initCalculator);
  } else {
    initCalculator();
  }

  // –≠–∫—Å–ø–æ—Ä—Ç –¥–ª—è –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –≤ –¥—Ä—É–≥–∏—Ö —Å–∫—Ä–∏–ø—Ç–∞—Ö
  window.PowerCalculator = PowerCalculator;

})();

