/* ============================================
   Responsive Styles
   ============================================ */

/* Mobile First - Base styles for 320px+ */

/* Small devices (640px and up) */
@media (min-width: 640px) {
  .container {
    padding-left: var(--spacing-lg);
    padding-right: var(--spacing-lg);
  }
}

/* Medium devices (768px and up) */
@media (min-width: 768px) {
  .container {
    max-width: var(--container-md);
  }

  .header__nav {
    display: flex;
  }

  .header__menu-toggle {
    display: none !important;
  }

  .mobile-menu {
    display: none !important;
  }
}

/* Large devices (1024px and up) */
@media (min-width: 1024px) {
  .container {
    max-width: var(--container-lg);
  }

  .hero {
    min-height: 100vh;
  }

  .hero__container {
    grid-template-columns: 1fr 1fr;
  }

  .hero__content {
    text-align: left;
  }

  .hero__actions {
    justify-content: flex-start;
  }

  .footer__container {
    grid-template-columns: repeat(4, 1fr);
  }
}

/* Extra large devices (1280px and up) */
@media (min-width: 1280px) {
  .container {
    max-width: var(--container-xl);
  }
}

/* 2XL devices (1536px and up) */
@media (min-width: 1536px) {
  .container {
    max-width: var(--container-2xl);
  }
}

/* Large Desktop (1920px and up) */
@media (min-width: 1920px) {
  .container {
    max-width: 1800px;
  }

  .hero__title {
    font-size: clamp(3rem, 4vw, 4.5rem);
  }

  .hero__subtitle {
    font-size: clamp(1.25rem, 2.5vw, 1.75rem);
  }
}

/* Ultra-wide (2560px and up) */
@media (min-width: 2560px) {
  .container {
    max-width: 2200px;
  }

  .section {
    padding: calc(var(--spacing-3xl) * 1.5) 0;
  }
}

/* Print styles are in components/print.css */

/* High DPI displays */
@media (-webkit-min-device-pixel-ratio: 2), (min-resolution: 192dpi) {
  img {
    image-rendering: -webkit-optimize-contrast;
    image-rendering: crisp-edges;
  }
}

/* Landscape orientation */
@media (orientation: landscape) and (max-height: 500px) {
  .hero {
    min-height: auto;
    padding: var(--spacing-xl) 0;
  }

  .mobile-menu {
    padding: var(--spacing-md);
  }
}

/* Touch Device Optimizations - Enhanced Visual Feedback */
@media (hover: none) and (pointer: coarse) {
  /* –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º touch targets –Ω–∞ –º–æ–±–∏–ª—å–Ω—ã—Ö */
  .btn,
  .header__nav-link,
  .header__menu-toggle,
  .mobile-menu__nav-link,
  .form__checkbox-label,
  .form__radio-label {
    min-height: 44px;
    min-width: 44px;
  }

  /* –£–ª—É—á—à–∞–µ–º readability –Ω–∞ touch —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞—Ö */
  p, li, .card__body {
    font-size: clamp(1rem, 4vw, 1.0625rem);
    line-height: 1.6;
  }

  /* –£–≤–µ–ª–∏—á–∏–≤–∞–µ–º spacing –º–µ–∂–¥—É —ç–ª–µ–º–µ–Ω—Ç–∞–º–∏ */
  .btn-group {
    gap: var(--spacing-lg);
  }

  .form {
    gap: var(--spacing-xl);
  }

  /* –£–ª—É—á—à–∞–µ–º –≤–∏–∑—É–∞–ª—å–Ω—ã–π feedback –¥–ª—è touch */
  .btn--primary:active,
  .btn--gradient:active,
  .btn--glow:active {
    transform: scale(0.98);
    box-shadow: var(--shadow-sm);
  }

  /* –£–ø—Ä–æ—â–∞–µ–º —ç—Ñ—Ñ–µ–∫—Ç—ã –¥–ª—è –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏ */
  .card--3d,
  .card--premium::before,
  .btn--gradient::before {
    display: none;
  }

  /* –£–ª—É—á—à–∞–µ–º –∫–æ–Ω—Ç—Ä–∞—Å—Ç–Ω–æ—Å—Ç—å –¥–ª—è touch –∏–Ω—Ç–µ—Ä—Ñ–µ–π—Å–æ–≤ */
  .btn--primary {
    box-shadow: var(--shadow-md);
  }

  /* –£–±–∏—Ä–∞–µ–º —Å–ª–æ–∂–Ω—ã–µ –∞–Ω–∏–º–∞—Ü–∏–∏ –Ω–∞ touch —É—Å—Ç—Ä–æ–π—Å—Ç–≤–∞—Ö */
  .hero::after,
  .cta::before {
    animation: none;
  }

  .badge--glow,
  .btn--glow {
    animation: none;
  }
}

/* Responsive Images - Base Support */
img {
  max-width: 100%;
  height: auto;
}

/* Responsive Typography - Extended */
@media (max-width: 767px) {
  h1 {
    font-size: clamp(1.75rem, 6vw, 2.5rem);
  }

  h2 {
    font-size: clamp(1.5rem, 5vw, 2rem);
  }

  h3 {
    font-size: clamp(1.25rem, 4vw, 1.75rem);
  }

  .hero__title {
    font-size: clamp(1.75rem, 8vw, 3rem);
  }

  .hero__subtitle {
    font-size: clamp(1rem, 3vw, 1.25rem);
  }
}

/* Container Padding Adjustments */
@media (max-width: 639px) {
  .container {
    padding-left: var(--spacing-md);
    padding-right: var(--spacing-md);
  }

  .section {
    padding: var(--spacing-xl) 0;
  }
}

/* Tablet Optimizations */
@media (min-width: 768px) and (max-width: 1023px) {
  .grid {
    grid-template-columns: repeat(2, 1fr);
  }

  .hero__container {
    grid-template-columns: 1fr;
  }
}

/* Large Desktop Optimizations */
@media (min-width: 1920px) {
  .container {
    max-width: 1600px;
  }

  .section {
    padding: var(--spacing-3xl) 0;
  }
}

/* Reduced motion */
@media (prefers-reduced-motion: reduce) {
  *,
  *::before,
  *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
    scroll-behavior: auto !important;
  }

  /* –û—Ç–∫–ª—é—á–∞–µ–º —Å–ª–æ–∂–Ω—ã–µ –∞–Ω–∏–º–∞—Ü–∏–∏ */
  .hero,
  .cta,
  .btn--multicolor,
  .badge--glow,
  .btn--glow {
    animation: none !important;
    background-size: 100% 100% !important;
  }

  /* –û—Ç–∫–ª—é—á–∞–µ–º transform –∞–Ω–∏–º–∞—Ü–∏–∏ */
  .card--3d:hover,
  .card--premium:hover,
  .benefit-card:hover .benefit-card__icon {
    transform: none !important;
  }

  /* –û—Ç–∫–ª—é—á–∞–µ–º will-change –¥–ª—è –ø—Ä–æ–∏–∑–≤–æ–¥–∏—Ç–µ–ª—å–Ω–æ—Å—Ç–∏ */
  * {
    will-change: auto !important;
  }
}

